import Vue from 'vue';
import axios from 'axios';
import vSelect from 'vue-select';
import 'vue-select/dist/vue-select.css';
import registro from "../agrega_usuario.vue";
import Datepicker from 'vuejs-datepicker';
import flatPickr from 'vue-flatpickr-component';
import moment from 'moment';
import 'flatpickr/dist/flatpickr.css';
import asignarLibrosVue from "../../institucion/components/asignarLibros.vue";
import visitasUsuario from "./components/visitasUsuario.vue";
import editarUsuarioVue from "./components/editarUsuario.vue";
import AsignarDocumentosVue from '@/views/nee_documentos/components/AsignarDocumentos';
export default {
  components: {
    'v-select': vSelect,
    registro: registro,
    flatPickr: flatPickr,
    asignarLibrosVue: asignarLibrosVue,
    visitasUsuario: visitasUsuario,
    editarUsuarioVue: editarUsuarioVue,
    AsignarDocumentosVue: AsignarDocumentosVue
  },
  data: function data() {
    return {
      popupAsignacion: false,
      op_agregar: 0,
      popupVistaUsuarios: false,
      popupCursosEstudiantes: false,
      popupLibrosEstudiantes: false,
      valorBusqueda: "2",
      codigosLibros: [],
      estudiantesCursos: [],
      estudiantesCodigos: [],
      busqueda: "usuario",
      estudiantes: [],
      usuario: {
        razonBusqueda: ""
      },
      popupActivo: false,
      instituciones: [],
      usuarios: {
        idusuario: "",
        cedula: "",
        nombres: "",
        apellidos: "",
        email: "",
        name_usuario: "",
        telefono: "",
        estado_idEstado: "",
        institucion_idInstitucion: "",
        id_group: "",
        fecha_nacimiento: '',
        cargo_id: '',
        iniciales: '',
        foto_user: '',
        capacitador: ''
      },
      docente: '',
      listaVisitas: [],
      datoU: '',
      cantidad_cursos: "",
      cursosCodigos: [],
      user: [],
      seleccionadas: '',
      area: [],
      user_institucion: '',
      user_email: '',
      show_inst: '',
      user_nombre: '',
      fromDate: null,
      toDate: null,
      //variables para el historico
      datosHistorico: {
        nombre: ''
      },
      arregloHistorico: [],
      //fin variables para el historico
      periodo: {
        periodo_id: 0,
        estado: 0,
        descripcion: ''
      },
      userRoot: false,
      //VARIABLES NEE
      popupAsignacionNEE: false,
      userSelect: {},
      arregloTemas: [],
      //FIN VARIABLES NEE
      userFacturadores: false
    };
  },
  mounted: function mounted() {
    var me = this;
    me.user = JSON.parse(localStorage.getItem('usuario'));
    me.$http.get(me.$server_url + 'institucionesResportes').then(function (res) {
      me.instituciones = res.data;
    });
    me.getUserRoot(me.user);
    if (me.user.id_group == 22 || me.user.id_group == 23) {
      me.userFacturadores = true;
    }
  },
  methods: {
    changeHandle: function changeHandle(e) {
      var me = this;
      me.busquedaFiltroEstudiante();
      me.popupActivo = false;
    },
    getUserRoot: function getUserRoot(dato) {
      var me = this;
      this.$http.get(this.$server_url + 'permisos/' + dato.idusuario).then(function (result) {
        if (result.data.length > 0) {
          me.userRoot = true;
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    //agregar usuario
    agrega_nuevo_user: function agrega_nuevo_user() {
      var me = this;
      me.op_agregar = 1;
    },
    listaUsuarios: function listaUsuarios() {
      var me = this;
      me.op_agregar = 2;
    },
    variableHijo: function variableHijo(valor) {
      var me = this;
      me.op_agregar = valor;
    },
    //====METODOS NEET
    irAsignacionNEE: function irAsignacionNEE(item) {
      var me = this;
      me.getPeriodo(item);
      me.popupAsignacionNEE = true;
    },
    getTemas: function getTemas() {
      var me = this;
      this.$http.get(this.$server_url + "neetTema?listadoTemas=yes").then(function (res) {
        me.arregloTemas = res.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //asignacion del usuario docente
    //FIN METODOS NEETT
    irAsignacion: function irAsignacion(item) {
      var me = this;
      localStorage.setItem('user_select', JSON.stringify(item));
      localStorage.setItem('show_inst', 'si'); //desde vista docentes, mostrar institucion
      me.show_inst = localStorage.getItem('show_inst');
      me.usuarioAsignacion = JSON.parse(localStorage.getItem('user_select'));
      me.user_seleccionado = me.usuarioAsignacion.idusuario;
      me.user_nombre = me.usuarioAsignacion.nombres + ' ' + me.usuarioAsignacion.apellidos;
      me.user_email = me.usuarioAsignacion.email;
      me.user_institucion = item.nombreInstitucion;
      me.getAreaSelect();
      me.getSeleccionadas();
      me.getPeriodo(item);
      me.popupAsignacion = true;
    },
    //=======METODOS PARA MODAL DE ASIGNACION DE LIBROS
    getPeriodo: function getPeriodo(tr) {
      var me = this;
      this.$http.get(this.$server_url + "institucionTraerPeriodo?institucion_id=" + tr.institucion_idInstitucion).then(function (res) {
        if (res.data.length == 0) {
          me.periodo.periodo_id = 0;
          me.periodo.estado = 0;
          me.periodo.descripcion = "";
        }
        var datos = res.data[0];
        me.periodo.periodo_id = datos.periodo;
        me.periodo.estado = datos.estado;
        me.periodo.descripcion = datos.descripcion;
      });
    },
    getSeleccionadas: function getSeleccionadas() {
      var me = this;
      me.$vs.loading();
      var data = new FormData();
      data.append('idusuario', me.user_seleccionado);
      this.$http.post(this.$server_url + "asignaturasDocent", data).then(function (res) {
        // console.log(res.data);
        me.seleccionadas = res.data;
        // me.popupDocentes = true;
        me.$vs.loading.close();
      });
    },
    getAreaSelect: function getAreaSelect() {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + "areaSelect").then(function (response) {
        me.area = response.data.items;
        // console.log(me.area);
        me.$vs.loading.close();
      });
    },
    getVisitas: function getVisitas(tr) {
      var me = this;
      me.datoU = tr;
      me.op_agregar = 3;
    },
    agregaAsignacion: function agregaAsignacion(item) {
      var me = this;
      var data = new FormData();
      if (me.periodo.periodo_id == 0) {
        me.$vs.notify({
          time: 4000,
          text: 'La institución no tiene período',
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      if (me.periodo.estado == 0) {
        me.$vs.notify({
          time: 4000,
          text: 'La institución tiene período inactivo',
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      data.append('usuario_idusuario', me.user_seleccionado);
      data.append('asignatura_idasignatura', item.id);
      this.$http.post(this.$server_url + "asignar_asignatura_docentes", data).then(function (res) {
        // console.log(res.data);
        if (res.data > 0) {
          me.$vs.notify({
            time: 7000,
            text: 'Esta asignatura ya se encuentra asignada al docente',
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
        } else {
          me.$vs.notify({
            text: 'Asignatura agregada',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          me.getSeleccionadas();
        }
        me.$vs.loading.close();
      });
    },
    eliminaAsignacion: function eliminaAsignacion(item) {
      var me = this;
      // console.log(item)
      me.$vs.loading();
      this.$http.get(this.$server_url + "eliminaAsignacion/" + item.idasignado).then(function (res) {
        // console.log(res.data);
        me.$vs.notify({
          text: item.nombreasignatura + ' eliminada',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.getSeleccionadas();
        me.$vs.loading.close();
      });
    },
    confirmaQuitar: function confirmaQuitar() {
      var me = this;
      me.popupAsignacion = false;
      me.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: 'Confirmar',
        text: '¿Está seguro de eliminar este contenido?',
        acceptText: 'Aceptar',
        cancelText: 'Cancelar',
        accept: me.f_quitarTodo,
        cancel: me.f_regresar
      });
    },
    moverPeriodoActual: function moverPeriodoActual() {
      var me = this;
      this.$http.get(this.$server_url + 'moverAsignaturasPeriodo/' + me.user_seleccionado + '/' + me.periodo.periodo_id).then(function (res) {
        me.$vs.notify({
          text: 'Se cambio correctamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.getSeleccionadas();
      }).catch(function (error) {
        console.log(error + ' error');
      });
    },
    f_regresar: function f_regresar() {
      var me = this;
      me.popupAsignacion = true;
    },
    f_quitarTodo: function f_quitarTodo() {
      var me = this;
      var idSelects = [];
      for (var index = 0; index < me.seleccionadas.length; index++) {
        idSelects.push(me.seleccionadas[index].idasignado);
      }
      me.$vs.loading();
      var data = new FormData();
      data.append('idasiguser', idSelects);
      this.$http.post(this.$server_url + "quitaTodasAsignaturas", data).then(function (res) {
        // console.log(res.data);
        me.$vs.notify({
          text: 'Asignaturas quitadas correctamente.',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.getSeleccionadas();
        me.$vs.loading.close();
      });
    },
    //=======FIN DE METODOS PARA MODAL DE ASIGNACION DE LIBROS
    busquedaFiltroEstudiante: function busquedaFiltroEstudiante() {
      var me = this;
      me.estudiantesCursos = [];
      me.arregloHistorico = [];
      if (me.usuario.razonBusqueda.length < 5) {
        me.$vs.notify({
          text: 'Minimo 5 caracteres de busqueda',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      me.$vs.loading();
      if (me.user.id_group == 11) {
        //PARA LOS ASESORES
        this.$http.get(this.$server_url + 'busquedaFiltroEstudiante?busqueda=' + me.busqueda + '&razonBusqueda=' + me.usuario.razonBusqueda + '&asesor=yes').then(function (res) {
          me.$vs.loading.close();
          if (res.data.status == '0') {
            me.$vs.notify({
              text: res.data.message,
              color: 'primary',
              iconPack: 'feather',
              icon: 'icon-check'
            });
            return false;
          } else {
            me.estudiantesCursos = res.data;
          }
        }).catch(function (error) {
          console.log(error + ' error');
          me.$vs.loading.close();
        });
      } else {
        //para los administradores
        this.$http.get(this.$server_url + 'busquedaFiltroEstudiante?busqueda=' + me.busqueda + '&razonBusqueda=' + me.usuario.razonBusqueda).then(function (res) {
          me.$vs.loading.close();
          if (res.data.status == '0') {
            me.$vs.notify({
              text: res.data.message,
              color: 'primary',
              iconPack: 'feather',
              icon: 'icon-check'
            });
            return false;
          } else {
            var datos = res.data;
            if (me.userFacturadores) {
              datos = res.data.filter(function (p) {
                return p.id_group != 1;
              });
            }
            me.estudiantesCursos = datos;
          }
          if (me.estudiantesCursos.length == 0) {
            me.$vs.notify({
              text: 'No se encontro resultados',
              color: 'primary',
              iconPack: 'feather',
              icon: 'icon-check'
            });
          }
        }).catch(function (error) {
          console.log(error + ' error');
          me.$vs.loading.close();
        });
      }
    },
    //historico de cambios instituciones
    getHistoricoChangeInstitucion: function getHistoricoChangeInstitucion(tr) {
      var me = this;
      me.arregloHistorico = [];
      me.datosHistorico.nombre = tr.nombres + " " + tr.apellidos;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'gestionEstudiante?getHistorico=yes&idusuario=' + tr.idusuario).then(function (res) {
        me.$vs.loading.close();
        if (res.data.length == 0) {
          me.$vs.notify({
            text: 'No hay historico del estudiante',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        me.arregloHistorico = res.data;
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    //editar
    getEdit: function getEdit() {
      var data = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : {};
      var me = this;
      console.log(data);
      me.usuarios = data;
    },
    //para ver los libros de los estudiantes
    verLibrosEstudiante: function verLibrosEstudiante(idusuario, institucion_idInstitucion) {
      var me = this;
      me.$vs.loading();
      var libro = "yes";
      this.$http.get(this.$server_url + 'busquedaFiltroEstudiante?libroEstudiante=' + libro + '&idusuario=' + idusuario + '&institucion=' + institucion_idInstitucion).then(function (res) {
        me.$vs.loading.close();
        me.codigosLibros = res.data.codigos;
        me.popupLibrosEstudiantes = true;
        if (res.data.message) {
          me.$vs.notify({
            text: res.data.message,
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-user'
          });
        }
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    //para ver los cursos de los estudiantes
    verCursos: function verCursos(idusuario) {
      var me = this;
      me.$vs.loading();
      var busquedaCurso = "yes";
      this.$http.get(this.$server_url + 'busquedaFiltroEstudiante?busquedaCurso=' + busquedaCurso + '&idusuario=' + idusuario).then(function (res) {
        me.$vs.loading.close();
        me.cantidad_cursos = res.data.cantidad[0].cantidad_cursos;
        me.cursosCodigos = res.data.codigos;
        me.popupCursosEstudiantes = true;
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    openConfirmResetear: function openConfirmResetear() {
      this.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: "Confirmar",
        acceptText: 'Si, deseo resetear',
        cancelText: 'Cancelar',
        text: 'Estas seguro de resetear el password?',
        accept: this.generarPassword
      });
    },
    generarPassword: function generarPassword(tr) {
      var me = this;
      var formData = new FormData();
      this.$vs.loading({
        background: this.backgroundLoading,
        color: this.colorLoading,
        container: "#button-with-loading",
        scale: 0.45
      });
      formData.append('email', me.userSelect.email);
      this.$http.post(this.$server_url + 'restaurarPassword', formData).then(function (response) {
        me.$vs.notify({
          text: 'Use el número de cédula para acceder al perfil del usuario.',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-lock'
        });
        me.$vs.loading.close("#button-with-loading > .con-vs-loading");
      }).catch(function (error) {
        me.$vs.notify({
          text: 'Error en el proceso',
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-lock'
        });
        me.$vs.loading.close("#button-with-loading > .con-vs-loading");
      });
    }
  }
};
