import "core-js/modules/web.dom.iterable";
import axios from 'axios';
import UploadFileGerenciaVue from "../../admin/components/UploadFileGerencia";
import UploadFileVue from "../../admin/components/UploadFile";
import imageZoom from 'vue-image-zoomer';
export default {
  data: function data() {
    return {
      usuario: [],
      pedido: {},
      popupObservacion: false,
      popupAutorizar: false,
      popupAdminAutoriza: false,
      popupAsignarValor: false,
      popupSubirEntrega: false,
      popupSolicitud: false,
      filtro: 1,
      tmpArregloObsequios: [],
      userAdmin: false,
      userGerencia: false,
      userFacturador: false,
      userAsesor: false,
      //VARIABLES PARA MOSTRAR DOCUMENTOS
      popupDocumento: false,
      estadoPhoto: 0,
      documento: '',
      //FIN VARIABLES PARA MOSTRAR DOCUMENTOS
      //VARIABLES PARA ASIGNAR VALOR
      valorAsignar: 0,
      attachmentArrayCotizacion: [],
      attachmentArrayFactura: []
      //FIN VARIABLES PARA ASIGNAR
    };
  },
  props: {
    periodo_id: {
      type: String,
      default: ""
    },
    arregloObsequios: {
      type: Array,
      default: []
    },
    userRoot: {
      type: Boolean,
      default: false
    },
    userAdmin: {
      type: Boolean,
      default: false
    }
  },
  components: {
    UploadFileGerenciaVue: UploadFileGerenciaVue,
    UploadFileVue: UploadFileVue,
    imageZoom: imageZoom
  },
  watch: {
    arregloObsequios: function arregloObsequios(results) {
      var me = this;
      me.tmpArregloObsequios = [];
      if (results.length > 0) {
        me.filtroTipo();
      }
    }
  },
  created: function created() {
    var me = this;
    me.usuario = JSON.parse(localStorage.getItem("usuario"));
    me.tmpArregloObsequios = me.arregloObsequios;
  },
  mounted: function mounted() {
    var me = this;
    //FILTRO POR DEFECTO
    //gerencia -> mostrar directos pedidos aprobados
    if (me.usuario.id_group == 25) me.filtro = 4;
    if (me.usuario.id_group == 1) me.filtro = 3;
    //FIN FILTRO POR DEFECTO
    //user admin
    if (me.usuario.id_group == 1) me.userAdmin = true;
    //user gerencia
    if (me.usuario.id_group == 25) me.userGerencia = true;
    //user facturador
    if (me.usuario.id_group == 23 || me.usuario.id_group == 22) me.userFacturador = true;
    //user asesor
    if (me.usuario.id_group == 11) me.userAsesor = true;
  },
  computed: {
    sumaPendientes: function sumaPendientes() {
      var me = this;
      var datos = '';
      if (me.usuario.id_group == 11 || me.userRoot || me.userAdmin) {
        datos = me.arregloObsequios.filter(function (p) {
          return p.estado == 0 || p.estado == 1;
        });
      } else {
        datos = me.arregloObsequios.filter(function (p) {
          return p.estado == 1;
        });
      }
      var resultado = datos.length;
      return resultado;
    },
    sumaAnulados: function sumaAnulados() {
      var me = this;
      var datos = me.arregloObsequios.filter(function (p) {
        return p.estado == 2;
      });
      var resultado = datos.length;
      return resultado;
    },
    sumaAprobados: function sumaAprobados() {
      var me = this;
      var datos = me.arregloObsequios.filter(function (p) {
        return p.estado == 3;
      });
      var resultado = datos.length;
      return resultado;
    },
    sumaEvidencias: function sumaEvidencias() {
      var me = this;
      var datos = me.arregloObsequios.filter(function (p) {
        return p.estado == 4;
      });
      var resultado = datos.length;
      return resultado;
    },
    sumaAutorizados: function sumaAutorizados() {
      var me = this;
      var datos = me.arregloObsequios.filter(function (p) {
        return p.estado == 5;
      });
      var resultado = datos.length;
      return resultado;
    },
    sumaEntregados: function sumaEntregados() {
      var me = this;
      var datos = me.arregloObsequios.filter(function (p) {
        return p.estado == 6;
      });
      var resultado = datos.length;
      return resultado;
    }
  },
  methods: {
    filtroTipo: function filtroTipo() {
      var me = this;
      me.tmpArregloObsequios = [];
      //para ver los pedidos pendientes
      if (me.filtro == 1) {
        var datos = '';
        if (me.usuario.id_group == 11 || me.usuario.id_group == 1) {
          datos = me.arregloObsequios.filter(function (p) {
            return p.estado == 0 || p.estado == 1;
          });
        } else {
          datos = me.arregloObsequios.filter(function (p) {
            return p.estado == 1;
          });
        }
        me.tmpArregloObsequios = datos;
        return;
      }
      //para ver los pedidos anulados
      if (me.filtro == 2) {
        var _datos = me.arregloObsequios.filter(function (p) {
          return p.estado == 2;
        });
        me.tmpArregloObsequios = _datos;
        return;
      }
      //para ver los pedidos aprobados
      if (me.filtro == 3) {
        var _datos2 = me.arregloObsequios.filter(function (p) {
          return p.estado == 3;
        });
        me.tmpArregloObsequios = _datos2;
        return;
      }
      //para ver las evidencias cotizaciones y factura
      if (me.filtro == 4) {
        var _datos3 = me.arregloObsequios.filter(function (p) {
          return p.estado == 4;
        });
        me.tmpArregloObsequios = _datos3;
        return;
      }
      //para ver los pedidos autorizados
      if (me.filtro == 5) {
        var _datos4 = me.arregloObsequios.filter(function (p) {
          return p.estado == 5;
        });
        me.tmpArregloObsequios = _datos4;
        return;
      }
      //para ver los pedidos entregados por el asesor
      if (me.filtro == 6) {
        var _datos5 = me.arregloObsequios.filter(function (p) {
          return p.estado == 6;
        });
        me.tmpArregloObsequios = _datos5;
        return;
      }
    },
    openConfirmarEliminar: function openConfirmarEliminar() {
      this.$vs.dialog({
        type: 'confirm',
        color: 'warning',
        acceptText: 'Si deseo eliminar',
        cancelText: 'Cancelar',
        title: "Confirmar",
        text: 'Estás seguro de eliminar el pedido de obsequios?',
        accept: this.deleteOrder
      });
    },
    editarPedido: function editarPedido(tr) {
      var me = this;
      me.$emit('changeEdit', tr);
    },
    gerenciaAutoriza: function gerenciaAutoriza() {
      var me = this;
      me.popupAutorizar = false;
      me.$emit('changeAutorizar', me.pedido);
    },
    deleteOrder: function deleteOrder() {
      var me = this;
      var formData = new FormData();
      formData.append('id', me.pedido.id);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'obsequio_eliminar', formData).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == 0) {
          me.$vs.notify({
            text: res.data.message,
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-check',
            time: 4000
          });
          return;
        }
        me.$emit('changeRecharge', true);
        me.$vs.notify({
          text: 'Pedido de obsequios eliminado con exito',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    recharge: function recharge() {
      var me = this;
      me.popupAdminAutoriza = false;
      me.popupSubirEntrega = false;
      me.popupSolicitud = false;
      me.$emit('changeRecharge', true);
    },
    openDocument: function openDocument(tr) {
      var me = this;
      if (tr.foto_solicitud.substr(-3) == 'pdf') {
        var ruta = me.$data_url + 'archivos/obsequios/' + me.tipoDocumento + '/' + me.documento;
        window.open(ruta, '_blank');
        return;
      }
      me.popupDocumento = true;
    },
    //METODOS GERENCIA
    openAutorizar: function openAutorizar() {
      var me = this;
      //gerencia autoriza
      if (me.usuario.id_group == 25) {
        me.popupAutorizar = true;
      }
      //Admin autoriza
      else {
        me.popupAdminAutoriza = true;
      }
    },
    //METODOS PARA ASIGNAR VALOR
    Getcotizacion: function Getcotizacion(e) {
      var me = this;
      me.attachmentArrayCotizacion = e;
    },
    GetFactura: function GetFactura(e) {
      var me = this;
      me.attachmentArrayFactura = e;
    },
    subirDocumento: function subirDocumento() {
      var me = this;
      // if(me.valorAsignar > me.pedido.valor_obsequios){
      //     me.$vs.notify({
      //         text: "El valor del pedido no puede ser mayor a $"+me.pedido.valor_obsequios,
      //         color: 'warning',
      //         iconPack: 'feather',
      //         icon: 'icon-alert-triangle',
      //         time:4000,
      //     })
      //     return
      // }
      if (me.attachmentArrayCotizacion.length == 0) {
        me.$vs.notify({
          text: "Suba un una imagen de la cotización por favor",
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      if (me.attachmentArrayFactura.length == 0) {
        me.$vs.notify({
          text: "Suba un una imagen de la factura por favor",
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      if (me.valorAsignar < 10) {
        me.$vs.notify({
          text: "El valor no puede ser menor a 10",
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      //files
      var imgsEnviarC = [];
      var imgsEnviarF = [];
      var formData = new FormData();
      //COTIZACION
      me.attachmentArrayCotizacion.forEach(function (element) {
        imgsEnviarC.push(element.file);
      });
      for (var i = 0; i < this.attachmentArrayCotizacion.length; i++) {
        formData.append('cotizacion[]', imgsEnviarC[i]);
      }
      //FACTURA
      me.attachmentArrayFactura.forEach(function (element) {
        imgsEnviarF.push(element.file);
      });
      for (var _i = 0; _i < this.attachmentArrayFactura.length; _i++) {
        formData.append('factura[]', imgsEnviarF[_i]);
      }
      formData.append('id', me.pedido.id);
      formData.append('estado', 4);
      formData.append('valor_total', me.valorAsignar);
      var config = {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      };
      me.$vs.loading();
      axios.post(this.$data_url + 'api/saveCotizacionFactura', formData, config).then(function (res) {
        me.$vs.loading.close();
        me.$vs.notify({
          text: "Se guardo correctamente",
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.attachmentArrayCotizacion = [];
        me.attachmentArrayFactura = [];
        me.valorAsignar = 0;
        me.popupAsignarValor = false;
        me.recharge();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    } //FIN METODOS PARA ASIGNAR VALOR
  }
};
