    getInstitucionInfo: function getInstitucionInfo(tr) {
      var me = this;
      if (tr.estado_institucion_temporal == "1") {
        this.asesorSelect = me.filtro.asesor.vendedor;
      } else {
        this.$http.get(this.$server_url + "institucionActiva?idInstitucion=".concat(tr.institucion_id)).then(function (res) {
          if (res.data.length > 0) {
            var nombreAsesor = res.data[0].nombres + " " + res.data[0].apellidos;
            me.setNombre(nombreAsesor);
          }
        }).catch(function (error) {
          console.log(error + ' error');
        });
      }
    },
