import ECharts from "vue-echarts/components/ECharts";
import "echarts/lib/component/tooltip";
import "echarts/lib/component/legend";
import "echarts/lib/chart/pie";
import Vue from 'vue';
import axios from 'axios';

//para el reporte
import pdfMake from "pdfmake/build/pdfmake";
var pdfFonts = require('pdfmake/build/vfs_fonts.js');
pdfMake.vfs = pdfFonts.pdfMake.vfs;
export default {
  data: function data() {
    return {
      nombreinstitucion: "",
      historicoinstitucionArreglo: [],
      historicoEstudianteArreglo: [],
      filtro: [],
      recursos1insti: "",
      recursos2insti: "",
      recursos3insti: "",
      recursos4insti: "",
      recursos5insti: "",
      recursos6insti: "",
      recursos7insti: "",
      recursos8insti: "",
      recursos9insti: "",
      recursos10insti: "",
      recursos11insti: "",
      recursos12insti: "",
      recursos13insti: "",
      recursos14insti: "",
      pie: {
        tooltip: {
          trigger: "item",
          formatter: "{a} <br/>{b} : {c} ({d}%)"
        },
        legend: {
          orient: "vertical",
          left: "left",
          data: ["libros", "unidades", "cuadernos", "teletarea", "proyectos", "propuestas", "juegos", "material", "articulos", "tareas", "pdf guia", "pdf sin guia", "guia didactica"]
        },
        series: [{
          name: "Contenido",
          type: "pie",
          radius: "55%",
          center: ["50%", "60%"],
          color: ["#FFD5CC", "#EE6345", "#B73E24", "#DCB42B", "#8E7314", "#F1E70B", "#6A99E5", "#6F8D2A", "#23E3B8", "#39AD93", "#ED3F69", "#B83FED", "#5A1CC6", "#1C24C6", "#0C6F74"],
          data: [],
          itemStyle: {
            emphasis: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: "rgba(0, 0, 0, 0.5)"
            }
          }
        }]
      }
    };
  },
  components: {
    ECharts: ECharts
  },
  methods: {
    //para generar el reporte
    reporte: function reporte() {
      var docDefinition = {
        content: [{
          image: 'snow',
          fit: [100, 100],
          alignment: 'center'
        }, {
          text: " "
        }, {
          text: " "
        }, {
          text: "Reporte de visitas de ".concat(this.nombreinstitucion),
          bold: true,
          color: "red",
          alignment: 'center',
          fontSize: 22
        }, {
          text: " "
        }, {
          text: " "
        }, {
          text: "Visitas de  recursos por estudiantes:"
        }, {
          text: " "
        }, {
          ul: [{
            text: "libros :".concat(this.recursos1insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "unidades :".concat(this.recursos2insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "cuadernos :".concat(this.recursos3insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "teletarea :".concat(this.recursos4insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "proyectos :".concat(this.recursos5insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "adaptaciones :".concat(this.recursos6insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "propuestas :".concat(this.recursos7insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "juegos :".concat(this.recursos8insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "material :".concat(this.recursos9insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "articulos :".concat(this.recursos10insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "tareas :".concat(this.recursos11insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "pdf guia :".concat(this.recursos12insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "pdf sin guia :".concat(this.recursos13insti),
            bold: false,
            color: "#2DADE8"
          }, {
            text: "guia didactica :".concat(this.recursos14insti),
            bold: false,
            color: "#2DADE8"
          }]
        }],
        //para colocar la imagen de prolipa
        images: {
          snow: 'data:image/jpeg;base64,'
        }
      };
      pdfMake.createPdf(docDefinition).download("institucionvisitas.pdf");
    },
    getReporteEstudiante: function getReporteEstudiante() {
      var _this = this;
      var transformar2 = JSON.parse(localStorage.getItem("visitasid"));
      var item = transformar2;
      var me = this;
      axios.get('https://foro.prolipadigital.com.ec/historico-recursos?idinstitucion=' + item + '&_limit=-1').then(function (res) {
        _this.historicoinstitucionArreglo = res.data;
        me.historicoEstudianteArreglo = _this.historicoinstitucionArreglo.filter(function (p) {
          return p.idgrupo == "4";
        });
        var transformaraParse = _this.historicoEstudianteArreglo;
        //paa obtener el historico de recusos
        _this.recursos1insti = transformaraParse.filter(function (p) {
          return p.recurso == "1";
        }).length;
        _this.recursos2insti = transformaraParse.filter(function (p) {
          return p.recurso == "2";
        }).length;
        _this.recursos3insti = transformaraParse.filter(function (p) {
          return p.recurso == "3";
        }).length;
        _this.recursos4insti = transformaraParse.filter(function (p) {
          return p.recurso == "4";
        }).length;
        _this.recursos5insti = transformaraParse.filter(function (p) {
          return p.recurso == "5";
        }).length;
        _this.recursos6insti = transformaraParse.filter(function (p) {
          return p.recurso == "6";
        }).length;
        _this.recursos7insti = transformaraParse.filter(function (p) {
          return p.recurso == "7";
        }).length;
        _this.recursos8insti = transformaraParse.filter(function (p) {
          return p.recurso == "8";
        }).length;
        _this.recursos9insti = transformaraParse.filter(function (p) {
          return p.recurso == "9";
        }).length;
        _this.recursos10insti = transformaraParse.filter(function (p) {
          return p.recurso == "10";
        }).length;
        _this.recursos11insti = transformaraParse.filter(function (p) {
          return p.recurso == "11";
        }).length;
        _this.recursos12insti = transformaraParse.filter(function (p) {
          return p.recurso == "12";
        }).length;
        _this.recursos13insti = transformaraParse.filter(function (p) {
          return p.recurso == "13";
        }).length;
        _this.recursos14insti = transformaraParse.filter(function (p) {
          return p.recurso == "14";
        }).length;

        // asignar dato al pie

        _this.pie.series[0].data = [{
          value: _this.recursos1insti,
          name: "libros"
        }, {
          value: _this.recursos2insti,
          name: "unidades"
        }, {
          value: _this.recursos3insti,
          name: "cuadernos"
        }, {
          value: _this.recursos4insti,
          name: "teletarea"
        }, {
          value: _this.recursos5insti,
          name: "proyectos"
        }, {
          value: _this.recursos6insti,
          name: "adaptaciones"
        }, {
          value: _this.recursos7insti,
          name: "propuestas"
        }, {
          value: _this.recursos8insti,
          name: "juegos"
        }, {
          value: _this.recursos9insti,
          name: "material"
        }, {
          value: _this.recursos10insti,
          name: "articulos"
        }, {
          value: _this.recursos11insti,
          name: "tareas"
        }, {
          value: _this.recursos12insti,
          name: "pdf guia"
        }, {
          value: _this.recursos13insti,
          name: "pdf sin guia"
        }, {
          value: _this.recursos14insti,
          name: "guia didactica"
        }];
        //para filtrar los que tienen mayor a cero
        var transformar3 = _this.pie.series[0].data;
        _this.filtro = transformar3.filter(function (p) {
          return p.value > "0";
        });
        _this.pie.series[0].data = _this.filtro;
      }).catch(function (err) {
        console.log(err);
      });
    }
  },
  created: function created() {
    //para traer el nombre de la institucion
    var visitasnombreinstitucionparse = JSON.parse(localStorage.getItem("visitasnombreinstitucion"));
    this.nombreinstitucion = visitasnombreinstitucionparse;
    this.getReporteEstudiante();
  },
  mounted: function mounted() {
    var dataIndex = -1;
    var pie = this.$refs.pie;
    var dataLen = pie.options.series[0].data.length;
    setInterval(function () {
      pie.dispatchAction({
        type: "downplay",
        seriesIndex: 0,
        dataIndex: dataIndex
      });
      dataIndex = (dataIndex + 1) % dataLen;
      pie.dispatchAction({
        type: "highlight",
        seriesIndex: 0,
        dataIndex: dataIndex
      });
      pie.dispatchAction({
        type: "showTip",
        seriesIndex: 0,
        dataIndex: dataIndex
      });
    }, 1000);
  }
};
