import axios from 'axios';
import moment from 'moment';
import vSelect from 'vue-select';
import showliquidacion from "./showliquidacion.vue";
export default {
  data: function data() {
    return {
      usuario: [],
      update: true,
      id: 0,
      titleModal: '',
      // para el select de libros
      arregloAsesores: [],
      // arreglorProfesores: [],
      arregloInstitucion: [],
      //para traer las ciudades
      arregloCiudad: [],
      //arreglo para filtrar las instituciones por ciudades
      arregloTraeInstitucion: [],
      //arreglo para filtar los profesores por institucion
      arregloTraeProfesor: [],
      //para el select de la ciudades
      ciudadSelect: '',
      /*Modal identificadores a cada modal*/
      popupActivo2: false,
      popupActivo3: false,
      popupActivo4: false,
      popupEditarAsesor: false,
      //arreglo para la tabla temporada
      arregloTemporada: [],
      //objeto para guardar el valor de los inputs,select etc 
      temporada: {
        contrato: '',
        year: '',
        ciudad: '',
        temporada: '',
        profesor: '',
        cedulaprofesor: '',
        asesor: '',
        institucion: '',
        //para el modal de editar que se muestre la cedula del profesor para que el administrador pueda copiar y buscar en el select
        temporal_nombre_docente: '',
        cedulaprofesortemporal: '',
        nombresasesortemporal: '',
        instituciontemporal: '',
        periodo: ''
      },
      //objeto para guardar el valor de los inputs,select etc de la tabla docentes
      docente: {
        cedula: '',
        nombres: '',
        apellidos: '',
        email: '',
        name_usuario: '',
        institucion_idInstitucion: ''
      },
      //Para el showliquidacion
      fecha: '',
      liquidacionArreglo: [],
      arregloPeriodos: [],
      bloquear: true,
      codigos: [],
      contratoLiquidacion: '',
      verLiquidacion: false,
      //variables para filtro
      periodo: '',
      region2: '2',
      region: '2',
      contrato: '',
      tipo: '1',
      //fin variables para filtro,
      permisoRoot: false,
      temporada_id: 0
    };
  },
  components: {
    'v-select': vSelect,
    showliquidacion: showliquidacion
  },
  watch: {
    contrato: function contrato() {
      var me = this;
      me.periodo = "";
      me.arregloTemporada = [];
    }
  },
  methods: {
    //para obtener el listado de los usuarios root
    getUserRoot: function getUserRoot() {
      var me = this;
      this.$http.get(this.$server_url + 'permisos?permiso=yes&usuario_id=' + me.usuario.idusuario).then(function (res) {
        if (res.data.status == '1') {
          me.permisoRoot = true;
        } else {
          me.permisoRoot = false;
        }
      }).catch(function (error) {
        console.log(error + ' error');
      });
    },
    verificarContratoPeriodo: function verificarContratoPeriodo() {
      var me = this;
      this.$http.get(this.$server_url + 'validarContrato?institucion_id=' + me.temporada.institucion.idInstitucion + '&periodo_id=' + me.temporada.periodo.idperiodoescolar).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == 0) {
          me.$vs.notify({
            text: res.data.message,
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-check',
            time: 8000
          });
          me.bloquear = true;
          me.codigos = res.data.codigos;
          return false;
        }
        if (res.data.status == 1) {
          me.$vs.notify({
            text: res.data.message,
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          me.bloquear = false;
        }
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    fechaActual: function fechaActual() {
      var fecha = moment().format('YYYY/MM/DD');
      this.fecha = fecha;
    },
    openConfirm: function openConfirm(tr) {
      var me = this;
      me.temporada_id = tr.id_temporada;
      this.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: "Confirmar",
        text: 'Al aceptar BORRARA todas las verificaciones si tuviera',
        accept: this.eliminarTemporada
      });
    },
    //eliminar temporada
    eliminarTemporada: function eliminarTemporada() {
      var me = this;
      var formData = new FormData();
      formData.append('id_temporada', me.temporada_id);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'temporadas/eliminar', formData).then(function (res) {
        me.listarTemporadas();
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'Temporada  eliminada y contrato',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      });
    },
    // lista de asesores para usar en el select
    listaAsesores: function listaAsesores() {
      var me = this;
      this.$http.get(this.$server_url + 'temporadas?asesores=yes').then(function (res) {
        me.arregloAsesores = res.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //lista de profesores para usar en el select
    // listaProfesores() {
    //     let me = this
    //     me.$vs.loading()
    //     this.$http.get(this.$server_url + `temporadas`).then(res => {
    //             me.arreglorProfesores = res.data.profesores;
    //             me.$vs.loading.close()
    //         })
    //         .catch(function (error) {
    //             console.log(error)
    //         })
    // },
    //lista de instituciones para usar en el select
    // listaInstituciones() {
    //     let me = this
    //     this.$http.get(this.$server_url + `temporadas`).then(res => {
    //             me.arregloInstitucion = res.data.listainstitucion;
    //             me.arregloCiudad = res.data.ciudad;
    //         })
    //         .catch(function (error) {
    //             console.log(error)
    //         })
    // },
    //lista de ciudades para usar en el select
    listaCiudades: function listaCiudades() {
      var me = this;
      this.$http.get(this.$server_url + "temporadas").then(function (res) {
        me.arregloCiudad = res.data.ciudad;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //lista de ciudades para usar en el select
    getPeriodos: function getPeriodos() {
      var me = this;
      this.$http.get(this.$server_url + 'periodo?porAllEstados=yes&region=' + me.region).then(function (res) {
        me.arregloPeriodos = res.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    getPeriodos2: function getPeriodos2() {
      var me = this;
      me.contrato = "";
      me.arregloTemporada = [];
      this.$http.get(this.$server_url + 'periodo?porAllEstados=yes&region=' + me.region2).then(function (res) {
        me.arregloPeriodos = res.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //listar Temporadas
    list: function list() {
      var _this = this;
      var me = this;
      me.arregloTemporada = [];
      me.contrato = "";
      if (me.periodo == null || me.periodo == undefined || me.periodo == "") {
        me.$vs.notify({
          text: 'Seleccione un periodo',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return;
      }
      me.$vs.loading();
      this.$http.get(this.$server_url + 'getTemporadas?periodo_id=' + me.periodo.idperiodoescolar + '&temporada=yes').then(function (res) {
        _this.arregloTemporada = res.data;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
      });
    },
    listarTemporadas: function listarTemporadas() {
      var me = this;
      if (me.tipo == 1) {
        me.list();
      } else {
        me.filtroContrato();
      }
    },
    //listar Temporadas
    filtroContrato: function filtroContrato() {
      var _this2 = this;
      var me = this;
      me.arregloTemporada = [];
      if (me.contrato.length < 2) {
        me.$vs.notify({
          text: 'Ingrese mas de 3 caracteres',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return;
      }
      if (me.contrato.trim() == "") {
        me.$vs.notify({
          text: 'Debe escribir un contrato',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return;
      }
      me.$vs.loading();
      this.$http.get(this.$server_url + 'getTemporadas?contrato=' + me.contrato + '&filtroContrato=yes').then(function (res) {
        _this2.arregloTemporada = res.data;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
      });
    },
    // desactivar el estado de la tabla 
    desactivarTemporada: function desactivarTemporada(id_temporada) {
      var me = this;
      var formData = new FormData();
      formData.append('id_temporada', id_temporada);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'temporadas/desactivar', formData).then(function (res) {
        me.listarTemporadas();
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'Temporada  actualizada',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      });
    },
    activarTemporada: function activarTemporada(id_temporada) {
      var me = this;
      var formData = new FormData();
      formData.append('id_temporada', id_temporada);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'temporadas/activar', formData).then(function (res) {
        me.listarTemporadas();
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'Temporada  actualizada',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      });
    },
    //para actualizar temporada
    agregar: function agregar() {
      var _this3 = this;
      //datos para guardar
      var me = this;
      var formData = new FormData();
      if (me.update) {
        formData.append('id', me.id);
      }
      /*Fin de enviar el backend el id */
      formData.append('contrato', me.temporada.contrato);
      formData.append('year', me.temporada.year);
      formData.append('ciudad', me.temporada.ciudad);
      formData.append('temporada', me.temporada.temporada);
      formData.append('id_asesor', me.temporada.asesor.idusuario);
      formData.append('cedula_asesor', me.temporada.asesor.cedula);
      formData.append('id_profesor', me.temporada.profesor.idusuario);
      formData.append('idInstitucion', me.temporada.institucion.idInstitucion);
      formData.append('periodo', me.temporada.periodo.idperiodoescolar);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'temporadas', formData).then(function (res) {
        me.listarTemporadas();
        me.popupActivo4 = false;
        me.popupActivo2 = false;
        me.limpiar();
        me.$vs.loading.close();
        me.$vs.notify({
          text: res.data.message,
          color: 'primary',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        _this3.bloquear = true;
      });
    },
    actualizarAsesor: function actualizarAsesor() {
      var _this4 = this;
      //actualizar al asesor r
      var me = this;
      var formData = new FormData();
      if (me.update) {
        formData.append('id', me.id);
      }
      formData.append('contrato', me.temporada.contrato);
      formData.append('id_asesor', me.temporada.asesor.idusuario);
      formData.append('cedula_asesor', me.temporada.asesor.cedula);
      me.$vs.loading();
      this.$http.get(this.$server_url + 'temporadas?editarAsesor=yes&contrato=' + me.temporada.contrato + '&id_asesor=' + me.temporada.asesor.idusuario + '&cedula_asesor=' + me.temporada.asesor.cedula).then(function (res) {
        me.listarTemporadas();
        me.popupEditarAsesor = false;
        me.$vs.loading.close();
        me.$vs.notify({
          text: res.data.message,
          color: 'primary',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        _this4.temporada.asesor = '';
        _this4.temporada.profesor = '';
        _this4.temporada.institucion = '';
        _this4.bloquear = true;
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    //para agregar temporada
    agregarTemporada: function agregarTemporada() {
      //datos para guardar
      var me = this;
      var formData = new FormData();
      /* idusuario enviar al backend para la auditoria */
      // formData.append('id_asesor', me.temporada.asesor.idusuario);
      formData.append('contrato', me.temporada.contrato);
      formData.append('year', me.temporada.year);
      formData.append('ciudad', me.ciudadSelect.nombre);
      formData.append('temporada', me.temporada.temporada);
      formData.append('id_profesor', me.temporada.profesor.idusuario);
      formData.append('temporal_cedula_docente', me.temporada.profesor.cedula);
      formData.append('temporal_nombre_docente', me.temporada.profesor.profesornombres);
      formData.append('idInstitucion', me.temporada.institucion.idInstitucion);
      formData.append('temporal_institucion', me.temporada.institucion.nombreInstitucion);
      formData.append('id_asesor', me.temporada.asesor.idusuario);
      formData.append('cedula_asesor', me.temporada.asesor.cedula);
      formData.append('nombre_asesor', me.temporada.asesor.asesornombres);
      formData.append('periodo', me.temporada.periodo.idperiodoescolar);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'temporadas', formData).then(function (res) {
        me.contrato = me.temporada.contrato;
        me.tipo = 2;
        me.listarTemporadas();
        me.popupActivo4 = false;
        me.popupActivo2 = false;
        me.limpiar();
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'Temporada guardada  exitosamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      });
    },
    agregarDocente: function agregarDocente() {
      var me = this;
      var formData = new FormData();
      // formData.append('id_asesor', me.temporada.asesor.idusuario);
      formData.append('cedula', me.docente.cedula);
      formData.append('nombres', me.docente.nombres);
      formData.append('apellidos', me.docente.apellidos);
      formData.append('email', me.docente.email);
      formData.append('name_usuario', me.docente.name_usuario);
      formData.append('institucion_idInstitucion', me.docente.institucion_idInstitucion.idInstitucion);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'temporadas/docente', formData).then(function (res) {
        // me.listaProfesores();
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'Docente guardado  exitosamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      });
    },
    //cuando abre el modal  se le agre lo siguiente
    openModal: function openModal(data, condicion) {
      var me = this;
      if (this.update) {
        if (condicion == 1) {
          this.popupActivo2 = true;
        }
        //para editar solo el asesor
        else {
          this.popupEditarAsesor = true;
          me.temporada.asesor = {
            idusuario: data.id_asesor,
            nombres: data.asesorProlipa
          };
        }
        this.id = data.id_temporada;
        this.titleModal = 'Editar contrato ' + data.contrato;
        // this.titleModal = "Modificar Temporada";
        //llenar los datos base de datos en los inputs para editar
        this.temporada.contrato = data.contrato;
        this.temporada.year = data.year;
        this.temporada.ciudad = data.ciudad;
        this.temporada.temporada = data.temporada;
        this.temporada.temporal_nombre_docente = data.temporal_nombre_docente;
        this.temporada.cedulaprofesortemporal = data.temporal_cedula_docente;
        this.temporada.nombresasesortemporal = data.nombre_asesor;
        this.temporada.instituciontemporal = data.temporal_institucion;
        // this.temporada.profesor = {idusuario:data.idusuarios, nombres:data.profesor, apellidos:data.papellido};
        // this.temporada.institucion = {idInstitucion:data.idInstitucion, nombreInstitucion:data.nombreInstitucion};
      } else {
        this.popupActivo4 = true;
        this.id = 0;
        this.titleModal = "Crear Temporada";
      }
    },
    limpiar: function limpiar() {
      var me = this;
      me.temporada.asesor = "";
      me.temporada.profesor = "";
      me.temporada.institucion = "";
      me.temporada.periodo = "";
      me.ciudadSelect = "";
      this.temporada.contrato = "";
      this.temporada.year = "";
      this.temporada.ciudad = "";
      this.temporada.temporada = "";
      this.temporada.temporal_nombre_docente = "";
      this.temporada.cedulaprofesortemporal = "";
      this.temporada.nombresasesortemporal = "";
      this.temporada.instituciontemporal = "";
    },
    //LIQUIDACIONES
    irAshowLiquidacion: function irAshowLiquidacion(contrato) {
      var me = this;
      me.contratoLiquidacion = contrato;
      me.verLiquidacion = true;
      // me.$vs.loading()
      // this.$http.get(this.$server_url + 'temporadas/liquidacion/' + contratoLiquidacion)
      //     .then(res => {
      //         console.log(res)
      //         this.liquidacionArreglo = res.data;
      //         localStorage.setItem("liquidacionArreglo", JSON.stringify(this.liquidacionArreglo));
      //         me.$router.push('/showliquidacion/');
      //         me.$vs.loading.close()
      //     })
    },
    //para filtrar instituciones por ciudades
    filtrarInstituciones: function filtrarInstituciones() {
      var me = this;
      if (me.ciudadSelect.idciudad === undefined) {
        me.$vs.notify({
          text: 'Seleccione una ciudad antes de filtrar por Instituciones',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return;
      }
      me.arregloTraeInstitucion = [];
      me.$vs.loading();
      var formData = new FormData();
      formData.append('ciudad_id', me.ciudadSelect.idciudad);
      this.$http.post(this.$server_url + 'traerinstituciones', formData).then(function (res) {
        me.arregloTraeInstitucion = res.data;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
      });
    },
    //para filtrar instituciones por ciudades
    filtrarProfesores: function filtrarProfesores() {
      var me = this;
      if (me.temporada.institucion.idInstitucion === undefined) {
        me.$vs.notify({
          text: 'Seleccione una institucion antes de filtrar por profesores',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return;
      }
      me.arregloTraeProfesor = [];
      me.$vs.loading();
      var formData = new FormData();
      formData.append('idInstitucion', me.temporada.institucion.idInstitucion);
      this.$http.post(this.$server_url + 'traerprofesor', formData).then(function (res) {
        me.arregloTraeProfesor = res.data;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
      });
    }
  },
  created: function created() {
    var me = this;
    me.usuario = JSON.parse(localStorage.getItem("usuario"));
    //para se carguen al cargar la pagina
    this.fechaActual();
    // this.list();
    this.listaCiudades();
    this.listaAsesores();
    this.getPeriodos2();
    this.getUserRoot();
  }
};
