import Vue from 'vue';
import imageZoom from 'vue-image-zoomer';
import vSelect from 'vue-select';
import axios from 'axios';
// const pedidos_evidencia_aprobacionVue = () => import('../../../../pedidos/components/pedidos_evidencia_aprobacion.vue')
var ScreenPagosVue = function ScreenPagosVue() {
  return import("../pagos2/ScreenPagos2.vue");
};
var anticiposAnteriosVue = function anticiposAnteriosVue() {
  return import("../../../../pedidos/components/milton/anticiposAnterios.vue");
};
var documentosAnterioresVue = function documentosAnterioresVue() {
  return import("../../../../pedidos/components/documentosAnteriores/documentosAnteriores.vue");
};
import PagosRepository from "../../../../../repositories/PagoRepository";
export default {
  components: {
    'v-select': vSelect,
    ScreenPagosVue: ScreenPagosVue,
    anticiposAnteriosVue: anticiposAnteriosVue,
    // pedidos_evidencia_aprobacionVue,
    imageZoom: imageZoom,
    documentosAnterioresVue: documentosAnterioresVue
  },
  data: function data() {
    return {
      usuario: [],
      id_pedido: '',
      txt_finalizar: 'Finalizar',
      pedido: {
        id_pedido: 0,
        periodo: '',
        tipo_venta: '',
        fecha_envio: '',
        fecha_entrega: '',
        institucion: '',
        asesor: '',
        descuento: '',
        anticipo: '',
        observacion: '',
        ruc: '',
        direccionInstitucion: '',
        nivel: '',
        tipo_descripcion: '',
        telefonoInstitucion: '',
        pendiente_liquidar: 0,
        convenio_anios: '',
        anticipo_aprobado: 0,
        contrato_generado: '',
        ifanticipo: false,
        ifagregado_anticipo_aprobado: 0,
        deuda: 0,
        periodo_deuda: '',
        region_idregion: 0,
        historicoEstado: 0,
        estado: 0
      },
      tmpPedidos: [],
      porcentaje_descuento_series_basicas: 0,
      counterDanger: false,
      //variables para images
      src_imagen_opcion: '',
      img_opcion_old: '',
      verImg: false,
      popupImagen: false,
      errorImage: 'no',
      tamanioError: 'no',
      //fin variables para images
      //variables para ruc
      src_imagen_opcion_ruc: '',
      img_opcion_old_ruc: '',
      verImg_ruc: false,
      popupRuc: false,
      errorImage_ruc: 'no',
      tamanioError_ruc: 'no',
      //fin variables para ruc
      disableAsesor: false,
      disableFacturador: false,
      disableAsesorFacturador: false,
      //variables de coincidencias
      arregloIdsCoincidencias: [],
      verAnticipos: false,
      userRoot: false,
      //variable subir imagen
      attachmentArray: [],
      attachmentArray_ruc: [],
      tmpFiles: [],
      checkFueraPlataforma: false,
      //variables para documentos anteriores
      arregloDocumentosAnteriores: [],
      popupMostrarDocumentosAnteriores: false,
      //fin variables para documentos anteriores
      //modificarAnticipoAprobado => 0 =NO; 1 SI;
      modificarAnticipoAprobado: 0,
      userFacturador: false,
      campo: ''
    };
  },
  props: {
    ObjectPedido: {
      type: Object,
      default: function _default() {
        return {};
      },
      required: true
    },
    userAsesor: {
      type: Boolean,
      default: false
    },
    groupFacturador: {
      type: Boolean,
      default: false
    }
  },
  watch: {
    ObjectPedido: function ObjectPedido(results) {
      var me = this;
      me.sendPedido = me.ObjectPedido;
      me.pedido = results;
    }
  },
  mounted: function mounted() {
    var me = this;
    me.pedido = me.ObjectPedido;
    this.usuario = JSON.parse(localStorage.getItem('usuario'));
    if (me.usuario.id_group == 23) me.userFacturador = true;
    me.getUserRoot(me.usuario);
    if (this.usuario.id_group == 22 || this.usuario.id_group == 23) {
      this.txt_finalizar = 'Generar contrato';
    }
    if (this.ObjectPedido.id_pedido == 0) {
      localStorage.id_pedido = '';
    } else {
      localStorage.id_pedido = this.ObjectPedido.id_pedido;
      me.updateDocumentos();
    }
    this.get_datos_pedido();
    this.cargarTodosDocumentos(1);
  },
  methods: {
    changeAnticipoA: function changeAnticipoA(e) {
      var me = this;
      if (e.tipo == 'guardarAnticipo') {
        me.pedido.ifagregado_anticipo_aprobado = 1;
        me.pedido.anticipo_aprobado = e.value;
        //si modifica el facturador o admin el anticipo aprobado lo guardare en un historico
        me.modificarAnticipoAprobado = e.modificarAnticipoAprobado;
        me.updatePedido(2, 'anticipo_aprobado', e.value, 'ifagregado_anticipo_aprobado', 1);
        return;
      }
      if (e.tipo == 'guardarAnticipoContrato') {
        me.pedido.anticipo_aprobado = e.value;
        me.saveAnticipoContrato();
      }
      if (e.tipo == 'guardar') {
        me.get_datos_pedido();
        return;
      }
    },
    getUserRoot: function getUserRoot(dato) {
      var _this = this;
      var me = this;
      this.$http.get(this.$server_url + 'permisos/' + dato.idusuario).then(function (result) {
        if (result.data.length > 0) {
          me.userRoot = true;
        }
        if (_this.usuario.id_group == 11 || _this.userRoot == true) {
          _this.disableAsesor = false;
          _this.disableFacturador = true;
        } else {
          _this.disableAsesor = true;
          _this.disableFacturador = false;
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    //metodo para actualizar la deuda en el anticipo
    changeAnticipo: function changeAnticipo(e) {
      var me = this;
      // me.pedido.ifanticipo                   = true
      me.pedido.deuda = Math.abs(e.setDeuda);
      // me.pedido.anticipo_aprobado            = Math.abs(e.setDeuda)
      me.pedido.periodo_deuda = "Deuda período " + e.periodo;
      // me.pedido.ifagregado_anticipo_aprobado = 1
      me.guardarDeuda();
    },
    guardarDeuda: function guardarDeuda() {
      var me = this;
      var formData = new FormData();
      formData.append('id_pedido', me.pedido.id_pedido);
      // formData.append('ifanticipo',                   1);
      formData.append('deuda', me.pedido.deuda);
      // formData.append('anticipo_aprobado',            me.pedido.anticipo_aprobado)
      formData.append('periodo_deuda', me.pedido.periodo_deuda);
      // formData.append('ifagregado_anticipo_aprobado', 1)
      this.$http.post(this.$server_url + 'guardarPedidoDeuda', formData).then(function (res) {}).catch(function (error) {});
    },
    //fin metodos para las deudas anteriores
    get_datos_pedido: function get_datos_pedido() {
      var me = this;
      this.$http.get(this.$server_url + 'get_datos_pedido/' + me.pedido.id_pedido).then(function (res) {
        var datos = res.data.pedido[0];
        me.tmpFiles = res.data.files;
        me.img_opcion_old = datos.imagen;
        //RUC
        me.img_opcion_old_ruc = datos.doc_ruc;
        me.getIdsMilton(datos);
        //fin datos institucion
        //SETEAR CEDULA
        if (me.img_opcion_old == null || me.img_opcion_old == "" || me.img_opcion_old == "null") {
          me.verImg = false;
        } else {
          me.verImg = true;
        }
        //SETEAR RUC
        if (me.img_opcion_old_ruc == null || me.img_opcion_old_ruc == "" || me.img_opcion_old_ruc == "null") {
          me.verImg_ruc = false;
        } else {
          me.verImg_ruc = true;
        }
        //FIN SETEAR
        if (datos.ifanticipo == 1) {
          me.pedido.ifanticipo = true;
        }
        if (datos.ifanticipo == 0) {
          me.pedido.ifanticipo = false;
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    getIdsMilton: function getIdsMilton(tr) {
      var me = this;
      var preCoincicias2 = '';
      var preArreglo = [];
      if (tr.codigo_mitlon_coincidencias == "null" || tr.codigo_mitlon_coincidencias == null || tr.codigo_mitlon_coincidencias == "") {} else {
        var transforWords = tr.codigo_mitlon_coincidencias;
        preCoincicias2 = transforWords.split(",");
      }
      //si no coincidencias en campo codigo milton coincidenias
      if (preCoincicias2 == "") {} else {
        preArreglo.push(preCoincicias2);
      }
      //setear array
      me.arregloIdsCoincidencias = preArreglo.flat();
      //no agregar ids duplicados
      var resolve = me.arregloIdsCoincidencias.filter(function (p) {
        return p == tr.codigo_institucion_milton;
      });
      if (resolve.length == 0) {
        me.arregloIdsCoincidencias.push(tr.codigo_institucion_milton);
      }
      me.verAnticipos = true;
    },
    verImagen: function verImagen(archivo) {
      var me = this;
      //cedula
      if (archivo == 0) {
        me.popupImagen = true;
      } else {
        me.popupRuc = true;
      }
    },
    verArchivo: function verArchivo(tip) {
      var me = this;
      var url;
      //tipo = 0 cedula ; 1 = ruc
      if (tip == 0) {
        url = this.$data_url + 'archivos/pedidos/img/img_cedula/' + me.img_opcion_old;
      } else {
        url = this.$data_url + 'archivos/pedidos/img/img_cedula/' + me.img_opcion_old_ruc;
      }
      window.open(url);
    },
    uploadFiles: function uploadFiles(e) {
      var me = this;
      me.attachmentArray = [];
      me.verImg = false;
      me.src_imagen_opcion = ' ';
      me.errorImage = 'no';
      me.tamanioError = "no";
      var validar = e.target.files[0].type;
      var tamanio = e.target.files[0].size;
      if (tamanio > 5000000) {
        me.tamanioError = "yes";
        me.$vs.notify({
          text: 'El tamaño de la imagen excede 5MB',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      if (validar == 'image/png' || validar == 'image/jpg' || validar == 'image/jpeg' || validar == 'application/pdf') {
        me.errorImage = 'no';
      } else {
        me.errorImage = 'yes';
        me.$vs.notify({
          text: 'Formato de imagen no aceptada solo se acepta png/jpg/jpeg/pdf',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      //var archivo = document.getElementById("file1").files[0];
      var archivo = e.target.files[0];
      me.tipoFile = "";
      if (archivo.type == "image/png" || archivo.type == "image/gif" || archivo.type == "image/jpeg" || archivo.type == "image/jpg") me.tipoFile = "img";else me.tipoFile = "other";
      var reader = new FileReader();
      if (me) {
        reader.readAsDataURL(archivo);
        reader.onloadend = function () {
          var cal = new Object();
          cal.file = archivo;
          cal.img = reader.result;
          cal.tipo = me.tipoFile;
          cal.name = archivo.name;
          me.attachmentArray.push(cal);
        };
      }
    },
    uploadFilesRUC: function uploadFilesRUC(e) {
      var me = this;
      me.attachmentArray_ruc = [];
      me.verImg_ruc = false;
      me.src_imagen_opcion_ruc = ' ';
      me.errorImage_ruc = 'no';
      me.tamanioError_ruc = "no";
      var validar = e.target.files[0].type;
      var tamanio = e.target.files[0].size;
      if (tamanio > 5000000) {
        me.tamanioError_ruc = "yes";
        me.$vs.notify({
          text: 'El tamaño de la imagen excede 5MB',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      if (validar == 'image/png' || validar == 'image/jpg' || validar == 'image/jpeg' || validar == 'application/pdf') {
        me.errorImage_ruc = 'no';
      } else {
        me.errorImage_ruc = 'yes';
        me.$vs.notify({
          text: 'Formato de imagen no aceptada solo se acepta png/jpg/jpeg/pdf',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      //var archivo = document.getElementById("file1").files[0];
      var archivo = e.target.files[0];
      me.tipoFile_ruc = "";
      if (archivo.type == "image/png" || archivo.type == "image/gif" || archivo.type == "image/jpeg" || archivo.type == "image/jpg") me.tipoFile_ruc = "img";else me.tipoFile_ruc = "other";
      var reader = new FileReader();
      if (me) {
        reader.readAsDataURL(archivo);
        reader.onloadend = function () {
          var cal = new Object();
          cal.file = archivo;
          cal.img = reader.result;
          cal.tipo = me.tipoFile_ruc;
          cal.name = archivo.name;
          me.attachmentArray_ruc.push(cal);
        };
      }
    },
    removeItemFromArr: function removeItemFromArr(arr, item, archivo) {
      //cedula
      if (archivo == 0) {
        this.verImg = false;
        document.getElementById("file10").value = '';
      } else {
        this.verImg_ruc = false;
        document.getElementById("file11").value = '';
      }
      var i = arr.indexOf(item);
      arr.splice(i, 1);
    },
    //guadar el estado para el facturador pueda ver o no
    //GUADAR EL ANTICIPO DESPUES DEL CONTRATO
    saveAnticipoContrato: function saveAnticipoContrato() {
      var me = this;
      var formData = new FormData();
      formData.append('id_pedido', me.pedido.id_pedido);
      formData.append('anticipo_aprobado', me.pedido.anticipo_aprobado);
      formData.append('contrato', me.pedido.contrato_generado);
      formData.append('user_created', me.usuario.idusuario);
      formData.append('id_group', me.usuario.id_group);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'guardarAnticipoAprobadoContrato', formData).then(function (res) {
        me.$vs.loading.close();
        me.get_datos_pedido();
        me.$vs.notify({
          text: res.data.message,
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (e) {
        me.$vs.loading.close();
      });
    },
    updatePedido: function updatePedido(numero, campo, valor, campo2, valor2) {
      var me = this;
      var formData = new FormData();
      if (numero == 1) {
        formData.append('unCampo', 'yes');
      }
      if (numero == 2) {
        formData.append('actualizarDosCampo', 'yes');
      }
      formData.append('id_pedido', me.pedido.id_pedido);
      formData.append('campo', campo);
      formData.append('campo2', campo2);
      formData.append('valor', valor);
      formData.append('valor2', valor2);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'updateThePedido', formData).then(function (res) {
        me.$vs.loading.close();
        me.$emit('rechargePedido', true);
        me.get_datos_pedido();
        me.$vs.notify({
          text: res.data.message,
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (e) {
        me.$vs.loading.close();
      });
    },
    guardarPedido: function guardarPedido() {
      var me = this;
      if (me.pedido.estado == 2) {
        me.$vs.notify({
          color: 'warning',
          title: 'Mensaje',
          text: 'No se puede modificar el pedido anulado',
          time: 5000
        });
        return false;
      }
      var fileImgPreg = "";
      var fileImgPreg_ruc = "";
      if (me.pedido.ifanticipo > 0) {
        if (me.verImg == false) {
          //subir la foto de la cedula de anticipo
          if (me.attachmentArray.length == 0) {
            me.$vs.notify({
              text: "Suba la foto de la cédula para evidencia del anticipo",
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
            return;
          }
        }
        if (me.verImg_ruc == false) {
          //subir la archivo para el RUC
          if (me.attachmentArray_ruc.length == 0) {
            me.$vs.notify({
              text: "Suba el archivo del ruc por favor",
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
            return;
          }
        }
        //VALIDACION CEDULA
        if (me.tamanioError == 'yes') {
          me.$vs.notify({
            text: 'El tamaño de la imagen de la cedula excede 5MB',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        if (me.errorImage == 'yes') {
          me.$vs.notify({
            text: 'El formato de la imagen de la cedula solo se acepta jpg/png/jpeg',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        //VALIDACION RUC
        if (me.tamanioError_ruc == 'yes') {
          me.$vs.notify({
            text: 'El tamaño del archivo del ruc excede 5MB',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        if (me.errorImage_ruc == 'yes') {
          me.$vs.notify({
            text: 'El formato del archivo del ruc solo se acepta jpg/png/jpeg/',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        if (me.pedido.anticipoAsesor > 0) {
          //si el anticipo que quiere el asesor es menor que el anticipo  sugerido
          // if(me.pedido.anticipoAsesor < me.pedido.anticipo){
          //   me.$vs.notify({
          //   text:'El anticipo que desea no puede ser menor que el anticipo segerido',
          //   color:'warning',
          //   iconPack: 'feather',
          //   icon:'icon-check'})
          //   return false;
          // }
        }
      }
      //CEDULA
      if (document.getElementById("file10") != null) {
        fileImgPreg = document.getElementById("file10").files[0];
      }
      if (document.getElementById("file11") != null) {
        fileImgPreg_ruc = document.getElementById("file11").files[0];
      }
      //RUC
      //actualizar fecha creacion pedido
      if (me.pedido.id_pedido > 0 && me.usuario.id_group == 11) {
        me.UpdateFechaCreacionPedido(me.pedido.id_pedido);
      }
      var formData = new FormData();
      me.$vs.loading();
      formData.append('id_pedido', me.pedido.id_pedido);
      // formData.append('anticipo',             me.pedido.anticipo);
      //cedula
      formData.append('imagen', fileImgPreg);
      formData.append('img_opcion_old', me.img_opcion_old);
      //ruc
      formData.append('doc_ruc', fileImgPreg_ruc);
      formData.append('img_opcion_old_ruc', me.img_opcion_old_ruc);
      var ianticipo = 0;
      if (me.pedido.ifanticipo) {
        ianticipo = 1;
      } else {
        ianticipo = 0;
      }
      formData.append('ifanticipo', ianticipo);
      formData.append('anticipoAsesor', me.pedido.anticipoAsesor);
      // formData.append('convenio_anios',       me.pedido.convenio_anios)
      //fin para actualizar la institucion
      me.$vs.loading();
      axios.post(this.$data_url + 'api/guardarPedido', formData).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == 0) {
          me.$vs.notify({
            text: res.data.message,
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-check',
            time: 8000
          });
          return;
        }
        if (res.data.error) {
          alert(res.data.error);
          return;
        } else {
          me.src_imagen_opcion = '';
          me.src_imagen_opcion_ruc = "";
          if (me.pedido.anticipo > 0 && me.usuario.id_group == 11) {
            document.getElementById("file10").value = '';
            document.getElementById("file11").value = '';
          }
        }
        me.$vs.notify({
          text: 'Se guardo correctamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.$emit('rechargePedido', true);
        me.get_datos_pedido();
        me.generateAnticipoAprobado();
      }).catch(function (err) {
        console.log(err);
        me.$vs.loading.close();
      });
    },
    UpdateFechaCreacionPedido: function UpdateFechaCreacionPedido(id_pedido) {
      var me = this;
      this.$http.get(this.$server_url + 'UpdateFechaCreacionPedido/' + id_pedido).then(function (res) {}).catch(function (error) {
        console.log(error + ' error');
      });
    },
    //===METODOS PARA ACTUALIZAR DOCUMENTOS ANTERIORES=====
    updateDocumentos: function updateDocumentos() {
      var me = this;
      this.$http.get(this.$server_url + 'updateDocumentoAnterior/' + me.ObjectPedido.id_pedido + '/' + '0').then(function (res) {}).catch(function (error) {
        console.log(error + ' error');
      });
    },
    //===METODOS DOCUMENTOS ANTERIORES========
    rechargeDocumentos: function rechargeDocumentos(e) {
      var me = this;
      //0 => only recharge; 1 => recharge and load pedido
      if (e == 0) {
        me.popupMostrarDocumentosAnteriores = false;
      }
      if (e == 1) {
        me.popupMostrarDocumentosAnteriores = false;
        me.get_datos_pedido();
        me.updateDocumentos();
        setTimeout(function () {
          me.pedido.ifanticipo = true;
        }, 2000);
      }
    },
    verDocumentosAnteriores: function verDocumentosAnteriores() {
      var me = this;
      var mensaje = "";
      if (me.pedido.ifanticipo) {
        mensaje = "activar anticipo";
      } else {
        mensaje = "desactivar anticipo";
      }
      if (me.userFacturador || me.userRoot) {
        this.$vs.dialog({
          type: 'confirm',
          color: 'danger',
          title: "Confirmar",
          acceptText: 'Si, deseo cambiar',
          cancelText: 'Cancelar',
          text: "Estas seguro de ".concat(mensaje, " ."),
          accept: this.acceptCambiarAnticipo
        });
        return;
      }
      if (me.pedido.ifanticipo) {
        me.cargarTodosDocumentos(2);
      }
    },
    cargarTodosDocumentos: function cargarTodosDocumentos(proceso) {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'getTraerDocumentoDocente/' + me.ObjectPedido.id_pedido).then(function (res) {
        me.$vs.loading.close();
        var datos = res.data;
        me.arregloDocumentosAnteriores = datos.filter(function (p) {
          return p.id_periodo != me.ObjectPedido.id_periodo;
        });
        if (proceso == 2 && me.arregloDocumentosAnteriores.length > 0) {
          me.popupMostrarDocumentosAnteriores = true;
        }
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    acceptCambiarAnticipo: function acceptCambiarAnticipo() {
      var me = this;
      me.campo = 'ifanticipo';
      var valor = 0;
      if (me.pedido.ifanticipo) {
        valor = 1;
      } else {
        valor = 0;
      }
      me.actualizarPedido(valor);
    },
    //===FIN METODOS DOCUMENTOS ANTERIORES====
    //===METODOS SUBIR DOCUMENTOS DESPUES DEL CONTRATO===
    subirDocumentosDespuesContrato: function subirDocumentosDespuesContrato() {
      var me = this;
      if (me.ObjectPedido.estado == 2) {
        me.$vs.notify({
          color: 'warning',
          title: 'Mensaje',
          text: 'No se puede modificar el pedido anulado',
          time: 5000
        });
        return false;
      }
      var fileImgPreg = "";
      var fileImgPreg_ruc = "";
      if (me.pedido.anticipo > 0 && me.usuario.id_group == 11) {
        if (me.verImg == false) {
          //subir la foto de la cedula de anticipo
          if (me.attachmentArray.length == 0) {
            me.$vs.notify({
              text: "Suba la foto de la cédula para evidencia del anticipo",
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
            return;
          }
        }
        if (me.verImg_ruc == false) {
          //subir el archivo para el RUC
          if (me.attachmentArray_ruc.length == 0) {
            me.$vs.notify({
              text: "Suba el archivo del ruc por favor",
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
            return;
          }
        }
        //VALIDACION CEDULA
        if (me.tamanioError == 'yes') {
          me.$vs.notify({
            text: 'El tamaño de la imagen de la cedula excede 5MB',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        if (me.errorImage == 'yes') {
          me.$vs.notify({
            text: 'El formato de la imagen de la cedula solo se acepta jpg/png/jpeg/pdf',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        //VALIDACION RUC
        if (me.tamanioError_ruc == 'yes') {
          me.$vs.notify({
            text: 'El tamaño del archivo del ruc excede 5MB',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        if (me.errorImage_ruc == 'yes') {
          me.$vs.notify({
            text: 'El formato del archivo del ruc solo se acepta jpg/png/jpeg/pdf',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
      }
      //CEDULA
      if (document.getElementById("file10") != null) {
        fileImgPreg = document.getElementById("file10").files[0];
      }
      //RUC
      if (document.getElementById("file11") != null) {
        fileImgPreg_ruc = document.getElementById("file11").files[0];
      }
      var formData = new FormData();
      me.actualizarPedidoSinValor("documentos Imagenes");
      me.$vs.loading();
      formData.append('id_pedido', me.pedido.id_pedido);
      //cedula
      formData.append('imagen', fileImgPreg);
      formData.append('img_opcion_old', me.img_opcion_old);
      //ruc
      formData.append('doc_ruc', fileImgPreg_ruc);
      formData.append('img_opcion_old_ruc', me.img_opcion_old_ruc);
      //fin para actualizar la institucion
      me.$vs.loading();
      axios.post(this.$data_url + 'api/guardarDocumentosDespuesContrato', formData).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == 0) {
          me.$vs.notify({
            text: res.data.message,
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-check',
            time: 8000
          });
          return;
        }
        if (res.data.error) {
          alert(res.data.error);
          return;
        } else {
          me.src_imagen_opcion = '';
          me.src_imagen_opcion_ruc = "";
          if (me.pedido.anticipo > 0 && me.usuario.id_group == 11) {
            document.getElementById("file10").value = '';
            document.getElementById("file11").value = '';
          }
        }
        me.$vs.notify({
          text: 'Se guardo correctamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.getPedidos();
        me.updateDocumentos();
      }).catch(function (err) {
        console.log(err);
        me.$vs.loading.close();
      });
    },
    actualizarPedidoSinValor: function actualizarPedidoSinValor(campo) {
      var me = this;
      var formData = new FormData();
      //noCambios cuando no voy a actualizar nada solo voy a poner en el historico que hubo cambio en el convenio
      formData.append('noCambios', "yes");
      formData.append('campo', campo);
      formData.append('id_pedido', me.pedido.id_pedido);
      formData.append('valor', "");
      formData.append('user_created', me.usuario.idusuario);
      this.$http.post(this.$server_url + 'actualizarPedido', formData).then(function (res) {}).catch(function (error) {});
    },
    //===FIN METODOS PARA SUBIR DOCUMENTOS DESPUES DEL CONTRATO====
    //===METODOS ROOT========
    actualizarPedido: function actualizarPedido(valor) {
      var me = this;
      var formData = new FormData();
      formData.append('actualizarCampo', 'yes');
      formData.append('campo', me.campo);
      formData.append('id_pedido', me.pedido.id_pedido);
      formData.append('valor', valor);
      formData.append('user_created', me.usuario.idusuario);
      this.$http.post(this.$server_url + 'actualizarPedido', formData).then(function (res) {
        me.$vs.notify({
          text: res.data.message,
          color: 'primary',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        me.get_datos_pedido();
      }).catch(function (error) {});
    } //===FIN METODOS ROOT====
  }
};
