    descargaArchivo: function descargaArchivo(item) {
      var _this = this;
      // console.log(item)
      var me = this;
      var h_idinstitucion = me.miusuario.institucion_idInstitucion;
      var h_idusuario = me.miusuario.idusuario;
      var h_idgrupo = me.miusuario.id_group;
      var h_idasignatura = me.proyectos.proyectos_asignaturas[0].idasignatura;
      var h_nombreasignatura = me.proyectos.proyectos_asignaturas[0].datos.nombreasignatura;
      var h_recursotipo = 5; //5 = proyecto
      var datoHistorico = {
        'user_nombre': me.miusuario.nombres,
        'user_apellido': me.miusuario.apellidos,
        'user_email': me.miusuario.email,
        'user_cedula': me.miusuario.cedula,
        'recurso_accion': 'descargar',
        'recurso_tipo': 'proyecto',
        'recurso_nombre': me.proyectos.nombre,
        'recurso_detalle': item.name
      };
      // console.log(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo, datoHistorico)
      me.historicoVisitas(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo, datoHistorico);
      me.saveHistoricoVisitas(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo, datoHistorico);
      if (this.grupo_usuario == 9) {
        this.$vs.notify({
          text: 'El archivo no esta disponible en la version DEMO',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      this.$vs.loading();
      axios({
        url: 'https://foro.prolipadigital.com.ec' + item.url,
        method: 'GET',
        responseType: 'blob'
      }).then(function (response) {
        var fileURL = window.URL.createObjectURL(new Blob([response.data]));
        var fileLink = document.createElement('a');
        fileLink.href = fileURL;
        fileLink.setAttribute('download', item.name + item.ext);
        document.body.appendChild(fileLink);
        fileLink.click();
        _this.$vs.notify({
          time: 6000,
          text: 'El archivo se esta descargando',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        _this.$vs.loading.close();
      }).catch(function (error) {
        _this.$vs.notify({
          text: 'Error en la descarga del archivo',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        _this.$vs.loading.close();
      });
    },
