import $ from 'jquery';
import vSelect from 'vue-select';
export default {
  components: {
    'v-select': vSelect
  },
  data: function data() {
    return {
      busquedaIndividual: '',
      ifInsititucion: false,
      error: false,
      desglose: false,
      popupActivo: false,
      popupIndividual: false,
      institucion_id: '',
      counterDanger: false,
      arregloCiudad: [],
      instituciones: [],
      popupInstitucion: false,
      nombreInstitucion: '',
      popupAddNuevaInstitucion: false,
      temporalInstitucion: {
        ciudad: '',
        nombreInstitucion: '',
        region: '2'
      },
      institucion_id_temporal: '',
      estado_institucion_temporal: 2,
      periodo_id_temporal: 0,
      nombreInstitucionTemporal: '',
      busquedaInstitucion: '',
      longitud: '0',
      txt_ingreso: '',
      txt_observacion: '',
      todoCodigos: '',
      m_mensaje: '',
      codigos: '',
      repetidas: '',
      codigosDuplicados: false,
      arrayCodigosDuplicados: [],
      id: '',
      codigo: '',
      popupDuplicados: false,
      codigosEscuelas: [],
      usuario: [],
      popupBusqueda: false,
      filtroBusqueda: [],
      arregloBusquedaIndividual: [],
      busqueda: ''
    };
  },
  mounted: function mounted() {
    var me = this;
    this.focusInput();
    // me.f_get_codigos();
  },
  created: function created() {
    this.usuario = JSON.parse(localStorage.getItem('usuario'));
    this.listaCiudades();
  },
  methods: {
    //busqueda individual de codigo
    busquedaCodigoIndividual: function busquedaCodigoIndividual() {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'bodega?individual=yes&codigo=' + me.busquedaIndividual).then(function (res) {
        me.$vs.loading.close();
        me.arregloBusquedaIndividual = res.data;
      }).catch(function (error) {
        me.$vs.loading.close();
        console.log(error);
      });
    },
    limpiar: function limpiar() {
      var me = this;
      me.txt_observacion = "";
      me.institucion_id = "";
      me.institucion_temporal_id = "";
      me.nombreInstitucion = "";
      me.estado_institucion_temporal = "2";
      $("#detalle").prop('disabled', false);
    },
    asginarInstitucion: function asginarInstitucion(tr) {
      var me = this;
      me.institucion_id = tr.idInstitucion;
      me.nombreInstitucion = tr.nombreInstitucion;
      me.txt_observacion = me.nombreInstitucion;
      me.estado_institucion_temporal = 0;
      me.ifInsititucion = true;
      me.instituciones = [];
      $("#detalle").prop('disabled', true);
    },
    //lista de ciudades para usar en el select
    listaCiudades: function listaCiudades() {
      var me = this;
      this.$http.get(this.$server_url + "ciudad").then(function (res) {
        me.arregloCiudad = res.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    getInstitucion: function getInstitucion() {
      var me = this;
      me.instituciones = [];
      if (me.busquedaInstitucion.length < 5) {
        me.$vs.notify({
          text: 'Minimo 5 caracteres de busqueda',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      me.$vs.loading();
      this.$http.get(this.$server_url + 'listaInsitucion?busqueda=' + me.busquedaInstitucion).then(function (res) {
        // me.tmpInstituciones = res.data;
        me.instituciones = res.data;
        // me.filtroRegion();
        me.$vs.loading.close();
        if (res.data.message) {
          me.$vs.notify({
            text: res.data.message,
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-user',
            time: 8000
          });
        }
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    agregarInstitucionTemporal: function agregarInstitucionTemporal() {
      var me = this;
      if (me.temporalInstitucion.nombre_institucion == "" || me.temporalInstitucion.nombre_institucion == null || me.temporalInstitucion.nombre_institucion == undefined) {
        me.$vs.notify({
          color: 'primary',
          title: 'Mensaje',
          text: 'Debe escribir una institución por favor'
        });
        return false;
      }
      var formData = new FormData();
      formData.append('nombre_institucion', me.temporalInstitucion.nombre_institucion);
      formData.append('ciudad', me.temporalInstitucion.ciudad.nombre);
      formData.append('region', me.temporalInstitucion.region);
      formData.append('asesor_id', me.usuario.idusuario);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'GuardarInstitucionTemporal', formData).then(function (res) {
        me.$vs.loading.close();
        me.popupAddNuevaInstitucion = false;
        me.popupInstitucion = true;
        me.nombreInstitucion = me.temporalInstitucion.nombre_institucion;
        me.txt_observacion = me.nombreInstitucion;
        me.institucion_id_temporal = res.data.institucion_temporal_id;
        me.instituciones = [];
        me.periodo_id_temporal = res.data.periodo_id;
        me.estado_institucion_temporal = 1;
        me.ifInsititucion = true;
        $("#detalle").prop('disabled', true);
        me.$vs.notify({
          text: "Se guardo correctamente",
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      });
    },
    //para obtener el filtro de busqueda
    filtrarBusqueda: function filtrarBusqueda() {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'bodegaFiltro?busqueda=' + me.busqueda).then(function (res) {
        me.filtroBusqueda = res.data;
        me.popupBusqueda = true;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    //para obtener el listado de las areas
    getCodigosEscuela: function getCodigosEscuela() {
      var me = this;
      if (me.busqueda == "") {
        me.$vs.notify({
          text: 'ingrese un detalle para esta busqueda',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle',
          time: 8000
        });
        return;
      }
      me.$vs.loading();
      this.$http.get(this.$server_url + 'desgloseCodigo?escuela=' + me.txt_observacion).then(function (res) {
        var codigos = res.data;
        var extraida = [];
        var resultado = codigos.reduce(function (acumulador, obj) {
          var key = obj.codigo;
          // console.log(key)
          //  console.log(key,'rr',key.indexOf('-')) 
          extraida.push(key.substring(0, key.indexOf('-')));
          if (!acumulador[key]) {
            acumulador[key] = [];
          }
          acumulador[key].push(obj);
          return acumulador;
        }, {});
        var repetidos = {};
        extraida.forEach(function (numero) {
          repetidos[numero] = (repetidos[numero] || 0) + 1;
        });
        console.log(repetidos);
        me.codigosEscuelas = repetidos;
        me.desglose = true;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    focusInput: function focusInput() {
      this.$refs.ingreso.$el.focus();
    },
    buscarEscuela: function buscarEscuela() {
      var me = this;
    },
    f_ingresar: function f_ingresar() {
      var me = this;
      me.codigos = "";
      me.repetidas = "";
      if (me.txt_observacion == "") {
        me.$vs.notify({
          text: 'ingrese un detalle para este grupo',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle',
          time: 8000
        });
        return;
      }
      if (me.txt_ingreso.length > me.longitud) {
        me.popupActivo = true;
        return;
      }
      var formData = new FormData();
      formData.append('codigo', me.txt_ingreso);
      formData.append('observacion', me.txt_observacion);
      formData.append('usuario', me.usuario.idusuario);
      formData.append('institucion_id', me.institucion_id);
      formData.append('institucion_id_temporal', me.institucion_id_temporal);
      formData.append('estado_institucion_temporal', me.estado_institucion_temporal);
      formData.append('periodo_id_temporal', me.periodo_id_temporal);
      formData.append('nombreInstitucion', me.nombreInstitucion);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'add_codigo', formData).then(function (res) {
        if (res.data.codigosRepetidos.length == 0) {} else {
          me.popupDuplicados = true;
          me.codigosDuplicados = true;
          me.codigos = res.data.codigosRepetidos.codigos;
          me.repetidas = res.data.codigosRepetidos.repetidas;
          me.arrayCodigosDuplicados.push({
            "codigo": res.data.codigosRepetidos.codigos,
            "repetidas": res.data.codigosRepetidos.repetidas
          });
        }
        me.$vs.loading.close();
        me.txt_ingreso = "";
        me.f_get_codigos();
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    f_get_codigos: function f_get_codigos() {
      var me = this;
      // me.$vs.loading()
      me.m_mensaje = 'cargando lista....';
      this.$http.get(this.$server_url + 'get_codigo').then(function (res) {
        // console.log(res.data)
        me.todoCodigos = res.data;
        me.m_mensaje = '';
        me.txt_ingreso = '';
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    openConfirm: function openConfirm(item) {
      var me = this;
      me.id = item.id;
      me.codigo = item.codigo;
      this.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: "Confirm",
        text: 'ESTA SEGURO DE ELIMINAR_.',
        accept: this.f_eliminar
      });
    },
    f_eliminar: function f_eliminar(item) {
      var me = this;
      // console.log(item)
      // return
      me.$vs.loading();
      var formData = new FormData();
      formData.append('id', me.id);
      formData.append('codigo', me.codigo);
      formData.append('observacion', me.txt_observacion);
      this.$http.post(this.$server_url + 'elimina_codigo', formData).then(function (res) {
        console.log(res.data);
        // me.todoCodigos= res.data;
        me.f_get_codigos();
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    }
  }
};
