import axios from 'axios';
import vSelect from 'vue-select';
import flatPickr from 'vue-flatpickr-component';
import 'flatpickr/dist/flatpickr.css';
import moment from 'moment';
import niveles from "./matricula_niveles.vue";
import importarexcel from "./components/excel_pagos.vue";
import importarexcelbeca from "./components/excel_becas.vue";
import procesarMatriculas from "./components/excel_procesa_matriculas.vue";
import informacionEstudiante from "./components/informacionEstudiante.vue";
export default {
  data: function data() {
    return {
      color_fila: [],
      usuario: [],
      estudiantes: [],
      estudiante: {},
      modalMatricula: false,
      observaciones: '',
      estado_matricula: 0,
      modalPagos: false,
      modalSolicitud: false,
      niveles: [],
      paralelos: [],
      comprobante_pago: {},
      forma_pago: '',
      formas_pago: ['Efectivo', 'Transferencia', 'Cheque'],
      banco: '',
      bancos: ['Banco Pichincha', 'Banco Guayaquil', 'Produbanco', 'Coop. 29 Octubre'],
      n_tr_ch: '',
      val_pagado: 0,
      tipo_listado: 1,
      fecha_actual: '',
      estado_pago: [],
      activePrompt: false,
      fileName: '',
      estudiantes_selected: [],
      modalImportar: false,
      modalBeca: false,
      porcentaje_beca: '',
      aplicacion_beca: '',
      formas_beca: [{
        id: 1,
        label: 'Solo a pensiones'
      }, {
        id: 2,
        label: 'Solo a matrícula'
      }, {
        id: 3,
        label: 'Matrícula y pensiones'
      }],
      modalProcesaMatricula: false,
      matricula_admin: '',
      institucionAdmin: '',
      modalCuotas: false,
      val_cuota_edit: 0,
      val_pend_edit: 0,
      cuotas_matricula: [],
      id_matricula_selected: '',
      informacionEstudiantes: [],
      emailuser: '',
      periodos: [],
      periodo_selected: {},
      value_filtro1: '',
      value_filtro2: '',
      value_filtro3: '',
      value_filtro4: ''
    };
  },
  components: {
    'v-select': vSelect,
    flatPickr: flatPickr,
    niveles: niveles,
    importarexcel: importarexcel,
    importarexcelbeca: importarexcelbeca,
    procesarMatriculas: procesarMatriculas,
    informacionEstudiante: informacionEstudiante
  },
  created: function created() {
    this.usuario = JSON.parse(localStorage.getItem('usuario'));
    this.matricula_admin = localStorage.getItem("matricula_admin");
    // this.getEstudiantes(2)// 2 carga todo pero no descarga excel
    this.getCombos();
  },
  methods: {
    getPeriodos: function getPeriodos() {
      var me = this;
      this.$http.get(this.$server_url + 'periodosXInstitucion/' + me.institucionAdmin).then(function (res) {
        me.periodos = res.data;
        me.periodo_selected = me.periodos[0];
      });
    },
    getCombos: function getCombos() {
      var me = this;
      if (me.matricula_admin == "yes") {
        me.institucionAdmin = localStorage.getItem("matricula_institucion");
      } else {
        me.institucionAdmin = me.usuario.institucion_idInstitucion;
      }
      me.getPeriodos();
      this.$http.get(this.$server_url + 'combos_matricula/' + me.institucionAdmin).then(function (res) {
        me.niveles = res.data.niveles;
        me.paralelos = res.data.paralelos;
      });
    },
    getEstudiantes: function getEstudiantes(excel) {
      var me = this;
      me.$vs.loading();
      var descarga_excel = 'no_excel';
      if (excel == 1 | excel == 2) {
        descarga_excel = 1;
      }
      if (me.matricula_admin == "yes") {
        me.institucionAdmin = localStorage.getItem("matricula_institucion");
      } else {
        me.institucionAdmin = me.usuario.institucion_idInstitucion;
      }
      this.$http.get(this.$server_url + 'listado_matriculas/' + me.institucionAdmin + '/' + me.periodo_selected.idperiodoescolar + '/' + me.tipo_listado + '/' + descarga_excel).then(function (res) {
        var date_actual = new Date();
        me.fecha_actual = moment(date_actual, 'DD/MM/YYYY HH:mm:ss').format('YYYY-MM-DD');
        me.estudiantes = res.data.items;
        if (excel == 1) {
          me.estudiantes_selected = res.data.items;
          me.exportToExcel();
        }
        me.$vs.loading.close();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    getCuotas: function getCuotas(id_matricula) {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'get_cuotas/' + id_matricula).then(function (res) {
        me.cuotas_matricula = res.data;
        me.$vs.loading.close();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    filtro_busqueda: function filtro_busqueda(value_filtro, tipo) {
      var me = this;
      if (value_filtro == '') {
        return;
      }
      me.$vs.loading();
      this.$http.get(this.$server_url + 'busqueda_estudiante_mat/' + me.periodo_selected.idperiodoescolar + '/' + me.institucionAdmin + '/' + tipo + '/' + value_filtro).then(function (res) {
        me.estudiantes = res.data.items;
        me.$vs.loading.close();
        return;
      }).catch(function (error) {
        me.$vs.loading.close();
        return;
      });
    },
    getDataEstudiante: function getDataEstudiante() {
      var me = this;
      me.$vs.loading();
      var cedula = localStorage.getItem("cedulaEstudiante");
      var institucion = localStorage.getItem("institucionEstudiante");
      this.$http.get(this.$server_url + 'estudiante/matricula?cedula=' + cedula + '&institucion_id=' + institucion).then(function (res) {
        me.informacionEstudiantes = res.data[0];
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    verEstudiante: function verEstudiante(item) {
      var me = this;
      me.getCuotas(item.matricula.id_matricula);
      me.modalMatricula = true;
      localStorage.setItem('emailEstudiante', item.matricula.email);
      localStorage.setItem('cedulaEstudiante', item.matricula.cedula);
      localStorage.setItem('institucionEstudiante', item.matricula.institucion_idInstitucion);
      me.getDataEstudiante(item.matricula.cedula, item.matricula.institucion_idInstitucion);
      me.estudiante = item;
      me.estado_matricula = item.matricula.estado_matricula;
      me.estudiante.matricula.nivel = {
        "nombrenivel": item.matricula.nombrenivel,
        "nivel_id": item.matricula.nivel_id
      };
      me.estudiante.matricula.paralelo = {
        "paralelo_id": item.matricula.paralelo_id,
        "descripcion": item.matricula.descripcion
      };
    },
    modificarMatricula: function modificarMatricula() {
      var me = this;
      var formData = new FormData();
      formData.append('id_matricula', me.estudiante.matricula.id_matricula);
      formData.append('estado_matricula', me.estado_matricula);
      formData.append('nivel', me.estudiante.matricula.nivel.nivel_id);
      formData.append('paralelo', me.estudiante.matricula.paralelo.paralelo_id);
      formData.append('id_usuario', me.estudiante.matricula.idusuario);
      formData.append('id_director', me.usuario.idusuario);
      formData.append('cedulaEstudiante', localStorage.cedulaEstudiante);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'matriculas_admin', formData).then(function (res) {
        me.modalMatricula = false;
        me.$vs.loading.close();
        me.getEstudiantes('no_excel');
        me.$vs.notify({
          text: 'Matricula modificada exitosamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    guardarPago: function guardarPago() {
      var me = this;
      me.$vs.loading();
      var formData = new FormData();
      formData.append('forma_pago', me.forma_pago);
      formData.append('banco', me.banco);
      formData.append('n_tr_ch', me.n_tr_ch);
      formData.append('val_pagado', me.val_pagado);
      formData.append('id_cuotas_id', me.comprobante_pago.id_cuotas_id);
      formData.append('id_matricula', me.comprobante_pago.id_matricula);
      formData.append('id_usuario', me.usuario.idusuario);
      this.$http.post(this.$server_url + 'guardar_pago_matricula', formData).then(function (res) {
        me.$vs.loading.close();
        if (res.data == 2) {
          me.$vs.notify({
            text: 'El valor ingresado excede el total pendiente de todas las cuotas faltantes.',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
        } else {
          me.getCuotas(me.id_matricula_selected);
          me.modalPagos = false;
          me.$vs.notify({
            text: 'Cuota pagada exitosamente',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-check'
          });
        }
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    exportToExcel: function exportToExcel() {
      var _this = this;
      var me = this;

      // let headerTitle = ['Cédula', 'Nombres', 'Apellidos', 'Teléfono','Fecha matriculación', 'Estado matrícula', 'Periodo', 'Valor cuota', 'valor pendiente', 'fecha pago', 'N. cuota', 'Solicitud de pago'];
      var headerTitle = ['Cédula', 'Nombres', 'Apellidos', 'Teléfono', 'Fecha matriculación', 'Estado matrícula', 'Periodo', 'Nivel', 'Paralelo'];
      // let headerVal = ['cedula', 'nombres', 'apellidos', 'telefono','fecha_matricula', 'estado_matricula', 'descripcion_periodo', 'valor_cuota', 'valor_pendiente', 'fecha_a_pagar', 'num_cuota', 'comentario'];
      var headerVal = ['cedula', 'nombres', 'apellidos', 'telefono', 'fecha_matricula', 'estado_matricula', 'descripcion_periodo', 'nivel', 'paralelo'];
      var estudiantes_filed = [];
      $.each(me.estudiantes_selected, function (key, value) {
        // if(value.cxc.length > 0){
        //     $.each(value.cxc,function(key_1, value_1){
        //         estudiantes_filed.push({
        //             cedula: value.matricula.cedula,
        //             nombres: value.matricula.nombres,
        //             apellidos: value.matricula.apellidos,
        //             telefono: value.matricula.telefono,
        //             fecha_matricula: value.matricula.fecha_matricula,
        //             estado_matricula: value.matricula.estado_matricula,
        //             descripcion_periodo: value.matricula.descripcion_periodo,
        //             valor_cuota: value_1.valor_cuota,
        //             valor_pendiente: value_1.valor_pendiente,
        //             fecha_a_pagar: value_1.fecha_a_pagar,
        //             num_cuota: value_1.num_cuota,
        //             comentario: value_1.comentario,
        //         })
        //     });
        // }else{
        //     estudiantes_filed.push({
        //         cedula: value.matricula.cedula,
        //         nombres: value.matricula.nombres,
        //         apellidos: value.matricula.apellidos,
        //         telefono: value.matricula.telefono,
        //         fecha_matricula: value.matricula.fecha_matricula,
        //         estado_matricula: value.matricula.estado_matricula,
        //         descripcion_periodo: value.matricula.descripcion_periodo
        //     })
        // }

        estudiantes_filed.push({
          cedula: value.matricula.cedula,
          nombres: value.matricula.nombres,
          apellidos: value.matricula.apellidos,
          telefono: value.matricula.telefono,
          fecha_matricula: value.matricula.fecha_matricula,
          estado_matricula: value.matricula.estado_matricula,
          descripcion_periodo: value.matricula.descripcion_periodo,
          nivel: value.matricula.nivel,
          paralelo: value.matricula.paralelo
        });
      });

      // console.log('**********+')
      // console.log({estudiantes_filed})
      // console.log('**********+')

      import('@/vendor/Export2Excel').then(function (excel) {
        var list = estudiantes_filed;
        var data = _this.formatJson(headerVal, list);
        excel.export_json_to_excel({
          header: headerTitle,
          data: data,
          filename: _this.fileName,
          autoWidth: true,
          bookType: 'xlsx'
        });
      });
    },
    formatJson: function formatJson(filterVal, jsonData) {
      return jsonData.map(function (v) {
        return filterVal.map(function (j) {
          return v[j];
        });
      });
    },
    aplicarBeca: function aplicarBeca() {
      var me = this;
      var formData = new FormData();
      formData.append('estudiantes_selected', JSON.stringify(me.estudiantes_selected));
      formData.append('porcentaje_beca', me.porcentaje_beca);
      formData.append('aplicacion_beca', me.aplicacion_beca.id);
      formData.append('id_user_action', me.usuario.idusuario);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'aplicar_becas', formData).then(function (res) {
        me.modalBeca = false;
        me.$vs.loading.close();
        me.getEstudiantes('no_excel');
        me.$vs.notify({
          text: 'Becas aplicadas exitosamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.estudiantes_selected = [];
      }).catch(function (error) {
        me.$vs.loading.close();
        me.estudiantes_selected = [];
      });
    },
    fillformPago: function fillformPago(item) {
      var me = this;
      me.id_matricula_selected = item.id_matricula;
      me.id_cuota_edit = item.id_cuotas_id;
      me.val_cuota_edit = item.valor_cuota;
      me.val_pend_edit = item.valor_pendiente;
      me.modalCuotas = true;
    },
    editarCuota: function editarCuota() {
      var me = this;
      var formData = new FormData();
      formData.append('id_cuota_edit', me.id_cuota_edit);
      formData.append('valor_cuota', me.val_cuota_edit);
      formData.append('valor_pendiente', me.val_pend_edit);
      formData.append('id_usuario', me.usuario.idusuario);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'editar_cuotas', formData).then(function (res) {
        me.modalCuotas = false;
        me.$vs.loading.close();
        me.getCuotas(me.id_matricula_selected);
        me.$vs.notify({
          text: 'Cuota modificada exitosamente.',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    actualizarData: function actualizarData() {
      var me = this;
      me.informacionEstudiantes.institucion = me.usuario.institucion;
      me.informacionEstudiantes.administrador = "yes";
      me.$vs.loading();
      this.$http.post(this.$server_url + 'updateEstudianteAdministrador', me.informacionEstudiantes).then(function (res) {
        me.$vs.loading.close();
        me.$vs.notify({
          text: res.data.message,
          color: 'primary',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        me.getDataEstudiante();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    generarPassword: function generarPassword() {
      var me = this;
      var formData = new FormData();
      this.$vs.loading({});
      me.emailuser = localStorage.getItem("emailEstudiante");
      formData.append('email', me.emailuser);
      this.$http.post(this.$server_url + 'restaurarPassword', formData).then(function (response) {
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'Use el número de cédula para acceder al perfil del usuario.',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-lock',
          time: 8000
        });
      }).catch(function (error) {
        me.$vs.notify({
          text: 'Error en el proceso',
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-lock'
        });
        me.$vs.loading.close();
      });
    }
  }
};
