import "core-js/modules/web.dom.iterable";
import "core-js/modules/es6.regexp.replace";
import "core-js/modules/es6.number.constructor";
import $ from 'jquery';
import TableSelectInstitucionVue from "../../../../usuarios/GestionEstudiantes/components/TableSelectInstitucion.vue";
export default {
  data: function data() {
    return {
      usuario: [],
      arregloObsequios: [],
      vReal: 0,
      contrato: '',
      porcentajeObsequio: 0,
      descuentoEscuela: 0,
      maximo_porcentaje_autorizado: 0,
      total_gastado: 0,
      valor_obsequios: 0,
      valueAvaible: 0,
      institucion_id: 0,
      nombreInstitucion: '',
      error: false,
      statusPeriodo: 0,
      msgError: '!No hay valores de obsequios',
      inputs: [{
        id: 0,
        cantidad: '',
        descripcion: '',
        especificacion: ''
      }],
      changeValues: false,
      //aprobacion
      observacion_facturador: '',
      counterDanger: false,
      estado: 0,
      mostrarContainer: true,
      //fin aprobacion
      //variables export
      formats: ['xls', 'xlsx', 'csv', 'txt'],
      fileName: null,
      selectedFormat: 'xls',
      cellAutoWidth: true,
      activePrompt: false
      //fin variables export
    };
  },
  components: {
    TableSelectInstitucionVue: TableSelectInstitucionVue
  },
  props: {
    pedido: {
      type: Object,
      default: {}
    },
    arregloPeriodo: {
      type: Object,
      default: {}
    },
    periodo_id: {
      type: String,
      default: "0"
    },
    id: {
      type: Number,
      default: 0
    },
    userAdmin: {
      type: Boolean,
      default: false
    },
    userRoot: {
      type: Boolean,
      default: false
    },
    userAsesor: {
      type: Boolean,
      default: false
    },
    userFacturador: {
      type: Boolean,
      default: false
    }
  },
  watch: {
    valueAvaible: function valueAvaible(results) {
      var me = this;
      if (results == 0 || results < 1) {
        me.mostrarContainer = false;
        // $('#containerH').css('display', 'none');
        if (me.usuario.id_group == 1) {
          me.changeValues = true;
        } else {
          me.changeValues = false;
        }
      } else {
        console.log("test", me.usuario.id_group);
        if (me.usuario.id_group == 11 || me.usuario.id_group == 1 || me.usuario.id_group == 22 || me.usuario.id_group == 23) {
          me.changeValues = true;
        } else {
          me.changeValues = false;
        }
        me.mostrarContainer = true;
        // $('#containerH').css('display', 'block');
      }
    }
  },
  created: function created() {
    var me = this;
    me.usuario = JSON.parse(localStorage.getItem("usuario"));
  },
  mounted: function mounted() {
    var me = this;
    //comentario facturador
    me.observacion_facturador = me.pedido.observacion_facturador;
    if (me.id > 0) {
      if (me.userRoot || me.userAdmin) {
        me.changeValues = true;
      }
      me.getPedido();
      me.institucion_id = me.pedido.institucion_id;
      me.getInfoPedidoInstitucion();
    }
  },
  computed: {
    SumaObsequios: function SumaObsequios() {
      var me = this;
      var preCalculo2 = me.vReal * me.porcentajeObsequio / 100;
      me.valor_obsequios = me.vReal * me.porcentajeObsequio / 100;
      me.valueAvaible = preCalculo2 - me.total_gastado;
      return preCalculo2;
    }
  },
  methods: {
    filtrarDecimal: function filtrarDecimal(numero) {
      var me = this;
      if (numero == 0) {
        return numero;
      } else {
        var transformar = numero.toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&,');
        return transformar;
      }
    },
    getPedido: function getPedido() {
      var me = this;
      me.inputs = [];
      this.$http.get(this.$server_url + 'obsequio/' + me.id).then(function (res) {
        var datos = res.data.detalle;
        if (datos.length > 0) {
          datos.forEach(function (element) {
            var cal = new Object();
            cal.id = element.id;
            cal.valor_obsequios = res.data.pedido[0].valor_obsequios;
            cal.obsequios_id = element.obsequios_id;
            cal.cantidad = element.cantidad;
            cal.descripcion = element.descripcion;
            cal.especificacion = element.especificacion;
            cal.created_at = element.created_at;
            cal.updated_at = element.updated_at;
            cal.asesor = me.pedido.asesor;
            cal.asesor = me.pedido.asesor;
            cal.nombreInstitucion = me.pedido.nombreInstitucion;
            cal.ciudad = me.pedido.ciudad;
            cal.periodo = me.pedido.periodo;
            me.inputs.push(cal);
          });
        }
      }).catch(function (error) {
        console.log(error + ' error');
      });
    },
    changeHandle: function changeHandle(e) {
      var me = this;
      me.descuentoEscuela = 0;
      me.porcentajeObsequio = 0;
      me.total_gastado = 0;
      me.contrato = '';
      me.vReal = 0;
      me.valueAvaible = 0;
      me.valor_obsequios = 0;
      me.inputs = [{
        id: 0,
        cantidad: '',
        descripcion: '',
        especificacion: ''
      }];
      // me.institucion_id                   = 0
      me.msgError = '!No hay valores de obsequios';
      // $('#containerH').css('display', 'none');
      me.mostrarContainer = false;
      if (me.userRoot || me.userAdmin) {
        me.changeValues = true;
      } else {
        me.changeValues = false;
      }
      var valor = e.change;
      if (valor == 'institucion') me.institucion_id = e.value;
      if (valor == 'nombreInstitucion') me.nombreInstitucion = e.value;
      if (valor == 'noPeriodo') me.error = e.value;
      if (valor == 'periodo') me.statusPeriodo = e.value;
      if (me.institucion_id > 0) {
        me.getInfoPedidoInstitucion();
      }
    },
    getInfoPedidoInstitucion: function getInfoPedidoInstitucion() {
      var me = this;
      this.$http.get(this.$server_url + 'obsequio?institucionPedido=yes&institucion_id=' + me.institucion_id + '&periodo_id=' + me.periodo_id).then(function (res) {
        if (res.data.status == 0) {
          me.msgError = res.data.message;
        } else {
          me.msgError = '!No hay valores de obsequios';
        }
        var datos = res.data;
        if (datos.length > 0) {
          me.maximo_porcentaje_autorizado = datos[0].maximo_porcentaje_autorizado;
          var porcentaje_obsequio = datos[0].porcentaje_obsequio;
          //porcentaje maximo - 5% de obsequios
          var preMaximoDescuento = me.maximo_porcentaje_autorizado - porcentaje_obsequio;
          me.vReal = datos[0].veN_VALOR;
          me.total_gastado = datos[0].total_gastado;
          me.descuentoEscuela = datos[0].veN_DESCUENTO;
          me.contrato = datos[0].veN_CODIGO;
          //si la comision de la escuela supera el maximo porcentaje de descuento
          if (me.descuentoEscuela >= me.maximo_porcentaje_autorizado) {
            me.porcentajeObsequio = 0;
            me.mostrarContainer = true;
            // $('#containerH').css('display', 'block');
            return;
          }
          if (me.descuentoEscuela <= preMaximoDescuento) {
            me.porcentajeObsequio = porcentaje_obsequio;
            return;
          } else {
            var resultado = 0;
            resultado = me.maximo_porcentaje_autorizado - me.descuentoEscuela;
            me.porcentajeObsequio = resultado;
            return;
          }
        }
      }).catch(function (error) {
        console.log(error + ' error');
      });
    },
    openConfirm: function openConfirm() {
      var me = this;
      var valCantidad = [];
      var ValDescripcion = [];
      valCantidad = me.inputs.filter(function (p) {
        return p.cantidad == 0 || p.cantidad == "" || p.cantidad < 0;
      });
      ValDescripcion = me.inputs.filter(function (p) {
        return p.descripcion == '' || p.descripcion == null || p.descripcion < undefined;
      });
      if (valCantidad.length > 0) {
        me.$vs.notify({
          text: 'Complete todos los campos o elimine los registros vacios',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check',
          time: 6000
        });
        return false;
      }
      if (ValDescripcion.length > 0) {
        me.$vs.notify({
          text: 'Campo descripción obligatorio llene por favor',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check',
          time: 4000
        });
        return false;
      }
      if (me.error && me.id == 0) {
        me.$vs.notify({
          text: 'La institución no tiene período',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      if (me.periodo_id <= 0 && me.id == 0) {
        me.$vs.notify({
          text: 'Seleccione una período por favor',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      if (me.institucion_id <= 0) {
        me.$vs.notify({
          text: 'Seleccione una institución por favor',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      if (me.statusPeriodo == 0 && me.id == 0) {
        me.$vs.notify({
          text: 'La institución tiene período inactivo, consulte con el administrador por favor',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      this.$vs.dialog({
        type: 'confirm',
        color: 'warning',
        acceptText: 'Si deseo guardar',
        cancelText: 'Cancelar',
        title: "Confirmar",
        text: 'Estás seguro de guardar el pedido?',
        accept: this.saveOrder
      });
    },
    saveOrder: function saveOrder() {
      var me = this;
      var formData = new FormData();
      formData.append('id', me.id);
      formData.append('institucion_id', me.institucion_id);
      formData.append('periodo_id', me.periodo_id);
      formData.append('asesor_id', me.usuario.idusuario);
      formData.append('data_obsequios', JSON.stringify(me.inputs));
      formData.append('maximo_porcentaje_autorizado', me.maximo_porcentaje_autorizado);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'obsequio', formData).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == 0) {
          me.$vs.notify({
            text: res.data.message,
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return;
        }
        me.$emit('changeRecharge', true);
        me.$vs.notify({
          text: res.data.message,
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (e) {
        me.$vs.loading.close();
      });
    },
    add: function add(k) {
      var me = this;
      this.inputs.push({
        id: 0,
        cantidad: '',
        descripcion: '',
        especificacion: ''
      });
    },
    remove: function remove(index) {
      var _this = this;
      var me = this;
      var preId = 0;
      preId = this.inputs[index].id;
      if (preId > 0) {
        var formData = new FormData();
        formData.append('id', preId);
        this.$http.post(this.$server_url + 'deleteDetalleObsequio', formData).then(function (res) {
          _this.inputs.splice(index, 1);
        });
      } else {
        this.inputs.splice(index, 1);
      }
    },
    //======METODOS PARA APROBAR===========
    openConfirmarAprobar: function openConfirmarAprobar(tip) {
      var me = this;
      me.estado = 0;
      me.estado = tip;
      var message = tip == 3 ? 'aprobar' : 'anular';
      this.$vs.dialog({
        type: 'confirm',
        color: 'warning',
        acceptText: "Si deseo ".concat(message),
        cancelText: 'Cancelar',
        title: "Confirmar",
        text: "Est\xE1s seguro de ".concat(message, " el pedido de obsequios?"),
        accept: this.TypechangeEstado
      });
    },
    TypechangeEstado: function TypechangeEstado() {
      var me = this;
      var formData = new FormData();
      formData.append('id', me.pedido.id);
      formData.append('estado', me.estado);
      formData.append('id_facturador', me.usuario.idusuario);
      formData.append('observacion_facturador', me.observacion_facturador);
      formData.append('valor_real_actual', me.vReal);
      formData.append('comision_escuela', me.descuentoEscuela);
      formData.append('porcentaje_obsequio', me.porcentajeObsequio);
      formData.append('valor_obsequios', me.valor_obsequios);
      formData.append('valor_disponible', me.valueAvaible);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'changeEstadoObsequio', formData).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == 0) {
          me.$vs.notify({
            text: res.data.message,
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return;
        }
        me.$emit('changeRecharge', true);
        me.$vs.notify({
          text: res.data.message,
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (e) {
        me.$vs.loading.close();
      });
    },
    limpiar: function limpiar() {
      var me = this;
      me.contrato = '';
    },
    //======FIN METODOS PARA APROBAR==============
    //========METODOS EXPORTAR EXCEL==============
    //para importar a excel
    exportToExcel: function exportToExcel() {
      var _this2 = this;
      var me = this;
      //this.fileName   = 'Obsequios_'+me.pedido.nombreInstitucion +'_'+me.pedido.periodo
      var headerTitle = ['Escuela', 'Ciudad', 'Asesor', 'Cantidad', 'Descripción', 'Especificación', 'Período', 'valor total para obsequios'];
      var headerVal = ['nombreInstitucion', 'ciudad', 'asesor', 'cantidad', 'descripcion', 'especificacion', 'periodo', 'valor_obsequios'];
      import('@/vendor/Export2Excel').then(function (excel) {
        var data = _this2.formatJson(headerVal, _this2.inputs);
        excel.export_json_to_excel({
          header: headerTitle,
          data: data,
          filename: _this2.fileName,
          autoWidth: _this2.cellAutoWidth,
          bookType: _this2.selectedFormat
        });
        _this2.clearFields();
      });
    },
    formatJson: function formatJson(filterVal, jsonData) {
      return jsonData.map(function (v) {
        return filterVal.map(function (j) {
          return v[j];
        });
      });
    },
    //limpiar campos para exportar de nuevo
    clearFields: function clearFields() {
      this.fileName = '';
      this.cellAutoWidth = true;
      this.selectedFormat = 'xls';
    } //========FIN METODOS PARA EXPORTAR EXCEL=====
  }
};
