  created: function created() {
    var me = this;
  },
  mounted: function mounted() {
    var me = this;
    me.asignaturaSelected = {
      id: localStorage.idasignatura,
      label: localStorage.nombreasignatura
    };
    me.usuario = JSON.parse(localStorage.getItem('usuario'));
