        me.id_input2 = item.opciones[1].nombre_opcion != null ? item.opciones[1].nombre_opcion : '';
      }
      if (item.opciones[2] != undefined) {
        me.img_db3 = item.opciones[2].imagen_opcion != null ? item.opciones[2].imagen_opcion : '';
