    getFicha: function getFicha() {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'j_juegos/' + me.id_juego).then(function (res) {
        me.datos_ficha = res.data[0];
        $.each(me.datos_ficha, function (key, value) {
          // console.log('key: ' + me.datos_ficha[key]+' _ value: '+value)
          if (value == 'null' || value == null || value == '') {
            me.datos_ficha[key] = '';
          }
        });
        me.$vs.loading.close();
      }).catch(function (error) {});
