      }).catch(function (error) {
        console.log('Error al bloquear el codigo solicitado ' + me.itemEstado.codigo);
      });
    },
    //METODOS PARA CRUD STORAGE
    changeAddStorage: function changeAddStorage(e) {
      var me = this;
      var getStorage = JSON.parse(localStorage.getItem("getStorage"));
      if (getStorage) {
        me.arregloStorage = getStorage;
      }
      this.$vs.notify({
        text: 'Se agrego el código ' + e.codigo + " del libro " + e.libro,
        color: 'primary',
        iconPack: 'feather',
        icon: 'icon-check',
        time: 4000
      });
      var cal = new Object();
      cal.codigo = e.codigo;
      cal.libro = e.libro;
      me.arregloStorage.push(cal);
      //quitar duplicados
      localStorage.removeItem("getStorage");
      var hash = {};
      var resultado = me.arregloStorage.filter(function (o) {
        return hash[o.codigo] ? false : hash[o.codigo] = true;
      });
      me.arregloStorage = resultado;
      localStorage.setItem('getStorage', JSON.stringify(me.arregloStorage));
    },
    changeDeleteStorage: function changeDeleteStorage(e) {
      var me = this;
      this.$vs.notify({
        text: 'Se elimino el código ' + e.codigo + " del libro " + e.libro,
        color: 'warning',
        iconPack: 'feather',
        icon: 'icon-check',
        time: 4000
      });
      var getStorage = JSON.parse(localStorage.getItem("getStorage"));
      me.arregloStorage = getStorage.filter(function (p) {
        return p.codigo != e.codigo;
      });
      //quitar duplicados
      localStorage.removeItem("getStorage");
      var hash = {};
      var resultado = me.arregloStorage.filter(function (o) {
        return hash[o.codigo] ? false : hash[o.codigo] = true;
      });
      me.arregloStorage = resultado;
      localStorage.setItem('getStorage', JSON.stringify(me.arregloStorage));
    },
    quitarDuplicados: function quitarDuplicados() {
      var me = this;
      //quitar duplicados
      localStorage.removeItem("getStorage");
      var hash = {};
      var resultado = me.arregloStorage.filter(function (o) {
        return hash[o.codigo] ? false : hash[o.codigo] = true;
      });
      me.arregloStorage = resultado;
      localStorage.setItem('getStorage', JSON.stringify(me.arregloStorage));
    } //FIN METODOS PARA CRUD STORAGE
  }
