import "core-js/modules/es6.regexp.replace";
import "core-js/modules/es6.regexp.match";
import "core-js/modules/es6.array.sort";
import "core-js/modules/es6.number.constructor";
import exportGuiasVue from "../../../../temporadas/components/exportGuias.vue";
import exportActaGuiasVue from "../../../../temporadas/components/exportActaGuias.vue";
import exportActaGuiasBodegaVue from "../../../../temporadas/components/exportActaGuiasBodega.vue";
import GuiasStockVue from "../GuiasStock.vue";
export default {
  data: function data() {
    return {
      usuario: [],
      tmpPedidos: [],
      pedido: {},
      id_pedido: 0,
      popupCarga: false,
      mostrarReporteG: false,
      mostrarImprimirActa: false,
      mostrarImprimirActaBodega: false,
      pedidoGuiaEliminar: '',
      filtro: 0,
      arregloLibros: [],
      arregloStock: [],
      modalLibros: false,
      popupConfirmAprobar: false,
      verResumen: false,
      totalLibros: 0,
      userAdmin: false,
      userFacturador: false,
      NohayStock: false,
      popupObservacion: false
    };
  },
  components: {
    exportGuiasVue: exportGuiasVue,
    exportActaGuiasVue: exportActaGuiasVue,
    exportActaGuiasBodegaVue: exportActaGuiasBodegaVue,
    GuiasStockVue: GuiasStockVue
  },
  props: {
    pedidos: {
      type: Array,
      default: []
    },
    id_periodo: {
      type: Number,
      default: 0
    }
  },
  created: function created() {
    var me = this;
    me.usuario = JSON.parse(localStorage.getItem("usuario"));
  },
  watch: {
    pedidos: function pedidos(results) {
      var me = this;
      me.tmpPedidos = [];
      me.filtroTipo();
    }
  },
  mounted: function mounted() {
    var me = this;
    setTimeout(function () {
      me.filtroTipo();
    }, 1000);
    if (me.usuario.id_group == 27 || me.usuario.id_group == 17) {
      me.filtro = 1;
    } else {
      me.filtro = 0;
    }
    //user admin
    if (me.usuario.id_group == 22 || me.usuario.id_group == 23 || me.usuario.id_group == 1) {
      me.userAdmin = true;
    }
    //user facturador
    if (me.usuario.id_group == 22 || me.usuario.id_group == 23) {
      me.userFacturador = true;
    }
  },
  computed: {
    sumaPendientes: function sumaPendientes() {
      var me = this;
      //pendientes admin bodega
      var predatos = [];
      if (me.usuario.id_group == 27) {
        predatos = me.pedidos.filter(function (p) {
          return p.id_asesor == me.usuario.idusuario;
        });
      } else {
        predatos = me.pedidos;
      }
      var datos = predatos.filter(function (p) {
        return p.estado_entrega == 0 && p.estado == 1;
      });
      var resultado = datos.length;
      return resultado;
    },
    SumaAprobados: function SumaAprobados() {
      var me = this;
      var datos = me.pedidos.filter(function (p) {
        return p.estado_entrega == 1 && p.estado == 1;
      });
      var resultado = datos.length;
      return resultado;
    },
    SumaEntregados: function SumaEntregados() {
      var me = this;
      var datos = me.pedidos.filter(function (p) {
        return p.estado_entrega == 2 && p.estado == 1;
      });
      var resultado = datos.length;
      return resultado;
    }
  },
  methods: {
    verStock: function verStock(tipo) {
      var me = this;
      me.arregloStock = [];
      me.$vs.loading();
      var url = "";
      //resumen para facturacion
      if (tipo == 0) {
        var url = this.$server_url + 'guias?verStock=yes&id_pedido=' + me.pedido.id_pedido;
      }
      //resumen para bodega
      if (tipo == 1) {
        url = this.$server_url + 'guias?verStockGuiasProlipa=yes&id_pedido=' + me.pedido.id_pedido + '&acta=' + me.pedido.ven_codigo;
      }
      this.$http.get(url).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == 0) {
          me.$vs.notify({
            text: res.data.message,
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-alert-triangle',
            time: 3000
          });
          return;
        }
        //ingresar libros en array
        var datos = res.data;
        if (tipo == 0) {
          //validate que haya stock
          var validate = datos.filter(function (p) {
            return p.nuevoStock < 1;
          });
          if (validate.length > 0) {
            me.NohayStock = true;
          } else {
            me.NohayStock = false;
          }
          //fin validate que haya stock
        }
        var predatos = [];
        predatos = datos;
        me.arregloStock = predatos.sort(function (a, b) {
          var nombreA = a.nombrelibro.toLowerCase();
          var nombreB = b.nombrelibro.toLowerCase();
          var numA = parseInt(nombreA.match(/\d+$/)); // extraer el número del final (si existe)
          var numB = parseInt(nombreB.match(/\d+$/)); // extraer el número del final (si existe)
          nombreA = nombreA.replace(/\d+$/, ''); // quitar el número del final
          nombreB = nombreB.replace(/\d+$/, ''); // quitar el número del final

          if (nombreA < nombreB) {
            return -1;
          }
          if (nombreA > nombreB) {
            return 1;
          }
          if (numA && !numB) {
            // si a tiene número y b no, b va primero
            return 1;
          }
          if (!numA && numB) {
            // si b tiene número y a no, a va primero
            return -1;
          }
          if (numA && numB) {
            // si ambos tienen número, compararlos
            if (numA < 10 && numB >= 10) {
              // si a es menor que 10 y b es 10 o mayor, a va primero
              return -1;
            }
            if (numA >= 10 && numB < 10) {
              // si b es menor que 10 y a es 10 o mayor, b va primero
              return 1;
            }
            return numA - numB; // comparar los números directamente
          }
          return 0; // si son iguales en todo, no hay diferencia
        });
        //open confirm
        me.popupConfirmAprobar = true;
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
        me.$vs.notify({
          text: "Hubo problemas con el servidor de facturacion",
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-alert-triangle',
          time: 3000
        });
      });
    },
    enviarGuiasBDMilton: function enviarGuiasBDMilton() {
      var me = this;
      //validacion 
      if (me.pedido.codigo_contrato == null || me.pedido.codigo_contrato == "" || me.pedido.codigo_contrato == undefined || me.pedido.iniciales == "" || me.pedido.iniciales == null || me.pedido.iniciales == "null") {
        me.$vs.notify({
          text: 'El codigo del periodo o las iniciales del asesor no estan configuradas',
          color: 'primary',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      if (me.usuario.cod_usuario == null || me.usuario.cod_usuario == "null" || me.usuario.cod_usuario == "" || me.usuario.cod_usuario == undefined) {
        me.$vs.notify({
          text: 'El codigo del usuario facturador no esta configurado',
          color: 'primary',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      var formData = new FormData();
      formData.append('id_pedido', me.pedido.id_pedido);
      formData.append('codigo_contrato', me.pedido.codigo_contrato);
      formData.append('iniciales', me.pedido.iniciales);
      formData.append('usuario_fact', me.usuario.idusuario);
      formData.append('codigo_usuario_fact', me.usuario.cod_usuario);
      formData.append('region_idregion', me.pedido.region_idregion);
      formData.append('asesor_id', me.pedido.id_asesor);
      formData.append('id_periodo', me.pedido.id_periodo);
      formData.append('cli_ins_codigo', me.pedido.cli_ins_codigo);
      me.$vs.loading();
      this.$http.post(this.$server_url + 'guardarGuiasBDMilton', formData).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == 0) {
          me.$vs.notify({
            text: res.data.message,
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-alert-triangle',
            time: 3000
          });
          return;
        }
        me.$vs.notify({
          text: 'Se guardo correctamente',
          color: 'primary',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        me.popupConfirmAprobar = false;
        me.$emit('changeRecarge', true);
      }).catch(function (error) {
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'No se puedo guardar',
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
      });
    },
    changeHandle: function changeHandle(e) {
      var me = this;
      me.popupCarga = false;
      me.mostrarReporteG = false;
      me.mostrarImprimirActa = false;
      me.mostrarImprimirActaBodega = false;
      me.pedido = {};
    },
    ver_pedido: function ver_pedido(item) {
      var me = this;
      localStorage.setItem("id_periodo_selected", item.id_periodo);
      localStorage.setItem("nombre_periodo_selected", item.periodo);
      this.$store.dispatch('setGuia', item);
      this.$emit('showPedido', 1);
    },
    getReporteGuia: function getReporteGuia(tr) {
      var me = this;
      localStorage.setItem("id_periodo_selected", tr.id_periodo);
      localStorage.setItem("nombre_periodo_selected", tr.periodo);
      me.id_pedido = tr.id_pedido;
      me.popupCarga = true;
      me.mostrarReporteG = true;
    },
    ImprimirActa: function ImprimirActa(tipo) {
      var me = this;
      me.popupCarga = true;
      if (tipo == 0) {
        me.mostrarImprimirActa = true;
      }
      if (tipo == 1) {
        me.mostrarImprimirActaBodega = true;
      }
    },
    confirma_delete_pedido: function confirma_delete_pedido(item) {
      console.log(item);
      var me = this;
      me.pedidoGuiaEliminar = item;
      me.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: 'Confirmar',
        text: 'Seguro desea eliminar este pedido?. No lo podrá recuperar',
        acceptText: 'Aceptar',
        cancelText: 'Cancelar',
        accept: me.f_eliminarGuiaPedido
      });
    },
    f_eliminarGuiaPedido: function f_eliminarGuiaPedido() {
      var me = this;
      this.$http.get(this.$server_url + 'deletePedidoGuia/' + me.pedidoGuiaEliminar.id_pedido).then(function (result) {
        console.log(result.data);
        me.$vs.notify({
          color: 'danger',
          text: 'Pedido eliminado correctamente'
        });
        me.$emit('changeRecarge', true);
      }).catch(function (err) {
        console.log(err);
      });
    },
    filtroTipo: function filtroTipo() {
      var me = this;
      me.tmpPedidos = [];
      //para ver los pedidos pendientes
      if (me.filtro == 0) {
        //bodega mostrar los pedidos pendientes de bodega
        var predatos = [];
        if (me.usuario.id_group == 27) {
          predatos = me.pedidos.filter(function (p) {
            return p.id_asesor == me.usuario.idusuario;
          });
        } else {
          predatos = me.pedidos;
        }
        var datos = predatos.filter(function (p) {
          return p.estado_entrega == 0 && p.estado == 1;
        });
        me.tmpPedidos = datos;
        return;
      }
      //para ver los pedidos aprobados
      if (me.filtro == 1) {
        var _datos = me.pedidos.filter(function (p) {
          return p.estado_entrega == 1 && p.estado == 1;
        });
        me.tmpPedidos = _datos;
        return;
      }
      //para ver los pedidos entregados por bodega
      if (me.filtro == 2) {
        var _datos2 = me.pedidos.filter(function (p) {
          return p.estado_entrega == 2 && p.estado == 1;
        });
        me.tmpPedidos = _datos2;
        return;
      }
    },
    //====METODOS DE BODEGA======
    openConfirm: function openConfirm() {
      this.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: "Confirmar",
        acceptText: 'aceptar',
        cancelText: 'cancelar',
        text: 'Esta seguro de entregar esta solicitud de guias?',
        accept: this.entregarPedido
      });
    },
    entregarPedido: function entregarPedido() {
      var me = this;
      me.$vs.loading();
      var data = {
        'id_pedido': this.pedido.id_pedido,
        'estado': '2',
        'grupo': 'bodega',
        'acta': this.pedido.ven_codigo,
        'asesor_id': this.pedido.id_asesor
      };
      this.$http.post(this.$server_url + 'entregarPedido', data).then(function (res) {
        me.f_notificaciones('Pedido entregado', 'success', 'check');
        me.$emit('changeRecarge', true);
        me.$vs.loading.close();
      }).catch(function (err) {
        console.log(err);
        me.$vs.loading.close();
      });
    },
    f_notificaciones: function f_notificaciones(msj, color, icono) {
      var me = this;
      me.$vs.notify({
        text: msj,
        color: color,
        iconPack: 'feather',
        icon: icono
      });
      return;
    } //====FIN METODOS DE BODEGA====
  }
};
