        me.$vs.notify({
          text: 'Corrija el formato del excel y vuelva a intentar.',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      me.errores = [];
      $.each(me.tableData, function (key, value) {
        if (!value.codigo) {
          me.errores.push(value);
        }
      });
      if (me.errores.length > 0) {
        me.modalErrores = true;
      } else {
        me.modalConfirm = true;
      }
    }
  }
};
