import historicoMixin from "@/mixins/historicomix";
import Vue from 'vue';
import axios from 'axios';
import vSelect from 'vue-select';
import moment from "moment";
// import gql from 'graphql-tag'

Vue.filter('fecha', function (value) {
  if (value) {
    return moment(String(value)).format('YYYY-MM-DD HH:mm');
  }
});
export default {
  components: {
    'v-select': vSelect
  },
  mixins: [historicoMixin],
  // apollo: {
  //     proyectos: gql `query {
  //         proyectos { id nombre descripcion asignatura idusuario grupo_usuario archivo{id} archivodocente{id} createdAt updatedAt proyectos_asignaturas{ id datos idasignatura } } }`,
  // },
  data: function data() {
    return {
      lista_proyectos: '',
      eliminaWQ: '',
      usuario: '',
      grupo_usuario: '',
      proyectos_prolipa: [],
      proyectos_todo_docentes: [],
      proyectos_admin_prolipa: [],
      asignaturaDocente: '',
      tipoProy: '',
      proyectos: '',
      miusuario: '',
      filesEliminar: ['archivo', 'archivodocente', 'fileIntroduccion', 'fileTarea', 'fileProceso', 'fileRecurso', 'fileEvaluacion', 'fileConclusion']
    };
  },
  mounted: function mounted() {
    localStorage.removeItem('webq');
    localStorage.removeItem('edita');
    localStorage.removeItem('tipoProyectoControl');
    this.asignaturaDocente = localStorage.getItem('idasignatura');
    this.usuario = JSON.parse(localStorage.getItem('usuario'));
    this.miusuario = this.usuario;
    this.grupo_usuario = this.miusuario.id_group;
    this.getProyectos();
    // console.log('usuario dato ' + this.usuario, this.grupo_usuario)
  },
  methods: {
    getProyectos: function getProyectos() {
      var me = this;
      axios.post('https://foro.prolipadigital.com.ec/graphql', {
        query: 'query { proyectos { id nombre descripcion asignatura idusuario grupo_usuario archivo{id} archivodocente{id} fileIntroduccion{id} fileTarea{id} fileProceso{id} fileRecurso{id} fileEvaluacion{id} fileConclusion{id} createdAt updatedAt proyectos_asignaturas{ id datos idasignatura } } }'
      }).then(function (res) {
        me.proyectos_admin_prolipa = [];
        me.proyectos_prolipa = [];
        me.proyectos_todo_docentes = [];
        me.proyectos = res.data.data.proyectos;
        for (var index = 0; index < me.proyectos.length; index++) {
          // filtrar los proyectos del docente, de la asignatura  que se encuentre seleccionado
          if (me.proyectos[index].grupo_usuario == 1 && me.proyectos[index].idusuario == me.miusuario.idusuario) {
            me.proyectos_admin_prolipa.push(me.proyectos[index]);
          }

          // filtrar los proyectos de PROLIPA
          if (me.proyectos[index].grupo_usuario == 1) {
            me.proyectos_prolipa.push(me.proyectos[index]);
          }
          // filtrar proyectos de los docentes
          if (me.proyectos[index].grupo_usuario == 6) {
            me.proyectos_todo_docentes.push(me.proyectos[index]);
          }
        }
      });
    },
    crearProyecto: function crearProyecto() {
      this.$router.push('/agregarProyectoAdmin');
    },
    editaWebQ: function editaWebQ(item) {
      localStorage.edita = true;
      localStorage.webq = JSON.stringify(item);
      this.$router.push('/agregarProyectoAdmin');
    },
    openConfirmEliminar: function openConfirmEliminar(item) {
      var me = this;
      me.eliminaWQ = item;
      // console.log(item, me.eliminaWQ)
      // return
      me.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: "Confirmar",
        text: '¿Está seguro en eliminar este proyecto?',
        acceptText: 'Aceptar',
        cancelText: 'Cancelar',
        accept: me.f_eliminarWebQ
      });
    },
    f_eliminarWebQ: function f_eliminarWebQ() {
      var me = this;
      var cantidad = me.eliminaWQ.proyectos_asignaturas.length;
      var conteo = 0;
      //eliminar archivo

      var h_idinstitucion = me.miusuario.institucion_idInstitucion;
      var h_idusuario = me.miusuario.idusuario;
      var h_idgrupo = me.miusuario.id_group;
      var h_idasignatura = me.eliminaWQ.proyectos_asignaturas[0].idasignatura;
      var h_nombreasignatura = me.eliminaWQ.proyectos_asignaturas[0].datos.nombreasignatura;
      var h_recursotipo = 5; //5 = proyecto
      var datoHistorico = {
        'user_nombre': me.miusuario.nombres,
        'user_apellido': me.miusuario.apellidos,
        'user_email': me.miusuario.email,
        'user_cedula': me.miusuario.cedula,
        'recurso_accion': 'eliminar',
        'recurso_tipo': 'proyecto',
        'recurso_nombre': me.eliminaWQ.nombre,
        'recurso_detalle': ''
      };
      // console.log(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo,datoHistorico)
      me.historicoVisitas(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo, datoHistorico);
      me.saveHistoricoVisitas(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo, datoHistorico);
      if (me.eliminaWQ.archivo != null) {
        if (me.eliminaWQ.archivo[0] != undefined) {
          //eliminar archivo del estudiante
          axios.delete("https://foro.prolipadigital.com.ec/upload/files/" + me.eliminaWQ.archivo[0].id).then(function (res) {
            me.$vs.notify({
              text: 'Archivo de ESTUDIANTE eliminado',
              color: 'success',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          }).catch(function (error) {
            me.$vs.notify({
              text: 'No se eliminó el archivo de ESTUDIANTE ',
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          });
        }
      }
      if (me.eliminaWQ.archivodocente != null) {
        if (me.eliminaWQ.archivodocente[0] != undefined) {
          //eliminar archivodocente del estudiante
          axios.delete("https://foro.prolipadigital.com.ec/upload/files/" + me.eliminaWQ.archivodocente[0].id).then(function (res) {
            me.$vs.notify({
              text: 'Archivo de DOCENTE eliminado',
              color: 'success',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          }).catch(function (error) {
            me.$vs.notify({
              text: 'No se eliminó el archivo de ESTUDIANTE ',
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          });
        }
      }
      if (me.eliminaWQ.fileIntroduccion != null) {
        if (me.eliminaWQ.fileIntroduccion[0] != undefined) {
          //eliminar fileIntroduccion del estudiante
          axios.delete("https://foro.prolipadigital.com.ec/upload/files/" + me.eliminaWQ.fileIntroduccion[0].id).then(function (res) {
            me.$vs.notify({
              text: 'Archivo de INTRODUCCION eliminado',
              color: 'success',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          }).catch(function (error) {
            me.$vs.notify({
              text: 'No se eliminó el archivo de INTRODUCCION ',
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          });
        }
      }
      if (me.eliminaWQ.fileTarea != null) {
        if (me.eliminaWQ.fileTarea[0] != undefined) {
          //eliminar fileTarea del estudiante
          axios.delete("https://foro.prolipadigital.com.ec/upload/files/" + me.eliminaWQ.fileTarea[0].id).then(function (res) {
            me.$vs.notify({
              text: 'Archivo de TAREA eliminado',
              color: 'success',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          }).catch(function (error) {
            me.$vs.notify({
              text: 'No se eliminó el archivo de TAREA ',
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          });
        }
      }
      if (me.eliminaWQ.fileProceso != null) {
        if (me.eliminaWQ.fileProceso[0] != undefined) {
          //eliminar fileProceso del estudiante
          axios.delete("https://foro.prolipadigital.com.ec/upload/files/" + me.eliminaWQ.fileProceso[0].id).then(function (res) {
            me.$vs.notify({
              text: 'Archivo de PROCESO eliminado',
              color: 'success',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          }).catch(function (error) {
            me.$vs.notify({
              text: 'No se eliminó el archivo de PROCESO ',
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          });
        }
      }
      if (me.eliminaWQ.fileRecurso != null) {
        if (me.eliminaWQ.fileRecurso[0] != undefined) {
          //eliminar fileRecurso del estudiante
          axios.delete("https://foro.prolipadigital.com.ec/upload/files/" + me.eliminaWQ.fileRecurso[0].id).then(function (res) {
            me.$vs.notify({
              text: 'Archivo de RECURSO eliminado',
              color: 'success',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          }).catch(function (error) {
            me.$vs.notify({
              text: 'No se eliminó el archivo de RECURSO ',
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          });
        }
      }
      if (me.eliminaWQ.fileEvaluacion != null) {
        if (me.eliminaWQ.fileEvaluacion[0] != undefined) {
          //eliminar fileEvaluacion del estudiante
          axios.delete("https://foro.prolipadigital.com.ec/upload/files/" + me.eliminaWQ.fileEvaluacion[0].id).then(function (res) {
            me.$vs.notify({
              text: 'Archivo de EVALUACION eliminado',
              color: 'success',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          }).catch(function (error) {
            me.$vs.notify({
              text: 'No se eliminó el archivo de EVALUACION ',
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          });
        }
      }
      if (me.eliminaWQ.fileConclusion != null) {
        if (me.eliminaWQ.fileConclusion[0] != undefined) {
          //eliminar fileConclusion del estudiante
          axios.delete("https://foro.prolipadigital.com.ec/upload/files/" + me.eliminaWQ.fileConclusion[0].id).then(function (res) {
            me.$vs.notify({
              text: 'Archivo de CONCLUSION eliminado',
              color: 'success',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          }).catch(function (error) {
            me.$vs.notify({
              text: 'No se eliminó el archivo de CONCLUSION ',
              color: 'warning',
              iconPack: 'feather',
              icon: 'icon-alert-triangle'
            });
          });
        }
      }
      for (var index = 0; index < me.eliminaWQ.proyectos_asignaturas.length; index++) {
        axios.delete('https://foro.prolipadigital.com.ec/proyectos-asignaturas/' + me.eliminaWQ.proyectos_asignaturas[index].id).then(function (res) {
          // console.log('asignatura asociada, eliminada ')
          conteo++;
          // console.log(conteo)
          if (conteo == cantidad) {
            // console.log('igualdad')
            axios.delete('https://foro.prolipadigital.com.ec/proyectos/' + me.eliminaWQ.id).then(function (res) {
              me.$vs.notify({
                text: 'Proyecto eliminado correctamente',
                color: 'success',
                iconPack: 'feather',
                icon: 'icon-check'
              });
              me.getProyectos();
            }).catch(function (error) {
              console.log(error);
              me.$vs.notify({
                text: 'No se eliminó el proyecto',
                color: 'warning',
                iconPack: 'feather',
                icon: 'icon-alert-triangle'
              });
            });
          }
        }).catch(function (error) {
          console.log('no se eliminaron las asignaturas asociadas a este proyecto ' + error);
        });
      }
      //eliminar proyecto
    },
    verWebQ: function verWebQ(item) {
      var me = this;
      localStorage.edita = true;
      localStorage.webq = JSON.stringify(item);
      var h_idinstitucion = me.miusuario.institucion_idInstitucion;
      var h_idusuario = me.miusuario.idusuario;
      var h_idgrupo = me.miusuario.id_group;
      var h_idasignatura = item.proyectos_asignaturas[0].idasignatura;
      var h_nombreasignatura = item.proyectos_asignaturas[0].datos.nombreasignatura;
      var h_recursotipo = 5; //5 = proyecto
      var datoHistorico = {
        'user_nombre': me.miusuario.nombres,
        'user_apellido': me.miusuario.apellidos,
        'user_email': me.miusuario.email,
        'user_cedula': me.miusuario.cedula,
        'recurso_accion': 'ver',
        'recurso_tipo': 'proyecto',
        'recurso_nombre': item.nombre,
        'recurso_detalle': ''
      };
      // console.log(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo,datoHistorico)
      me.historicoVisitas(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo, datoHistorico);
      me.saveHistoricoVisitas(h_idinstitucion, h_idusuario, h_idgrupo, h_idasignatura, h_nombreasignatura, h_recursotipo, datoHistorico);
      this.$router.push('/verProyectosAdmin');
    },
    volver: function volver() {
      var me = this;
      this.$router.push('/proyectosAdmin');
    }
  }
};
