import flatPickr from 'vue-flatpickr-component';
import 'flatpickr/dist/flatpickr.css';
import Vue from 'vue';
import axios from 'axios';
import vSelect from 'vue-select';
import moment from 'moment';
import formrompecabezas from "./form_rompecabezas.vue";
import $ from 'jquery';
export default {
  data: function data() {
    return {
      asignaturaSelected: '',
      unidadSelected: {
        id: 1,
        label: "Unidad 1"
      },
      datos_crear_juego: {},
      data_juego: [],
      datos_ficha: {},
      juego: {
        estado_juego: false
      },
      preguntas: [],
      temas: [],
      usuario: [],
      asignaturas: [],
      unidades: [{
        id: "1",
        label: "Unidad 1"
      }, {
        id: "2",
        label: "Unidad 2"
      }, {
        id: "3",
        label: "Unidad 3"
      }, {
        id: "4",
        label: "Unidad 4"
      }, {
        id: "5",
        label: "Unidad 5"
      }, {
        id: "6",
        label: "Unidad 6"
      }, {
        id: "7",
        label: "Unidad 7"
      }, {
        id: "8",
        label: "Unidad 8"
      }],
      configdateTimePicker: {
        enableTime: true,
        dateFormat: 'Y-m-d H:i:s'
      },
      tabActivo: 1,
      id_juego: '',
      id_tipo_juego: '',
      activePromptEditarCrear: false,
      id_temas: [],
      editarPregActivo: false,
      tipoUsuario: 1
    };
  },
  components: {
    'v-select': vSelect,
    flatPickr: flatPickr,
    formrompecabezas: formrompecabezas
  },
  created: function created() {
    var me = this;
    me.asignaturaSelected = {
      id: localStorage.idasignatura,
      label: localStorage.nombreasignatura
    };
    me.usuario = JSON.parse(localStorage.getItem('usuario'));
    me.tipoUsuario = me.usuario.id_group;
    me.id_tipo_juego = localStorage.getItem('id_tipo_juego');
    if (JSON.parse(localStorage.getItem('data_juego'))) {
      me.juego = JSON.parse(localStorage.getItem('data_juego'));
      me.id_juego = me.juego.id_juego;
      me.asignaturaSelected = {
        id: me.juego.temas[0].idasignatura,
        label: me.juego.temas[0].nombreasignatura
      };
    }
  },
  mounted: function mounted() {
    var me = this;
    me.getTemas();
    me.cargarSelecTemas();
    me.getAsignaturas();
  },
  methods: {
    getAsignaturas: function getAsignaturas() {
      var me = this;
      if (me.tipoUsuario === 1) {
        this.$http.get(this.$server_url + 'asignaturasDoc/0').then(function (res) {
          me.asignaturas = res.data;
          me.$vs.loading.close();
        }).catch(function (error) {
          console.log(error);
        });
      } else {
        return;
      }
    },
    cargarSelecTemas: function cargarSelecTemas() {
      var me = this;
      if (me.juego.temas) {
        for (var i = 0; i < me.juego.temas.length; i++) {
          me.id_temas.push({
            id: me.juego.temas[i].id,
            nombre_tema: me.juego.temas[i].nombre_tema
          });
        }
      }
    },
    getPreguntas: function getPreguntas() {
      var me = this;
      this.$http.get(this.$server_url + 'j_contenidos/' + me.id_juego).then(function (res) {
        me.preguntas = res.data;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
      });
    },
    getTemas: function getTemas() {
      var me = this;
      if (me.asignaturaSelected.id) {
        me.$vs.loading();
        var formData = new FormData();
        formData.append('asignatura', me.asignaturaSelected.id);
        formData.append('unidad', me.unidadSelected.id);
        this.$http.post(this.$server_url + 'temasignunidad', formData).then(function (res) {
          //this.$http.get(this.$server_url+'temAsignaruta/'+ me.asignaturaSelected.id).then(res => {
          me.temas = res.data;
          me.$vs.loading.close();
        }).catch(function (error) {
          console.log(error);
        });
      } else {
        return;
      }
    },
    agregarJuego: function agregarJuego(tab) {
      var me = this;
      me.juego.fecha_inicio = '2021-01-01 12:00:00';
      me.juego.fecha_fin = '2021-01-01 12:00:00';
      if (me.id_temas === '' || me.juego.nombre_juego === '' || me.juego.descripcion_juego === '' || me.juego.puntos === '' || me.juego.duracion === '' || me.id_temas === undefined || me.juego.nombre_juego === undefined || me.juego.descripcion_juego === undefined || me.juego.puntos === undefined || me.juego.duracion === undefined || me.id_temas.length < 1) {
        me.$vs.notify({
          text: 'Debe completar todos los campos antes de continuar',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      var estado_juego = 1;
      if (me.juego.estado_juego === false) {
        estado_juego = 0;
      }
      me.$vs.loading();
      var formData = new FormData();
      formData.append('id_tipo_juego', me.id_tipo_juego);
      formData.append('id_docente', me.usuario.idusuario);
      formData.append('nombre_juego', me.juego.nombre_juego);
      formData.append('descripcion_juego', me.juego.descripcion_juego);
      formData.append('puntos', me.juego.puntos);
      formData.append('duracion', me.juego.duracion);
      formData.append('fecha_inicio', me.juego.fecha_inicio);
      formData.append('fecha_fin', me.juego.fecha_fin);
      formData.append('estado', estado_juego);
      this.$http.post(this.$server_url + 'j_juegos', formData).then(function (res) {
        me.id_juego = res.data.id_juego;
        localStorage.setItem('id_rompecabezas', me.id_juego);
        me.guardarTemasJuego();
        me.$vs.loading.close();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    editarJuego: function editarJuego() {
      var me = this;
      me.juego.fecha_inicio = '2021-01-01 12:00:00';
      me.juego.fecha_fin = '2021-01-01 12:00:00';
      if (me.id_temas === '' || me.juego.nombre_juego === '' || me.juego.descripcion_juego === '' || me.juego.puntos === '' || me.juego.duracion === '' || me.id_temas === undefined || me.juego.nombre_juego === undefined || me.juego.descripcion_juego === undefined || me.juego.puntos === undefined || me.juego.duracion === undefined || me.id_temas.length < 1) {
        me.$vs.notify({
          text: 'Debe completar todos los campos antes de continuar',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      var estado_juego = 1;
      if (me.juego.estado_juego === false) {
        estado_juego = 0;
      }
      me.$vs.loading();
      var formData = new FormData();
      formData.append('id_juego', me.id_juego);
      formData.append('id_tipo_juego', me.id_tipo_juego);
      formData.append('id_docente', me.usuario.idusuario);
      formData.append('nombre_juego', me.juego.nombre_juego);
      formData.append('descripcion_juego', me.juego.descripcion_juego);
      formData.append('puntos', me.juego.puntos);
      formData.append('duracion', me.juego.duracion);
      formData.append('fecha_inicio', me.juego.fecha_inicio);
      formData.append('fecha_fin', me.juego.fecha_fin);
      formData.append('estado', estado_juego);
      this.$http.post(this.$server_url + 'j_juegos', formData).then(function (res) {
        me.$vs.loading.close();
        me.eliminarTemasJuego();
        localStorage.setItem('id_rompecabezas', me.id_juego);
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    eliminarTemasJuego: function eliminarTemasJuego() {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'eliminarTemasJuego/' + me.id_juego).then(function (res) {
        me.$vs.loading.close();
        me.guardarTemasJuego();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    guardarTemasJuego: function guardarTemasJuego() {
      var me = this;
      me.$vs.loading();
      var temas_vector = [];
      for (var i = 0; i < me.id_temas.length; i++) {
        temas_vector.push(me.id_temas[i].id);
      }
      var formData = new FormData();
      formData.append('id_juego', me.id_juego);
      formData.append('id_temas', temas_vector);
      this.$http.post(this.$server_url + 'guardarTemasJuego', formData).then(function (res) {
        me.$vs.loading.close();
        me.tabActivo = 2;
        me.datos_crear_juego = {};
        me.editarPregActivo = false;
        me.pintartab(2);
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    verPregunta: function verPregunta(item) {
      var me = this;
      me.editarPregActivo = true;
      me.datos_crear_juego = {};
      me.datos_crear_juego = item;
    },
    cargarPreguntaJuego: function cargarPreguntaJuego() {
      var me = this;
      var noValido = /\s/;
      if (noValido.test(me.datos_crear_juego.respuesta)) {
        me.$vs.notify({
          text: 'Las palabras de la sopa de letras no pueden tener espacios en blanco.',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      var formData = new FormData();
      formData.append('id_juego', me.id_juego);
      formData.append('pregunta', '-');
      formData.append('respuesta', me.datos_crear_juego.respuesta);
      this.$http.post(this.$server_url + 'j_contenidos', formData).then(function (res) {
        me.$vs.loading.close();
        me.getPreguntas();
        me.datos_crear_juego = {};
        me.editarPregActivo = false;
        me.$vs.notify({
          text: 'Editado correctamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    editarPreguntaJuego: function editarPreguntaJuego() {
      var me = this;
      var noValido = /\s/;
      if (noValido.test(me.datos_crear_juego.respuesta)) {
        me.$vs.notify({
          text: 'Las palabras de la sopa de letras no pueden tener espacios en blanco.',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      var formData = new FormData();
      formData.append('id_contenido_juego', me.datos_crear_juego.id_contenido_juego);
      formData.append('id_juego', me.id_juego);
      formData.append('pregunta', '-');
      formData.append('respuesta', me.datos_crear_juego.respuesta);
      this.$http.post(this.$server_url + 'j_contenidos', formData).then(function (res) {
        me.$vs.loading.close();
        me.getPreguntas();
        me.datos_crear_juego = {};
        me.editarPregActivo = false;
        me.$vs.notify({
          text: 'Editado correctamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    eliminarPreguntaJuego: function eliminarPreguntaJuego() {
      var me = this;
      this.$http.delete(this.$server_url + 'j_contenidos/' + me.datos_crear_juego.id_contenido_juego).then(function (res) {
        me.$vs.loading.close();
        me.getPreguntas();
        me.datos_crear_juego = {};
        me.editarPregActivo = false;
        me.$vs.notify({
          text: 'Eliminado correctamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    getFicha: function getFicha() {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'j_juegos/' + me.id_juego).then(function (res) {
        me.datos_ficha = res.data[0];
        console.log(res.data);
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
      });
    },
    guardarFicha: function guardarFicha() {
      var me = this;
      var formData = new FormData();
      formData.append('id_juego', me.id_juego);
      formData.append('bloque_curricular', me.datos_ficha.bloque_curricular);
      formData.append('grado', me.datos_ficha.grado);
      formData.append('destrezas', me.datos_ficha.destrezas);
      formData.append('habilidades', me.datos_ficha.habilidades);
      formData.append('elaborado_por', me.datos_ficha.elaborado_por);
      formData.append('consigna', me.datos_ficha.consigna);
      formData.append('intencion_didactica', me.datos_ficha.intencion_didactica);
      formData.append('consideraciones', me.datos_ficha.consideraciones);
      this.$http.post(this.$server_url + 'j_juegos_ficha', formData).then(function (res) {
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'Ficha guardada correctamente',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.$router.go(-1);
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    pintartab: function pintartab(n) {
      for (var i = 1; i < 4; i++) {
        $("#btnNav" + i).removeClass("btnwizardSelect");
        $("#btnNav" + i).addClass("btnwizard");
      }
      $("#btnNav" + n).removeClass("btnwizard");
      $("#btnNav" + n).addClass("btnwizardSelect");
      /*document.getElementById('btnNav'+n).style.background = 'red'
      document.getElementById('btnNav'+n).style.color = 'white'*/
    },
    getImagenesRomp: function getImagenesRomp() {
      this.$refs.formrompecabezas.get_img_rompecabezas();
    }
  }
};
