      me.asignaturaSelect.idasignatura = me.datosEditar.asignatura_id;
      me.asignaturaSelect.nombreasignatura = me.datosEditar.nombreasignatura;
      // console.log(me.datosEditar)
      me.titulo = 'Editar adaptación curricular';
    }
    me.getAsignaturas();
  },
