      var me = this;
      this.$http.get(this.$server_url + 'asignaturasActi').then(function (res) {
        me.asignaturas = res.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    cargaUnidad: function cargaUnidad(unidad) {
      var me = this;
      if (unidad == 1) {
        me.msj1 = '';
        me.porcentaje1 = 0;
        me.pesoArchivo1 = 0;
        me.muyPesado1 = false;
        me.file1 = me.$refs.file1.files[0];
        // verificar tamaño de archivo
        me.pesoArchivo1 = (me.file1.size / me.bytes).toFixed(2);
        if (me.pesoArchivo1 > me.limiteMegas) {
          me.muyPesado1 = true;
          me.msj1 = me.msjError;
        }
      }
      if (unidad == 2) {
        me.msj2 = '';
        me.porcentaje2 = 0;
        me.pesoArchivo2 = 0;
        me.muyPesado2 = false;
        me.file2 = me.$refs.file2.files[0];
        // verificar tamaño de archivo
        me.pesoArchivo2 = (me.file2.size / me.bytes).toFixed(2);
        if (me.pesoArchivo2 > me.limiteMegas) {
          me.muyPesado2 = true;
          me.msj2 = me.msjError;
        }
        me.adaptacion.unidad2 = me.file2;
      }
      if (unidad == 3) {
        me.msj3 = '';
        me.porcentaje3 = 0;
        me.pesoArchivo3 = 0;
        me.muyPesado3 = false;
        me.file3 = me.$refs.file3.files[0];
        // verificar tamaño de archivo
        me.pesoArchivo3 = (me.file3.size / me.bytes).toFixed(2);
        if (me.pesoArchivo3 > me.limiteMegas) {
