    getLibros: function getLibros(id_pedido) {
      var me = this;
      me.arregloAreas = [];
      me.arregloLibros = [];
      me.$vs.loading();
      this.$http.get(this.$server_url + 'get_val_pedidoInfoTodo/' + id_pedido)
      // this.$http.get(this.$server_url+'get_val_pedidoInfo/'+id_pedido)
      .then(function (res) {
        me.$vs.loading.close();
        var datos = res.data;
        if (datos.length > 0) {
          var datos2 = datos.filter(function (p) {
            return p.valor > 0;
          });
          //setear ARRAY
          var preSetear = [];
          datos2.forEach(function (element) {
            var cal = new Object();
            cal.serieArea = element.serieArea;
            cal.idlibro = element.idlibro;
            cal.anio = element.anio;
            cal.version = element.version;
            cal.codigo_liquidacion = element.codigo_liquidacion;
            cal.nombrelibro = element.nombrelibro;
            cal.valor = element.valor;
            cal.precio = element.precio;
            preSetear.push(cal);
          });
          //Generate unique array
          var preTemplate = [];
          datos2.forEach(function (element) {
            var cal = new Object();
            cal.serieArea = element.serieArea;
            cal.idlibro = element.idlibro;
            cal.anio = element.anio;
            cal.version = element.version;
            cal.codigo_liquidacion = element.codigo_liquidacion;
            cal.nombrelibro = element.nombrelibro;
            cal.precio = element.precio;
            preTemplate.push(cal);
          });
          //CLEAR REPEAT
          //quitar duplicados
          var hash = {};
          var preArrayUnico = [];
          var resultado = preTemplate.filter(function (o) {
            return hash[o.nombrelibro] ? false : hash[o.nombrelibro] = true;
          });
          preArrayUnico = resultado;
          //////
          //SUMAR VALORES PEDIDO + ALCANCES
          var sumarValores = preSetear.reduce(function (acc, valor) {
            if (acc[valor.idlibro]) {
              acc[valor.idlibro] += valor.valor;
            } else {
              acc[valor.idlibro] = valor.valor;
            }
            return acc;
          }, {});
          //PASAR AL AREGLO UNICO LOS VALORES SUMADOS POR  LIBRO
          var result = preArrayUnico.map(function (obj) {
            obj["valor"] = sumarValores[obj.idlibro];
            return obj;
          });
          //OBTENER VALORES DE VALOR * CANTIDAD
          var arrayListo = [];
          result.forEach(function (element) {
            var cal3 = new Object();
            cal3.serieArea = element.serieArea;
            cal3.idlibro = element.idlibro;
            cal3.anio = element.anio;
            cal3.version = element.version;
            cal3.nombrelibro = element.nombrelibro;
            cal3.codigo_liquidacion = element.codigo_liquidacion;
            cal3.precio = element.precio;
            cal3.valor = element.valor;
            cal3.subtotal = element.valor * element.precio;
            arrayListo.push(cal3);
          });
          ///===ORDENAR ARRAY ==
          me.arregloLibros = arrayListo.sort(function (a, b) {
            var nombreA = a.nombrelibro.toLowerCase();
            var nombreB = b.nombrelibro.toLowerCase();
            var numA = parseInt(nombreA.match(/\d+$/)); // extraer el número del final (si existe)
            var numB = parseInt(nombreB.match(/\d+$/)); // extraer el número del final (si existe)
            nombreA = nombreA.replace(/\d+$/, ''); // quitar el número del final
            nombreB = nombreB.replace(/\d+$/, ''); // quitar el número del final

            if (nombreA < nombreB) {
              return -1;
            }
            if (nombreA > nombreB) {
              return 1;
            }
            if (numA && !numB) {
              // si a tiene número y b no, b va primero
              return 1;
            }
            if (!numA && numB) {
              // si b tiene número y a no, a va primero
              return -1;
            }
            if (numA && numB) {
              // si ambos tienen número, compararlos
              if (numA < 10 && numB >= 10) {
                // si a es menor que 10 y b es 10 o mayor, a va primero
                return -1;
              }
              if (numA >= 10 && numB < 10) {
                // si b es menor que 10 y a es 10 o mayor, b va primero
                return 1;
              }
              return numA - numB; // comparar los números directamente
            }
            return 0; // si son iguales en todo, no hay diferencia
          });
          me.renderAreas(me.arregloLibros);
        } //end if
      }).catch(function (e) {
        me.$vs.loading.close();
      });
    },
    renderAreas: function renderAreas(tr) {
      var me = this;
      var agruparAreas = [];
      var datosSeteados = [];
      tr.forEach(function (element) {
        var cal = new Object();
        cal.serieArea = element.serieArea;
        agruparAreas.push(cal);
      });
      //QUITAR ARRAYS DUPLICADOS
      var hash = {};
      var resultado = agruparAreas.filter(function (o) {
        return hash[o.serieArea] ? false : hash[o.serieArea] = true;
      });
      datosSeteados = resultado;
      datosSeteados.map(function (p) {
        p["valores"] = tr.filter(function (s) {
          return s.serieArea == p.serieArea;
        });
        return p;
      });
      var ArregloOrdenar = [];
      datosSeteados.forEach(function (element) {
        var cal = new Object();
        cal.serieArea = element.serieArea;
        cal.inicial1 = element.valores.filter(function (p) {
          return p.idlibro == 115 || p.idlibro == 658 || p.idlibro == 115 || p.idlibro == 662;
        });
        cal.inicial2 = element.valores.filter(function (p) {
          return p.idlibro == 114 || p.idlibro == 529 || p.idlibro == 663;
        });
        cal.uno = element.valores.filter(function (p) {
          return p.anio == 1 && (p.version == '' || p.version == null) && p.idlibro != 115 && p.idlibro != 658 && p.idlibro != 115 && p.idlibro != 662;
        });
        cal.dos = element.valores.filter(function (p) {
          return p.anio == 2 && (p.version == '' || p.version == null) && p.idlibro != 114 && p.idlibro != 529 && p.idlibro != 663;
        });
        cal.tres = element.valores.filter(function (p) {
          return p.anio == 3 && (p.version == '' || p.version == null);
        });
        cal.cuatro = element.valores.filter(function (p) {
          return p.anio == 4 && (p.version == '' || p.version == null);
        });
        cal.cinco = element.valores.filter(function (p) {
          return p.anio == 5 && (p.version == '' || p.version == null);
        });
        cal.seis = element.valores.filter(function (p) {
          return p.anio == 6 && (p.version == '' || p.version == null);
        });
        cal.siete = element.valores.filter(function (p) {
          return p.anio == 7 && (p.version == '' || p.version == null);
        });
        cal.ocho = element.valores.filter(function (p) {
          return p.anio == 8 && (p.version == '' || p.version == null);
        });
        cal.nueve = element.valores.filter(function (p) {
          return p.anio == 9 && (p.version == '' || p.version == null);
        });
        cal.diez = element.valores.filter(function (p) {
          return p.anio == 10 && (p.version == '' || p.version == null);
        });
        cal.bgu1 = element.valores.filter(function (p) {
          return p.anio == 1 && p.version == 'BGU';
        });
        cal.bgu2 = element.valores.filter(function (p) {
          return p.anio == 2 && p.version == 'BGU';
        });
        cal.bgu3 = element.valores.filter(function (p) {
          return p.anio == 3 && p.version == 'BGU';
        });
        ArregloOrdenar.push(cal);
      });
      //ORDENAR ARRAY
      me.arregloAreas = ArregloOrdenar.sort(function (a, b) {
        if (a.serieArea < b.serieArea) {
          return 1;
        }
        if (a.serieArea > b.serieArea) {
          return -1;
        }
        return 0;
      });
      setTimeout(function () {
        me.imprimirEval();
      }, 1000);
    },
    imprimirEval: function imprimirEval() {
      var me = this;
      var element = document.getElementById('seccionImprimir2');
      var opt = {
        margin: 0.5,
        // margin:         [1.5, 0.5, 1, 1],
        filename: me.nombrearchivo,
        image: {
          type: 'jpeg',
          quality: 0.98
        },
        html2canvas: {
          scale: 2
        },
        pagebreak: {
          mode: 'avoid-all',
          inside: "avoid",
          before: '#page2el'
        },
        // jsPDF:        { unit: 'in', format: 'letter', orientation: 'portrait' }
        jsPDF: {
          unit: 'in',
          format: 'letter',
          orientation: 'portrait'
        }
        //jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' }
      };
      html2pdf().from(element).set(opt).save();
      me.arregloContrato = [];
      me.arregloLibros = [];
      me.$vs.notify({
        text: "Archivo descargado correctamente",
        color: 'success',
        iconPack: 'feather',
        icon: 'icon-file',
        time: 2000
      });
      me.$emit('change', true);
    }
