            nombreA = nombreA.replace(/\d+$/, ''); // quitar el número del final
            nombreB = nombreB.replace(/\d+$/, ''); // quitar el número del final

            if (nombreA < nombreB) {
              return -1;
            }
            if (nombreA > nombreB) {
              return 1;
            }
            if (numA && !numB) {
              // si a tiene número y b no, b va primero
              return 1;
            }
            if (!numA && numB) {
              // si b tiene número y a no, a va primero
              return -1;
            }
            if (numA && numB) {
              // si ambos tienen número, compararlos
