import listadoMaterarial from "./material_subir/listadoMaterial.vue";
import historicoMixin from "@/mixins/historicomix";
import Vue from 'vue';
import axios from 'axios';
import vSelect from 'vue-select';
import cargarmaterial from "./cargarmaterial";
export default {
  mixins: [historicoMixin],
  components: {
    'v-select': vSelect,
    cargarmaterial: cargarmaterial,
    listadoMaterarial: listadoMaterarial
  },
  data: function data() {
    return {
      infotemas: [],
      popupActivo: false,
      archivos: false,
      infoarchivos: [],
      modalMaterial: false,
      modalTemas: false,
      material: [],
      materialSelect: '',
      materiales: [],
      temas: [],
      temaSelect: [],
      asignaturas: [],
      asignaturaSelect: {},
      unidades: [{
        "id": 1,
        nombre_unidad: 'Unidad 1'
      }, {
        "id": 2,
        nombre_unidad: 'Unidad 2'
      }, {
        "id": 3,
        nombre_unidad: 'Unidad 3'
      }, {
        "id": 4,
        nombre_unidad: 'Unidad 4'
      }, {
        "id": 5,
        nombre_unidad: 'Unidad 5'
      }, {
        "id": 6,
        nombre_unidad: 'Unidad 6'
      }, {
        "id": 7,
        nombre_unidad: 'Unidad 7'
      }, {
        "id": 8,
        nombre_unidad: 'Unidad 8'
      }],
      unidadesSelect: {},
      n_material: '',
      materialAp: {
        'idmaterial': '',
        'nombrematerial': '',
        'descripcionmaterial': '',
        'zipmaterial': '',
        'webmaterial': '',
        'imagenmaterial': '',
        'exematerial': '',
        'Estado_idEstado': 1
      },
      estados: [],
      op_eliminar: '',
      op_editar: 0,
      edita: 0,
      item_eliminar: '',
      data_material: [],
      id_material_editar: '',
      estado_material: false,
      modalCursosEnvioLink: false,
      modalCursosArchivos: false,
      cursosDocente: [],
      verTipoModal: false,
      usuario: [],
      miusuario: [],
      tipoUsuario: 1,
      grupo_usuario: '',
      idusuario: '',
      datoU: '',
      modalMaterialTemas: false,
      temasMostrar: '',
      nombrematerial: '',
      //====VARIABLES PARA RECURSOS EXTERNOS
      libro: {
        libroweb: '1',
        libro_con_guia: '1',
        guia_didactica: '1',
        unidades: '1'
      },
      curso: {
        ver: '1',
        agregar: '1',
        editar: '1',
        eliminar: '1'
      },
      cuaderno: {
        guia: '1',
        didactica: '1',
        web: '1'
      },
      planificacion: {
        descargar: '1',
        visualizar: '1'
      },
      //variables para recursos adicionales
      mijuego: {
        agregar: '1',
        editar: '1',
        eliminar: '1',
        ver: '1',
        asignar: '1'
      },
      juegoProlipa: {
        asignar: '1',
        ver: '1',
        previsualizar: '1'
      },
      materialDigital: {
        ver: '1',
        previsualizar: '1',
        asignar: '1'
      },
      materialpdf: {
        subir: '1',
        ver: '1',
        descargar: '1',
        asignar: '1'
      },
      propuesta: {
        ver: '1'
      },
      adaptacion: {
        ver: '1'
      },
      articulo: {
        lengua: '1',
        matematica: '1',
        naturales: '1',
        sociales: '1',
        ensenanza: '1'
      },
      documentom: {
        ver: '1'
      },
      permisosExternos: '',
      //fin de variables para recursos adicionales
      //FIN DE VARIABLES PARA RECURSOS EXTERNOS
      userRoot: false
    };
  },
  mounted: function mounted() {
    var me = this;
    //asignatura que se obtiene en el docente
    me.asignaturaSelect = {
      id: localStorage.idasignatura,
      nombreasignatura: localStorage.nombreasignatura
    };
    me.usuario = JSON.parse(localStorage.getItem('usuario'));
    me.miusuario = me.usuario;
    me.tipoUsuario = me.usuario.id_group;
    me.miusuario = me.usuario;
    me.grupo_usuario = me.miusuario.id_group;
    if (me.grupo_usuario == 10) {
      //director
      me.datoU = JSON.parse(localStorage.getItem('datoUser'));
      me.miusuario = me.datoU;
    }
    me.getMaterialAsignaturas();
    me.traerArchivos();
    me.getTodoMaterial();
    me.getTodoAsignaturas();
    me.getEstados();
    me.getUserRoot(me.usuario);
    //PERMISOS EXTERNOS
    if (localStorage.status_permisos == "yes") {
      me.permisosExternos = JSON.parse(localStorage.permisosExternos);
      me.libro = me.permisosExternos.permisos_libros;
      me.curso = me.permisosExternos.permisos_cursos;
      me.cuaderno = me.permisosExternos.permisos_cuadernos;
      me.planificacion = me.permisosExternos.permisos_planificaciones;
      //data de los recursos adicionales
      me.mijuego = me.permisosExternos.zona_diversion_mi_juego;
      me.juegoProlipa = me.permisosExternos.zona_diversion_juego_prolipa;
      me.materialDigital = me.permisosExternos.material_apoyo_digital;
      me.materialpdf = me.permisosExternos.material_apoyo_pdf;
      me.propuesta = me.permisosExternos.propuestas_metodologicas;
      me.adaptacion = me.permisosExternos.adaptaciones;
      me.articulo = me.permisosExternos.articulos;
      me.documentom = me.permisosExternos.documentos_ministeriales;
    }
    //FIN DE PERMISOS EXTERNOS
  },
  methods: {
    getUserRoot: function getUserRoot(dato) {
      var me = this;
      this.$http.get(this.$server_url + 'permisos/' + dato.idusuario).then(function (result) {
        if (result.data.length > 0) {
          me.userRoot = true;
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    abrirMaterial: function abrirMaterial() {
      var data = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : {};
      var me = this;
      if (data.webmaterial === null || data.webmaterial === 'null') {
        me.$vs.notify({
          text: 'El material  aun no est√° disponible',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      localStorage.materialApoyo = data.webmaterial;
      var route = this.$router.resolve({
        path: '/material/apoyo/ver'
      });
      window.open(route.href, '_blank');
    },
    //para subir archivos
    //crear material
    crearMaterial: function crearMaterial() {
      localStorage.docente = "yes";
      this.$router.push('/subirmaterial/docente/');
    },
    //para traer los temas de los archivos
    verTemasArchivo: function verTemasArchivo(id_material) {
      var _this = this;
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'materialtemas/' + id_material).then(function (res) {
        _this.infotemas = res.data;
        me.popupActivo = true;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
      });
    },
    //para traer los archivos pdfs
    traerArchivos: function traerArchivos() {
      var _this2 = this;
      var me = this;
      var id_asignatura = localStorage.idasignatura;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'traer_archivos_asignaturas/' + id_asignatura).then(function (res) {
        _this2.infoarchivos = res.data;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
      });
    },
    getMaterialAsignaturas: function getMaterialAsignaturas() {
      var me = this;
      me.$vs.loading();
      var id_asignatura = localStorage.idasignatura;
      if (me.tipoUsuario === 1) {
        id_asignatura = 'null';
      }
      this.$http.get(this.$server_url + 'temas_por_material/' + id_asignatura).then(function (res) {
        me.material = res.data.items;
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
        me.$vs.loading.close();
      });
    },
    getTemas: function getTemas() {
      var me = this;
      me.temas = [];
      if (me.unidadesSelect === "" || me.unidadesSelect === undefined || me.unidadesSelect === null) {
        me.$vs.notify({
          text: 'Seleccione la unidad',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      if (me.asignaturaSelect === "" || me.asignaturaSelect === undefined || me.asignaturaSelect === null) {
        me.$vs.notify({
          text: 'Seleccione la asignatura',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      var asignatura = me.asignaturaSelect;
      if (me.asignaturaSelect.id) {
        asignatura = me.asignaturaSelect.id;
      }
      var formData = new FormData();
      formData.append('unidad', me.unidadesSelect);
      formData.append('asignatura', asignatura);
      this.$http.post(this.$server_url + 'temas_asignatura_material', formData).then(function (res) {
        me.temas = res.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    getTodoMaterial: function getTodoMaterial() {
      var me = this;
      // me.$vs.loading()
      me.op_eliminar = '';
      var id_asignatura = localStorage.idasignatura;
      if (me.tipoUsuario === 1) {
        id_asignatura = 'null';
      }
      this.$http.get(this.$server_url + 'todo_material_apoyo/' + id_asignatura).then(function (res) {
        me.materiales = res.data;
        me.$vs.loading.close();
        // console.log(me.material)
      }).catch(function (error) {
        console.log(error);
        me.$vs.loading.close();
      });
    },
    getTodoAsignaturas: function getTodoAsignaturas() {
      var me = this;
      // me.$vs.loading()
      this.$http.get(this.$server_url + 'todo_asignaturas').then(function (res) {
        me.asignaturas = res.data;
        me.$vs.loading.close();
        // console.log(me.material)
      }).catch(function (error) {
        console.log(error);
        me.$vs.loading.close();
      });
    },
    asignarMaterialAsignatura: function asignarMaterialAsignatura() {
      var me = this;
      if (me.materialSelect.idmaterial == undefined) {
        me.$vs.notify({
          text: 'Seleccione el material de apoyo',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      if (me.asignaturaSelect == '') {
        me.$vs.notify({
          text: 'Seleccione una asignatura',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      me.$vs.loading();
      var temas_vector = [];
      for (var i = 0; i < me.temaSelect.length; i++) {
        temas_vector.push(me.temaSelect[i].id);
      }
      var formData1 = new FormData();
      formData1.append('id_material', me.materialSelect.idmaterial);
      formData1.append('id_tema', temas_vector);
      this.$http.post(this.$server_url + 'temas_material', formData1).then(function (res) {
        // console.log(res.data)
        me.$vs.notify({
          text: 'Temas registrados en el material',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        me.$vs.loading.close();
        me.getMaterialAsignaturas();
        me.modalTemas = false;
      }).catch(function (error) {
        console.log(error);
        me.$vs.notify({
          text: 'fallooooo. ',
          color: 'danger',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        me.$vs.loading.close();
        me.vaciar();
        me.getMaterialAsignaturas();
        me.modalTemas = false;
      });
    },
    dataFormularioEditar: function dataFormularioEditar(item) {
      var me = this;
      me.materialSelect = {
        nombrematerial: item.nombrematerial,
        idmaterial: item.idmaterial
      };
      me.unidadesSelect = {
        id: item.temas[0].unidad,
        nombre_unidad: 'Unidad ' + item.temas[0].unidad
      };
      me.asignaturaSelect = item.asignatura_idasignatura;
      // me.estado_material = item.estado
      me.temaSelect = [];
      if (item.temas) {
        for (var i = 0; i < item.temas.length; i++) {
          me.temaSelect.push({
            id: item.temas[i].id_tema,
            nombre_tema: item.temas[i].nombre_tema
          });
        }
      }
      me.modalTemas = true;
    },
    editarTemasMaterialAsignatura: function editarTemasMaterialAsignatura() {
      var _this3 = this;
      var me = this;
      me.$vs.loading();
      var formData = new FormData();
      formData.append('idmaterial', me.data_material.idmaterial);
      formData.append('idasignatura', me.data_material.asignatura_idasignatura);
      this.$http.post(this.$server_url + 'borrar_temas_material', formData).then(function (res) {
        var temas_vector = [];
        for (var i = 0; i < me.temaSelect.length; i++) {
          temas_vector.push(me.temaSelect[i].id);
        }
        var formData1 = new FormData();
        formData1.append('id_material', me.data_material.idmaterial);
        formData1.append('id_tema', temas_vector);
        _this3.$http.post(_this3.$server_url + 'temas_material', formData1).then(function (res) {
          // console.log(res.data)
          me.$vs.notify({
            text: 'Temas editados en el material',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
          me.$vs.loading.close();
          me.vaciar();
          me.getMaterialAsignaturas();
          me.modalTemas = false;
        }).catch(function (error) {
          me.$vs.notify({
            text: 'fall√≥. ',
            color: 'danger',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
          me.$vs.loading.close();
        });
      }).catch(function (error) {});
    },
    openConfirmEliminar: function openConfirmEliminar(item) {
      var me = this;
      me.item_eliminar = item;
      me.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: 'Confirmar',
        text: '¬øEst√° seguro de eliminar esta asignaci√≥n?',
        acceptText: 'Aceptar',
        cancelText: 'Cancelar',
        accept: me.eliminarMaterial
      });
    },
    openConfirmEliminarMaterial: function openConfirmEliminarMaterial() {
      var me = this;
      me.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: 'Confirmar',
        text: '¬øEst√° seguro de eliminar este material?',
        acceptText: 'Aceptar',
        cancelText: 'Cancelar',
        accept: me.confirmEliminarMaterial
      });
    },
    confirmEliminarMaterial: function confirmEliminarMaterial() {
      var _this4 = this;
      var me = this;
      var formData = new FormData();
      formData.append('idmaterial', me.data_material.idmaterial);
      formData.append('idasignatura', me.data_material.asignatura_idasignatura);
      this.$http.post(this.$server_url + 'borrar_temas_material', formData).then(function (res) {
        _this4.$http.post(_this4.$server_url + 'borrar_material_asig', formData).then(function (res) {
          me.getMaterialAsignaturas();
          me.$vs.notify({
            text: 'Material eliminado correctamente',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
        });
      }).catch(function (error) {});
    },
    eliminarMaterial: function eliminarMaterial() {
      var me = this;
      // console.log(me.item_eliminar.temas)
      me.$vs.loading();
      if (me.op_eliminar == 2) {
        // console.log(me.item_eliminar)
        var formData = new FormData();
        formData.append('material_idmaterial', me.item_eliminar.idmaterial);
        formData.append('asignatura_idasignatura', me.item_eliminar.asignatura_idasignatura);
        formData.append('temas', me.item_eliminar.temas);
        this.$http.post(this.$server_url + 'quitar_material_asignatura', formData).then(function (res) {
          // console.log(res.data)
          me.$vs.notify({
            text: me.item_eliminar.nombrematerial + ' eliminado',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
          me.$vs.loading.close();
          me.vaciar();
          me.getMaterialAsignaturas();
        }).catch(function (error) {
          console.log(error);
          me.$vs.loading.close();
        });
      }
      if (me.op_eliminar == 1) {
        var _formData = new FormData();
        _formData.append('idmaterial', me.item_eliminar.idmaterial);
        this.$http.post(this.$server_url + 'eliminarMaterial', _formData).then(function (res) {
          // console.log(res.data)
          me.$vs.notify({
            text: me.item_eliminar.nombrematerial + ' eliminado',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
          me.$vs.loading.close();
          me.getTodoMaterial();
        }).catch(function (error) {
          console.log(error);
          me.$vs.loading.close();
        });
      }
    },
    getEstados: function getEstados() {
      var me = this;
      // me.$vs.loading()
      this.$http.get(this.$server_url + 'material_estados').then(function (res) {
        me.estados = res.data;
        me.$vs.loading.close();
        // console.log(me.material)
      }).catch(function (error) {
        console.log(error);
        me.$vs.loading.close();
      });
    },
    registrarMaterial: function registrarMaterial(item) {
      var me = this;
      // console.log(item)
      if (me.materialAp.nombrematerial == "" || me.materialAp.nombrematerial == undefined) {
        me.$vs.notify({
          text: 'Ingrese el nombre del material de apoyo',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      if (me.materialAp.webmaterial == '' || me.materialAp.webmaterial == undefined) {
        me.$vs.notify({
          text: 'Ingrese el nombre del archivo digital del material de apoyo',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      if (me.materialAp.imagenmaterial == '' || me.materialAp.imagenmaterial == undefined) {
        me.$vs.notify({
          text: 'Escriba el nombre de la portada del material de apoyo',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-alert-triangle'
        });
        return;
      }
      me.$vs.loading();
      var formData = new FormData();
      if (me.materialAp.idmaterial > 0) {
        formData.append('idmaterial', me.materialAp.idmaterial);
      }
      formData.append('nombrematerial', me.materialAp.nombrematerial);
      formData.append('descripcionmaterial', me.materialAp.descripcionmaterial);
      formData.append('webmaterial', me.materialAp.webmaterial);
      formData.append('exematerial', me.materialAp.exematerial);
      formData.append('imagenmaterial', me.materialAp.imagenmaterial);
      formData.append('Estado_idEstado', me.materialAp.Estado_idEstado);
      formData.append('zipmaterial', me.materialAp.zipmaterial);
      formData.append('creador', me.miusuario.idusuario);
      this.$http.post(this.$server_url + 'registrar_material', formData).then(function (res) {
        if (me.materialAp.idmaterial > 0) {
          me.edita = 0;
          me.$vs.notify({
            text: me.materialAp.nombrematerial + 'editado correctamente',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
        } else {
          me.$vs.notify({
            text: 'Material registrado correctamente',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
        }
        me.modalMaterial = false;
        me.$vs.loading.close();
        me.vaciar();
        me.getTodoMaterial();
      }).catch(function (error) {
        me.$vs.loading.close();
        me.getTodoMaterial();
      });
    },
    selectMaterial: function selectMaterial(item) {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'showMaterial/' + item.idmaterial).then(function (res) {
        me.materialAp = res.data[0];
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error);
        me.$vs.loading.close();
      });
    },
    vaciar: function vaciar() {
      var me = this;
      me.materialAp = {
        'idmaterial': '',
        'nombrematerial': '',
        'descripcionmaterial': '',
        'zipmaterial': '',
        'webmaterial': '',
        'imagenmaterial': '',
        'exematerial': '',
        'Estado_idEstado': 1
      };
      me.materialSelect = '';
      me.temaSelec = 0;
      me.unidadesSelect = {};
      me.modalMaterial = false;
    },
    vaciarModal: function vaciarModal() {
      var me = this;
      me.materialSelect = '';
      me.unidadesSelect = '';
      me.temaSelect = "";
      me.op_editar = 0;
      me.asignaturaSelect = "";
      // me.asignaturas      = []
      me.modalTemas = true;
    },
    pasarDatos: function pasarDatos(item) {
      var me = this;
      me.op_editar = 1;
      me.materialSelect = item.nombrematerial;
      me.asignaturaSelect = item.nombreasignatura;
      me.unidadesSelect = item.temas.length > 0 ? item.temas[0].id_unidad : '';
      me.temaSelect = item.temas;
      me.modalTemas = true;
    },
    modalAgregarMaterial: function modalAgregarMaterial() {
      var me = this;
      me.modalMaterial = true;
    },
    getCursosDocente: function getCursosDocente() {
      var me = this;
      var formData = new FormData();
      formData.append('id_usuario', me.miusuario.idusuario);
      formData.append('id_asignatura', localStorage.idasignatura);
      formData.append('periodo_id', localStorage.periodo_id);
      this.$http.post(this.$server_url + 'curso_libro_docente', formData).then(function (res) {
        me.cursosDocente = res.data;
        if (me.archivos) {
          me.modalCursosArchivos = true;
        } else {
          me.modalCursosEnvioLink = true;
        }
      }).catch(function (error) {});
    },
    verCalificaciones: function verCalificaciones(item) {
      var me = this;
      localStorage.codigo_calif = item.codigo;
      localStorage.nombre_curso_calif = item.nombre;
      localStorage.id_material_calif = me.data_material.idmaterial;
      localStorage.nombre_material_calif = me.data_material.nombrematerial;
      // me.$router.push('/calificaciones_materialapoyo')
      window.open('/calificaciones_materialapoyo', '_blank');
    },
    asignarCurso: function asignarCurso(item) {
      var me = this;
      if (me.grupo_usuario == 10) {
        me.$vs.notify({
          text: 'Este contenido pertenece a otro docente',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return;
      }
      var formData = new FormData();
      formData.append('codigo_curso', item.codigo);
