import Vue from 'vue';
import axios from 'axios';
import vSelect from 'vue-select';
import flatPickr from 'vue-flatpickr-component';
import 'flatpickr/dist/flatpickr.css';
import detalleSeminarios from "./detalle_seminarios.vue";
import ResumenSeminarios from "./components/ResumenSeminarios.vue";
import temas from "../admin/capacitaciones.vue";
import MenuEncuestaVue from "../home/padres/encuesta/MenuEncuesta.vue";
import ReporteCapacitacionesVue from "./components/ReporteCapacitaciones.vue";
import CrearCapacitacionVue from "./components/CrearCapacitacion.vue";
import editarCapacitacionVue from "./components/editarCapacitacion.vue";
import reporte from "../capacitaciones/reporte.vue";
Vue.use(axios);
export default {
  components: {
    'v-select': vSelect,
    flatPickr: flatPickr,
    detalleSeminarios: detalleSeminarios,
    ResumenSeminarios: ResumenSeminarios,
    temas: temas,
    MenuEncuestaVue: MenuEncuestaVue,
    ReporteCapacitacionesVue: ReporteCapacitacionesVue,
    CrearCapacitacionVue: CrearCapacitacionVue,
    editarCapacitacionVue: editarCapacitacionVue,
    reporte: reporte
  },
  data: function data() {
    return {
      usuario: [],
      updateCapacitacion: false,
      modalAdd: false,
      tipoSeminario: 'Seminarios',
      menuTipo: '0',
      title: '',
      tipo: '',
      popupVerlink: false,
      popupSeminario: false,
      seminarios: [],
      nombre_seminario: '',
      descripcion_seminario: '',
      instituciones: [],
      fecha_inicio: '',
      fecha_fin: '',
      configdateTimePicker: {
        enableTime: true,
        dateFormat: 'Y-m-d H:i:s'
      },
      editar_activo: false,
      institucion: {},
      id_seminario: '',
      link_reunion: '',
      capacitador: '',
      capacitadorCapacitacion: '',
      cant_asistentes: 0,
      asistencia_activa: '',
      popupAsistentes: false,
      asistentes: [],
      tipo_webinar: '0',
      institucion_disabled: false,
      url: '',
      cant_respuestas: '',
      cant_registrados: '',
      cantidad_asistentes: '',
      cantidad_encuestas: '',
      tipo_web: 0,
      periodo: '',
      link_recurso: '',
      clave_recurso: '',
      periodos: [],
      periodo_seminarios: {},
      //variables para capacitaciones
      nombreInstitucion: '',
      institucion_id: '',
      institucion_id_temporal: '',
      estado_institucion_temporal: 0,
      periodo_id_temporal: 0,
      nombreInstitucionTemporal: '',
      filtroInstituciones: [],
      estado: '0',
      messageError: "",
      ciudadSelect: '',
      error: false,
      MenuCapacitacion: 0,
      agenda_editar: {
        'id': '',
        'estado': '',
        'observacion': '',
        'personas': '',
        'startDate': '',
        'endDate': '',
        'link_reunion': '',
        'asistencia_activa': '',
        'hora_inicio': '',
        'hora_fin': ''
      },
      capacitacion: {},
      region: '',
      filtro_fecha_ini: '',
      filtro_fecha_fin: '',
      m_asesores: '',
      lista_asesores: '',
      agenda_capacitaciones: [],
      //para exportar
      codigoSelected: [],
      formats: ['xls', 'xlsx', 'csv', 'txt'],
      fileName: null,
      selectedFormat: 'xls',
      cellAutoWidth: true,
      activePrompt: false,
      arregloCapacitadores: [],
      arregloPeriodos: []
    };
  },
  mounted: function mounted() {
    var me = this;
    this.usuario = JSON.parse(localStorage.getItem('usuario'));
    this.getPeriodos();
    this.getInstituciones();
    me.f_lista_Asesores();
    me.getCapacitadores();
  },
  methods: {
    getCapacitadores: function getCapacitadores() {
      var me = this;
      this.$http.get(this.$server_url + 'getCapacitadores').then(function (res) {
        me.arregloCapacitadores = res.data;
      }).catch(function (error) {
        console.log(error + ' error');
      });
    },
    //METODOS DE LAS CAPACITACIONES
    f_filtro_fechas: function f_filtro_fechas(item) {
      var me = this;
      if (item == 0) {
        me.filtro_fecha_ini = "";
        me.filtro_fecha_fin = "";
        me.m_asesores = "";
      }
      me.f_array_filtro_contenido();
    },
    f_array_filtro_contenido: function f_array_filtro_contenido() {
      var me = this;
      me.seminarios = [];
      if (me.filtro_fecha_ini != "") {
        //filtro con fecha inicio
        if (me.filtro_fecha_fin != "") {
          //filtro con fecha inicio y fin
          if (me.m_asesores != "") {
            //filtro fecha inicio, fecha fin y asesor
            // console.log('f ini f_fin y asesor')
            for (var index = 0; index < me.agenda_capacitaciones.length; index++) {
              if (me.agenda_capacitaciones[index].fecha_inicio.substr(0, 10) >= me.filtro_fecha_ini && me.agenda_capacitaciones[index].fecha_fin.substr(0, 10) <= me.filtro_fecha_fin && me.agenda_capacitaciones[index].cedula == me.m_asesores.cedula) {
                me.seminarios.push(me.agenda_capacitaciones[index]);
              }
            }
          } else {
            //filtro fecha inicio, fecha fin sin asesor
            // console.log('f ini f_fin')
            for (var _index = 0; _index < me.agenda_capacitaciones.length; _index++) {
              if (me.agenda_capacitaciones[_index].fecha_inicio.substr(0, 10) >= me.filtro_fecha_ini && me.agenda_capacitaciones[_index].fecha_fin.substr(0, 10) <= me.filtro_fecha_fin) {
                me.seminarios.push(me.agenda_capacitaciones[_index]);
              }
            }
          }
        } else if (me.m_asesores != "") {
          //filtro con fecha inicio y asesor
          // console.log('f_inic y asesor')
          for (var _index2 = 0; _index2 < me.agenda_capacitaciones.length; _index2++) {
            if (me.agenda_capacitaciones[_index2].fecha_inicio.substr(0, 10) >= me.filtro_fecha_ini && me.agenda_capacitaciones[_index2].cedula == me.m_asesores.cedula) {
              me.seminarios.push(me.agenda_capacitaciones[_index2]);
            }
          }
        } else {
          //filtro con fecha inicio y asesor
          // console.log('solo f_inic')
          for (var _index3 = 0; _index3 < me.agenda_capacitaciones.length; _index3++) {
            if (me.agenda_capacitaciones[_index3].fecha_inicio.substr(0, 10) >= me.filtro_fecha_ini) {
              me.seminarios.push(me.agenda_capacitaciones[_index3]);
            }
          }
        }
        return;
      }
      if (me.filtro_fecha_fin != "") {
        //filtro con fecha fin
        if (me.m_asesores != "") {
          // console.log('f_fin y asesor')
          for (var _index4 = 0; _index4 < me.agenda_capacitaciones.length; _index4++) {
            if (me.agenda_capacitaciones[_index4].fecha_fin.substr(0, 10) <= me.filtro_fecha_fin && me.agenda_capacitaciones[_index4].cedula == me.m_asesores.cedula) {
              me.seminarios.push(me.agenda_capacitaciones[_index4]);
            }
          }
        } else {
          // console.log('solo_f_fin')
          for (var _index5 = 0; _index5 < me.agenda_capacitaciones.length; _index5++) {
            if (me.agenda_capacitaciones[_index5].fecha_fin.substr(0, 10) <= me.filtro_fecha_fin) {
              me.seminarios.push(me.agenda_capacitaciones[_index5]);
            }
          }
        }
        return;
      }
      if (me.m_asesores != "") {
        //filtro con asesor
        // console.log('solo_asesor')
        for (var _index6 = 0; _index6 < me.agenda_capacitaciones.length; _index6++) {
          if (me.agenda_capacitaciones[_index6].cedula == me.m_asesores.cedula) {
            me.seminarios.push(me.agenda_capacitaciones[_index6]);
          }
        }
        return;
      }
      // console.log('sin fechas, sin asesor')
      me.seminarios = me.agenda_capacitaciones;
    },
    f_lista_Asesores: function f_lista_Asesores(item) {
      var me = this;
      this.$http.get(this.$server_url + 'listaAsesores').then(function (res) {
        me.lista_asesores = res.data;
        // console.log(res.data)
        me.$vs.loading.close();
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    //para importar a excel
    exportToExcel: function exportToExcel() {
      var _this = this;
      var headerTitle = ['Tipo', 'Nombre', 'Area', 'Capacitadores', 'Asesor', 'Institucion', 'Link', 'Asistentes', 'Fecha capacitación', 'Hora inicio', 'Hora fin', 'fecha create', 'estado'];
      var headerVal = ['tipo', 'nombre', 'nombrearea', 'capacitadores', 'asesor', 'institucionFinal', 'link_reunion', 'cant_asistentes', 'fecha_inicio', 'hora_inicio', 'hora_fin', 'created_at', 'estadoCapacitacion'];
      import('@/vendor/Export2Excel').then(function (excel) {
        var data = _this.formatJson(headerVal, _this.codigoSelected);
        excel.export_json_to_excel({
          header: headerTitle,
          data: data,
          filename: _this.fileName,
          autoWidth: _this.cellAutoWidth,
          bookType: _this.selectedFormat
        });
        _this.clearFields();
        _this.guardarActivo = '';
        _this.generarActivo = ' ';
        _this.textAlertCant = '';
        _this.cantidad = '';
        _this.codigoSelected = [];
      });
    },
    formatJson: function formatJson(filterVal, jsonData) {
      console.log(jsonData);
      return jsonData.map(function (v) {
        return filterVal.map(function (j) {
          if (j === 'tipo') {
            return v[j] === 0 ? 'Presencial' : 'Virtual';
          } else {
            return v[j];
          }
        });
      });
    },
    //limpiar campos para exportar de nuevo
    clearFields: function clearFields() {
      this.fileName = '';
      this.cellAutoWidth = true;
      this.selectedFormat = 'xls';
    },
    //FIN DE METODOS DE LAS CAPACITACIONES
    verEnlace: function verEnlace(url) {
      var me = this;
      me.url = url;
      me.popupVerlink = true;
    },
    getPeriodos: function getPeriodos() {
      var me = this;
      this.$http.get(this.$server_url + 'get_periodos_seminarios').then(function (res) {
        var datos = res.data;
        me.periodos = datos.filter(function (p) {
          return p.idperiodoescolar != 20 && p.idperiodoescolar != 17 && p.idperiodoescolar != 15 && p.idperiodoescolar != 14 && p.idperiodoescolar > 11;
        });
        me.periodo_seminarios = me.periodos[0];
        me.getSeminarios();
      });
    },
    getSeminarios: function getSeminarios() {
      var me = this;
      me.seminarios = [];
      if (me.periodo_seminarios == null | me.periodo_seminarios == "" || me.periodo_seminarios == undefined) {
        me.$vs.notify({
          text: 'Seleccione un período por favor',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return;
      }
      me.$vs.loading();
      this.$http.get(this.$server_url + 'get_seminarios/' + me.periodo_seminarios.idperiodoescolar).then(function (res) {
        me.$vs.loading.close();
        var datos = res.data;
        if (me.menuTipo == 2) {
          me.agenda_capacitaciones = datos.filter(function (p) {
            return p.tipo_webinar == me.menuTipo;
          });
          me.seminarios = datos.filter(function (p) {
            return p.tipo_webinar == me.menuTipo;
          });
          return;
        }
        if (me.menuTipo == 1) {
          me.seminarios = datos.filter(function (p) {
            return p.tipo_webinar == me.menuTipo;
          });
          return;
        } else {
          me.seminarios = datos.filter(function (p) {
            return p.tipo_webinar != "1" && p.tipo_webinar != "2";
          });
          console.log('1', me.seminarios);
          return;
        }
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    getInstituciones: function getInstituciones() {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + 'get_instituciones').then(function (res) {
        me.instituciones = res.data;
        me.$vs.loading.close();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    //para obtener el perido atual
    getPeriodoInstitucion: function getPeriodoInstitucion() {
      var me = this;
      this.$http.get(this.$server_url + 'institucionTraerPeriodo?institucion_id=' + me.institucion.id_institucion).then(function (res) {
        me.periodo = res.data[0].periodo;
      }).catch(function (error) {
        console.log(error + ' error');
      });
    },
    agregar_seminario: function agregar_seminario() {
      var me = this;
      me.$vs.loading();
      if (me.tipo_webinar == "" || me.tipo_webinar == null || me.tipo_webinar == undefined) {
        me.tipo_web = "0";
      } else {
        me.tipo_web = me.tipo_webinar;
      }
      var seminario = {
        'id_seminario': '',
        'nombre': me.nombre_seminario,
        'descripcion': me.descripcion_seminario,
        'fecha_inicio': me.fecha_inicio,
        'fecha_fin': me.fecha_fin,
        'link_reunion': me.link_reunion,
        'id_institucion': me.institucion.id_institucion,
        'capacitador': me.capacitador,
        'cant_asistentes': me.cant_asistentes,
        'asistencia_activa': me.asistencia_activa,
        'tipo_webinar': me.tipo_web,
        'periodo_id': me.periodo,
        'link_recurso': me.link_recurso,
        'clave_recurso': me.clave_recurso
      };
      this.$http.post(this.$server_url + 'guardar_seminario', seminario).then(function (res) {
        me.getSeminarios();
        me.popupSeminario = false;
        me.$vs.loading.close();
        me.institucion.id_institucion = "";
        me.periodo = "";
      }).catch(function (error) {
        me.popupSeminario = false;
        me.$vs.loading.close();
      });
    },
    f_cambio_estado: function f_cambio_estado(item) {
      var me = this;
      localStorage.setItem('asesorSeleccionado', item.id_usuario);
      localStorage.setItem('planificacion_cedula', item.cedula);
      localStorage.setItem('planificacion_asesor_id', item.id_usuario);
      me.agenda_editar.id = item.id_seminario;
      me.agenda_editar.titulo = item.nombre;
      me.agenda_editar.personas = item.cant_asistentes;
      me.agenda_editar.estado = item.estado_capacitacion;
      me.agenda_editar.observacion = item.observacion_admin;
      me.agenda_editar.startDate = item.fecha_inicio;
      me.agenda_editar.endDate = item.fecha_fin;
      me.agenda_editar.link_reunion = item.link_reunion;
      me.agenda_editar.asistencia_activa = item.asistencia_activa;
      me.agenda_editar.hora_inicio = item.hora_inicio;
      me.agenda_editar.hora_fin = item.hora_fin;
      me.capacitadorCapacitacion = {
        idusuario: item.capacitador_id,
        capacitador: item.capacitador
      };
      me.capacitacion = item;
      me.modalAdd = true;
    },
    editarFormulario: function editarFormulario(item) {
      var me = this;
      me.capacitador = '';
      me.cant_asistentes = '';
      me.id_seminario = item.id_seminario;
      me.nombre_seminario = item.nombre;
      me.descripcion_seminario = item.descripcion;
      me.fecha_inicio = item.fecha_inicio;
      me.fecha_fin = item.fecha_fin;
      me.link_reunion = item.link_reunion;
      me.institucion = item;
      me.capacitador = item.capacitador;
      me.cant_asistentes = item.cant_asistentes;
      me.asistencia_activa = item.asistencia_activa;
      me.tipo_webinar = item.tipo_webinar;
      me.link_recurso = item.link_recurso;
      me.clave_recurso = item.clave_recurso;
      me.institucionActiva();
      me.editar_activo = true;
      me.popupSeminario = true;
    },
    editar_seminario: function editar_seminario() {
      var me = this;
      me.$vs.loading();
      var seminario = '';
      if (me.updateCapacitacion == true) {
        seminario = {
          'capacitacion': "yes",
          'id_seminario': me.agenda_editar.id,
          'nombre': me.agenda_editar.titulo,
          'fecha_inicio': me.agenda_editar.startDate,
          'fecha_fin': me.agenda_editar.endDate,
          'hora_inicio': me.agenda_editar.hora_inicio,
          'hora_fin': me.agenda_editar.hora_fin,
          'link_reunion': me.agenda_editar.link_reunion,
          'cant_asistentes': me.agenda_editar.personas,
          'observacion': me.agenda_editar.observacion,
          'estado_capacitacion': me.agenda_editar.estado,
          'asistencia_activa': me.agenda_editar.asistencia_activa,
          'institucion_id': me.institucion_id,
          'institucion_id_temporal': me.institucion_id_temporal,
          'estado_institucion_temporal': me.estado_institucion_temporal,
          'periodo_id_temporal': me.periodo_id_temporal,
          'nombreInstitucion': me.nombreInstitucion,
          'capacitador': me.capacitadorCapacitacion.capacitador,
          'capacitador_id': me.capacitadorCapacitacion.idusuario
        };
      } else {
        seminario = {
          'id_seminario': me.id_seminario,
          'nombre': me.nombre_seminario,
          'descripcion': me.descripcion_seminario,
          'fecha_inicio': me.fecha_inicio,
          'fecha_fin': me.fecha_fin,
          'link_reunion': me.link_reunion,
          'id_institucion': me.institucion.id_institucion,
          'capacitador': me.capacitador,
          'cant_asistentes': me.cant_asistentes,
          'asistencia_activa': me.asistencia_activa,
          'tipo_webinar': me.tipo_webinar,
          'link_recurso': me.link_recurso,
          'clave_recurso': me.clave_recurso
        };
      }
      this.$http.post(this.$server_url + 'guardar_seminario', seminario).then(function (res) {
        me.$vs.loading.close();
        me.getSeminarios();
        me.popupSeminario = false;
        me.modalAdd = false;
        if (res.data.message) {
          me.$vs.notify({
            text: res.data.message,
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-check'
          });
        }
      }).catch(function (error) {
        me.popupSeminario = false;
        me.$vs.loading.close();
      });
    },
    detalleSeminario: function detalleSeminario(item) {
      var me = this;
      me.$router.push('/detalle_seminarios/' + item.id_seminario);
    },
    eliminarSeminario: function eliminarSeminario(item) {
      var me = this;
      me.id_seminario = item.id_seminario;
      this.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: "Confirmar",
        text: '¿Está seguro de eliminar este seminario?',
        accept: this.confirmEliminarSeminario
      });
    },
    confirmEliminarSeminario: function confirmEliminarSeminario() {
      var me = this;
      this.$http.get(this.$server_url + 'eliminar_seminario/' + me.id_seminario).then(function (res) {
        me.getSeminarios();
        me.$vs.loading.close();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    //para obtener el conteno para webinar
    getContenoWebinar: function getContenoWebinar(seminario_id) {
      var me = this;
      this.$http.get(this.$server_url + 'seminario?contar=yes&seminario_id=' + seminario_id).then(function (res) {
        me.cant_registrados = res.data.registrados[0].registrados;
        me.cantidad_asistentes = res.data.asistentes[0].asistentes;
        me.cantidad_encuestas = res.data.encuestas;
      }).catch(function (error) {
        console.log(error + ' error');
      });
    },
    asistentesSeminario: function asistentesSeminario(item) {
      var me = this;
      //Para las respuestas
      me.tipo = item.tipo_webinar;
      if (me.tipo == 1) {
        me.title = "Detalle webinar" + " " + item.nombre;
      } else {
        me.title = "Detalle Seminario" + " " + item.nombre;
      }
      me.cant_respuestas = item.cant_respuestas;
      localStorage.setItem('id_seminario', item.id_seminario);
      localStorage.data_seminario = JSON.stringify(item);
      //Para los asistentes
      me.getContenoWebinar(item.id_seminario);
      this.$http.get(this.$server_url + 'asistentes_seminario/' + item.id_seminario).then(function (res) {
        me.asistentes = res.data;
        me.popupAsistentes = true;
        me.$vs.loading.close();
      }).catch(function (error) {
        me.$vs.loading.close();
      });
    },
    institucionActiva: function institucionActiva() {
      if (this.tipo_webinar == 1 || this.tipo_webinar == true) {
        this.institucion_disabled = true;
      } else {
        this.institucion_disabled = false;
      }
    },
    //=================CAPACITACIONES=//============
    regresar: function regresar() {
      var me = this;
      me.institucionesAsesor = [];
      me.error = false;
    },
    rechargeCapacitacion: function rechargeCapacitacion(e) {
      var me = this;
      me.MenuCapacitacion = 0;
      me.getSeminarios();
    },
    //recargar seminarios
    rechargeSeminarios: function rechargeSeminarios() {
      var me = this;
      me.getSeminarios();
      me.popupSeminario = false;
      me.modalAdd = false;
    }
  }
};
