      var me = this;
      this.$http.get(this.$server_url + 'ifAsignadoUsuario/' + me.usuario.idusuario + '/' + localStorage.periodo_id).then(function (res) {
        if (res.data.length > 0) {
          me.ifHaveNecesidad = true;
