import "core-js/modules/es6.regexp.to-string";
import flatPickr from 'vue-flatpickr-component';
import 'flatpickr/dist/flatpickr.css';
import vSelect from 'vue-select';
import pdfMake from "pdfmake/build/pdfmake";
var pdfFonts = require('pdfmake/build/vfs_fonts.js');
pdfMake.vfs = pdfFonts.pdfMake.vfs;
export default {
  created: function created() {
    var me = this;
    me.usuario = JSON.parse(localStorage.getItem("usuario"));
  },
  components: {
    'v-select': vSelect,
    flatPickr: flatPickr
  },
  data: function data() {
    return {
      usuario: [],
      instituciones: [],
      popupFiltroReporte: false,
      ticket: {
        razonBusqueda: ''
      },
      tickets: [],
      ticketsRespuesta: [],
      ticketsReporteGeneral: [],
      ticket_id: 0,
      valorBusqueda: '0',
      busqueda: 'cedula',
      fromDate: null,
      toDate: null,
      configdateTimePicker: {
        enableTime: true,
        dateFormat: 'Y-m-d'
      },
      reporteTicket: {
        institucion: ""
      }
    };
  },
  methods: {
    //para dar seguimiento al ticket
    SeguimientoTicket: function SeguimientoTicket(ticket_id, asunto, usuario, email, estado) {
      var me = this;
      localStorage.setItem("ticket_id", ticket_id);
      localStorage.setItem("asunto", asunto);
      localStorage.setItem("usuario_ticket", usuario);
      localStorage.setItem("email_ticket", email);
      localStorage.setItem("estado_ticket", estado);
      localStorage.setItem("ticketAyuda", "no");
      me.$router.push('/soporte/ticket/abierto/');
    },
    openConfirm: function openConfirm(ticket_id) {
      var me = this;
      me.ticket_id = ticket_id;
      me.$vs.dialog({
        type: 'confirm',
        color: 'danger',
        title: "Confirm",
        text: 'Estas seguro/a de cambiar de estado el ticket.',
        accept: me.AbrirTicket
      });
    },
    AbrirTicket: function AbrirTicket() {
      var me = this;
      me.$vs.loading();
      this.$http.get(this.$server_url + "ticket?estadoChange=yes&estado=1&ticket_id=" + me.ticket_id).then(function (res) {
        me.$vs.loading.close();
        me.$vs.notify({
          text: 'Ticket cambiado con exito',
          color: 'success',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        me.busquedaFiltroEstudiante();
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    busquedaFiltroEstudiante: function busquedaFiltroEstudiante() {
      var me = this;
      me.tickets = [];
      //para el filtro por numero del ticket
      if (me.busqueda == 'numero') {
        if (me.ticket.razonBusqueda.length < 1) {
          me.$vs.notify({
            text: 'Minimo 1 caracter de busqueda',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
      }
      //para el filtro por fechas
      if (me.busqueda == 'fecha') {
        if (me.fromDate == null) {
          me.$vs.notify({
            text: 'Debe escoger una fecha inicio',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        if (me.toDate == null) {
          me.$vs.notify({
            text: 'Debe escoger una fecha final',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
        if (me.fromDate > me.toDate) {
          me.$vs.notify({
            text: 'La fecha inicio no puede mayor que la fecha final',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
      }
      if (me.busqueda == 'cedula' || me.busqueda == 'institucion') {
        if (me.ticket.razonBusqueda.length < 5) {
          me.$vs.notify({
            text: 'Minimo 5 caracteres de busqueda',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        }
      }
      me.$vs.loading();
      this.$http.get(this.$server_url + 'ticket?busqueda=' + me.busqueda + '&razonBusqueda=' + me.ticket.razonBusqueda + '&fromDate=' + me.fromDate + '&toDate=' + me.toDate).then(function (res) {
        me.$vs.loading.close();
        if (res.data.status == '0') {
          me.$vs.notify({
            text: res.data.message,
            color: 'primary',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        } else {
          var datos = res.data.ticketCerrado;
          if (me.usuario.id_group == '1') {
            me.tickets = datos.filter(function (p) {
              return p.ticket_asesor == '0';
            });
            me.cantTickets = me.tickets.length;
          } else {
            me.tickets = datos.filter(function (p) {
              return p.ticket_asesor == '1' && p.vendedorInstitucion == me.usuario.cedula;
            });
            me.cantTickets = me.tickets.length;
          }
          me.instituciones = res.data.filtroInstituciones;
          // me.fromDate = null;
          // me.toDate = null;
          me.ticket.razonBusqueda = '';
        }
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    getTicketRespuesta: function getTicketRespuesta(ticket_id, asunto, codigo, nombreInstitucion) {
      var me = this;
      localStorage.setItem("asunto", asunto);
      localStorage.setItem("ticket_id", ticket_id);
      localStorage.setItem("ticket_codigo", codigo);
      localStorage.setItem("ticket_nombreInstitucion", nombreInstitucion);
      me.$vs.loading();
      this.$http.get(this.$server_url + 'ticket?RespuestaTicketAbiertoReporte=yes&ticket_id=' + ticket_id).then(function (res) {
        me.ticketsRespuesta = res.data;
        me.$vs.loading.close();
        me.reporte();
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    //filtro reporte por fechas
    ModalReporteGeneral: function ModalReporteGeneral() {
      var me = this;
      me.popupFiltroReporte = true;
    },
    filtrarReporteGeneral: function filtrarReporteGeneral() {
      var me = this;
      if (me.reporteTicket.institucion == "") {
        me.$vs.notify({
          text: 'Debe escoger una institucion',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      if (me.fromDate == null) {
        me.$vs.notify({
          text: 'Debe escoger una fecha inicio',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      if (me.toDate == null) {
        me.$vs.notify({
          text: 'Debe escoger una fecha final',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      if (me.fromDate > me.toDate) {
        me.$vs.notify({
          text: 'La fecha inicio no puede mayor que la fecha final',
          color: 'warning',
          iconPack: 'feather',
          icon: 'icon-check'
        });
        return false;
      }
      localStorage.setItem("ticket_nombreInstitucion", me.reporteTicket.institucion.nombreInstitucion);
      me.$vs.loading();
      this.$http.get(this.$server_url + 'ticket?reporteGeneral=yes&institucion_id=' + me.reporteTicket.institucion.idInstitucion + '&fromDate=' + me.fromDate + '&toDate=' + me.toDate).then(function (res) {
        me.ticketsReporteGeneral = res.data;
        me.$vs.loading.close();
        if (res.data.status == '0') {
          me.$vs.notify({
            text: res.data.message,
            color: 'primary',
            iconPack: 'feather',
            icon: 'icon-check'
          });
          return false;
        } else {
          me.reporteGeneral();
          me.popupFiltroReporte = false;
          me.fromDate = null;
          me.toDate = null;
        }
      }).catch(function (error) {
        console.log(error + ' error');
        me.$vs.loading.close();
      });
    },
    //para generar el reporte General
    reporteGeneral: function reporteGeneral() {
      var body = [];
      body.push([{
        text: 'Ticket'
      }, {
        text: 'Usuario'
      }, {
        text: 'asunto'
      }, {
        text: 'Duracion'
      }]);
      for (var key in this.ticketsReporteGeneral) {
        if (this.ticketsReporteGeneral.hasOwnProperty(key)) {
          var item = this.ticketsReporteGeneral[key];
          var fila = new Array();
          fila.push([{
            text: 'Ticket #: ' + item.ticket_id.toString(),
            style: 'codigo'
          }, {
            text: 'Grupo: ' + item.rol.toString(),
            style: 'codigo'
          }, {
            text: 'codigo: ' + item.codigo.toString(),
            style: 'usuario'
          }, {
            text: 'Interacciones: ' + item.cantidad.toString(),
            style: 'usuario'
          }]);
          fila.push([{
            text: 'Nombres: ' + item.usuario.toString(),
            style: 'codigo'
          }, {
            text: 'cedula: ' + item.cedula.toString(),
            style: 'usuario'
          }]);
          fila.push(item.descripcion.toString());
          fila.push(item.dias.toString() + ' dias');
          body.push(fila);
        }
      }
      var dd = {
        content: [{
          text: 'Reporte General de tickets de' + ' ' + localStorage.getItem("ticket_nombreInstitucion"),
          style: 'header'
        }, ' ', ' ', {
          table: {
            headerRows: 1,
            body: body
          }
        }],
        styles: {
          header: {
            fontSize: 18,
            bold: true,
            alignment: 'center'
          },
          codigo: {
            italics: true,
            alignment: 'left'
          },
          usuario: {
            italics: true,
            alignment: 'left'
          }
        }
      };
      pdfMake.createPdf(dd).download("TicketGeneral.pdf");
    },
    //para generar el reporte
    reporte: function reporte() {
      var body = [];
      body.push([{
        text: 'Grupo'
      }, {
        text: 'Usuario'
      }, {
        text: 'mensaje'
      }, {
        text: 'fecha'
      }]);
      for (var key in this.ticketsRespuesta) {
        if (this.ticketsRespuesta.hasOwnProperty(key)) {
          var item = this.ticketsRespuesta[key];
          var fila = new Array();
          fila.push(item.rol.toString());
          fila.push([{
            text: 'Nombres: ' + item.usuario.toString(),
            style: 'codigo'
          }, {
            text: 'cedula: ' + item.cedula.toString(),
            style: 'usuario'
          }]);
          fila.push(item.descripcion.toString());
          fila.push(item.fecha.toString());
          body.push(fila);
        }
      }
      var dd = {
        content: [{
          text: 'Ticket #' + localStorage.getItem("ticket_id") + ' ' + localStorage.getItem("asunto"),
          style: 'header'
        }, ' ', ' ', {
          text: 'Codigo: ' + localStorage.getItem("ticket_codigo"),
          style: 'codigo'
        }, {
          text: 'Institucion: ' + localStorage.getItem("ticket_nombreInstitucion"),
          style: 'institucion'
        }, ' ', ' ', {
          table: {
            headerRows: 1,
            body: body
          }
        }],
        styles: {
          header: {
            fontSize: 18,
            bold: true,
            alignment: 'center'
          },
          codigo: {
            italics: true,
            alignment: 'left',
            bold: true
          },
          usuario: {
            italics: true,
            alignment: 'left'
          },
          institucion: {
            italics: true,
            alignment: 'left',
            bold: true
          }
        }
      };
      pdfMake.createPdf(dd).download("TicketIndividual.pdf");
    }
  }
};
