import Vue from 'vue';
import axios from 'axios';
import vSelect from 'vue-select';
import flatPickr from 'vue-flatpickr-component';
import 'flatpickr/dist/flatpickr.css';
export default {
  data: function data() {
    return {
      Asignaturas: [],
      libroserieArreglo: [],
      activeConfirm: false,
      popupVerSerie: false,
      id: 0,
      idlibro: 0,
      title: '',
      popupGuardarLibro: false,
