        //COSTA LIBROS
        c_weblibro: "",
        c_pdfsinguia: "",
        c_pdfconguia: "",
        c_guiadidactica: "",
        c_portada: "portada.png"
      },
      serie: {
        nombrelibro: '',
        nombre_serie: '',
        iniciales: '',
        codigo_liquidacion: '',
