    copiarLink: function copiarLink(item) {
      var me = this;
      var host = location.protocol + '//' + location.hostname + ':' + location.port;
      var ruta = host + '/libro_free/' + item.pag_ini + '4.3' + item.pag_fin + '4.3' + item.fecha_ini + '4.3' + item.fecha_fin + '4.3' + item.weblibro;
      var aux = document.createElement("input");
      console.log('ruta', ruta);
      aux.setAttribute("value", ruta);
      document.body.appendChild(aux);
      aux.select();
      try {
        var status = document.execCommand('copy');
        if (!status) {
          me.$vs.notify({
            text: 'Error al copiar el link',
            color: 'warning',
            iconPack: 'feather',
            icon: 'icon-alert-triangle'
          });
        } else {
          me.$vs.notify({
            text: 'Link copiado',
            color: 'success',
            iconPack: 'feather',
            icon: 'icon-check'
          });
        }
      } catch (err) {
        console.log('No se pudo copiar');
      }
      document.body.removeChild(aux);
    }
