    import flatPickr from 'vue-flatpickr-component';
    import 'flatpickr/dist/flatpickr.css';
    import Vue from 'vue'
    import axios from 'axios'
    import vSelect from 'vue-select'
    import moment from 'moment'
    import $ from 'jquery'
    import io from "socket.io-client";
    export default{
     data:()=>({
                usuario:[],
                status:"0",
                codigosInstitucion:[],
                arregloTemporada:[],
                //para la tabla seleccionada
                codigoSelected: [],
                //para exportar
                formats: ['xls', 'xlsx', 'csv', 'txt'],
                fileName:null,
                selectedFormat: 'xls',
                cellAutoWidth: true,
                activePrompt: false,
                asesor:"",
                socket: io(process.env.socket),
            }
         ),
        props:{
            contratoLiquidacion:{
                type:String,
                default:"",
            },
            estado_verificacion:{
                type:Number,
                default:0
            },
            insNombre:{
                type:String,
                default:"",
            },
            ciuNombre:{
                type:String,
                default:"",
            },

            // contratoLiquidacion:{
            //     type:String,
            //     default:"C-C23-0000039-LJ",
            // },
            // estado_verificacion:{
            //     type:Number,
            //     default:0
            // },
            // insNombre:{
            //     type:String,
            //     default:"Pan de vida",
            // },
            // ciuNombre:{
            //     type:String,
            //     default:"Guayaquil",
            // },
        },
        computed:{
            sumaTotal(){
                let me = this;
                if(me.codigosInstitucion.length >0){
                    let datos = me.codigosInstitucion.filter(p => p.cantidad)
                    let total = datos.reduce((a, b) => a + (b["cantidad"] || 0), 0);
                    return total
                }
            },
        },
        methods:{
            //traer la liquidacion
            getLiquidacion(){
                let me = this;
                me.$vs.loading()
                this.$http.get(this.$server_url + 'temporadas/liquidacion/' + me.contratoLiquidacion)
                .then(res => {
                    me.$vs.loading.close()
                    if(res.data.status == 0){
                        me.$vs.notify({
                        text:res.data.message,
                        color:'danger',
                        iconPack: 'feather',
                        icon:'icon-check'})
                        return
                    }
                    //llenar el arreglo  de libros
                    this.arregloTemporada   = res.data.temporada
                    this.codigosInstitucion = res.data.codigos_libros;
                })
                .catch(function(e){
                    me.$vs.loading.close()
                })
            },
            OpenConfirm(){
                this.$vs.dialog({
                    type:'confirm',
                    color: 'danger',
                    title: `Confirm`,
                    text: 'Estas seguro de solicitar una verificaci贸n.',
                    accept:this.solicitarVerificacion
                })
            },
            solicitarVerificacion(){
                let me = this
                let formData = new FormData();
                formData.append('contrato',me.contratoLiquidacion);
                me.$vs.loading()
                this.$http.post(this.$server_url+'solicitarVerificacion', formData)
                .then(res => {
                me.$vs.loading.close()
                me.$vs.notify({
                    text:'Se solicito correctamente',
                    color:'success',
                    iconPack: 'feather',
                    icon:'icon-check'})
                })
                let datos = {
                    id_asesor:  me.usuario.idusuario,
                    emisor:     me.usuario.idusuario,
                    grupo:      me.usuario.id_group,
                    tipo:       "verificaciones"
                }
                me.sendSocket(datos)
                me.$emit('ChangeRegresar',true)
            },
            sendSocket(datos){
                try {
                this.socket.emit("enviar", datos);
                } catch (error) {
                    console.log(error)
                }
            },
            exportToExcel() {
                let headerTitle = [ 'serie','Libro', 'Cantidad', 'Cod. Liquidacion'];
                let headerVal = [ 'serie','nombrelibro', 'cantidad','codigo'];
                this.fileName = "Codigos_a_liquidar";
                import('@/vendor/Export2Excel').then(excel => {
                    const data = this.formatJson(headerVal, this.codigosInstitucion)
                    excel.export_json_to_excel({
                        header: headerTitle,
                        data,
                        filename: this.fileName,
                        autoWidth: this.cellAutoWidth,
                        bookType: this.selectedFormat
                    })
                    this.clearFields()
                    this.guardarActivo = '';
                    this.generarActivo = ' ';
                    this.textAlertCant = '';
                    this.cantidad = '';
                })
            },
            formatJson(filterVal, jsonData) {
                return jsonData.map(v => filterVal.map(j => {
                    return v[j]
                }))
            },
            //limpiar campos para exportar de nuevo
            clearFields() {
                this.fileName = ''
                this.cellAutoWidth = true
                this.selectedFormat = 'xls'
            },
        },
        components: {
            'v-select': vSelect,
            flatPickr,
        },
       created() {
           let me =this;
           me.usuario = JSON.parse(localStorage.getItem("usuario"))
           me.getLiquidacion()
        }
    }
