import Vue from 'vue'
import axios from 'axios'
import vSelect from 'vue-select'
import 'vue-select/dist/vue-select.css'
import cursosLibroDocente from '../docentes/cursos.vue'
import VxCard from '../../components/vx-card/VxCard.vue'
Vue.use(axios)
export default {
    components: {
        'v-select': vSelect,
        cursosLibroDocente,
        VxCard
    },
    data() {
        return {
            val: '',
            valMultipe: {
                value1: '',
                value2: ''
            },
            select: [],
            libros: [],
            usuario: [],
            juegos: [],
            materialapoyo: [],
            unidadesLibro: [],
            planificaciones: [],

            indexUnidad: '',
            valUnidad: '',
            librosunidades: [],
            librosunidades_anim: [],
            planificacionesunidades: [],
            teletareasunidades: [],
            canteletareas: 0,
            tabMostrar: 0,
            nombrelibroSelec: '',
            idUnidad: '',
            dataLibroSelec: [],
            carpeta: '',
            juegos: [],
            materialapoyo: [],
            temas: [],
            videos: [],
            popupVideoActive: false,
            id_asignatura: '',
            data_curso: [{
                    titulo: 'Tareas',
                    vista: '/docente/curso/a_virtual/nombre_curso',
                    imagen: 'tareas.png'
                },
                {
                    titulo: 'Foros',
                    vista: '/docente/a_foros_curso',
                    imagen: 'foro.png'
                },
                {
                    titulo: 'Proyectos integradores',
                    vista: '/docente/a_proyectos',
                    imagen: 'proyecto.png'
                },
                {
                    titulo: 'Evaluaciones',
                    vista: '/docente/a_evaluaciones',
                    imagen: 'evaluacion.png'
                },
            ],
        }
    },
    computed: {
        validName() {
            return (this.valMultipe.value1.length > 0 && this.valMultipe.value2.length > 0)
        }
    },
    created() {
        this.usuario = JSON.parse(localStorage.getItem('usuario'))
        this.id_asignatura = localStorage.getItem('idasignatura')
        this.nombre_curso = localStorage.getItem('nombre_curso')
    },
    mounted() {},
    methods: {
        irSeleccion(vista) {
            this.$router.push(vista);
        },
    },
}
