                        let h_nombreasignatura = localStorage.nombreasignatura
                        let h_recursotipo = 9; //9 = material
                        let datoHistorico = {
                            'user_nombre': me.miusuario.nombres,
                            'user_apellido': me.miusuario.apellidos,
