import Vue from 'vue'
import axios from 'axios'
import vSelect from 'vue-select'
export default {
    components: {
        'v-select': vSelect,
    },
    data() {
        return {
            listaMenu: '',
            tmpListaMenu: '',
            modalMenu: false,
            titulo: '',
            opciones: 0,
            menu: {
                'idmenu': '',
                'name': '',
                'url': '',
                'icon': '',
                'orden': '',
            },
            grupos: [],
            grupoSelect: {},
            grupoSelect1: {},
            txt_boton: '',
            color_boton: '',
            usuario: '',
            id_autorizado: '',
            mensaje: 'Hey !!!! ¿Qué quiere hacer?, este contenido es restringido, consulte con los encargados del desarrollo.',
            permisoRoot:false,
        }
    },
    mounted() {
        let me = this;
        me.usuario = JSON.parse(localStorage.getItem('usuario'))
        me.id_autorizado = me.usuario.idusuario;
        me.getUserRoot()
        me.getMenu();
    },
    methods: {
      getUserRoot() {
            let me = this;
            this.$http.get(this.$server_url+'permisos?permiso=yes&usuario_id='+me.usuario.idusuario)
                .then(function (res) {
                    if(res.data.status == '1'){
                        me.permisoRoot = true
                    }else{
                        me.permisoRoot = false
                    }
                })
                .catch(function (error) {
                    console.log(error + ' error');
                })
        },
        verLink(tr){
            let me = this;
            window.open(tr.url)
        },
        getMenu() {
            let me = this;
            me.$vs.loading();
            this.$http.get(this.$server_url + 'listaMenu')
                .then(res => {
                    me.tmpListaMenu = res.data;
                    me.perfiles();
                    me.$vs.loading.close();
                })
                .catch(err => {
                    // console.log(err)
                    me.$vs.loading.close();
                })
            this.$http.get(this.$server_url + 'grupos_users')
                .then(res => {
                    me.grupos = res.data
                })
        },
        agregarModal() {
            let me = this;
            me.modalMenu = true;
            me.titulo = 'Agregar menú';
            me.txt_boton = 'Registrar';
            me.color_boton = 'success';
            me.menu = {
                'idmenu': '',
                'name': '',
                'url': '',
                'icon': '',
                'orden': '',
            }
            me.grupoSelect = {}
        },
        editarModal(item) {
            let me = this;
            me.modalMenu = true;
            // console.log(item)
            me.menu = item;
            me.grupoSelect = {}
            me.grupoSelect.id = item.sys_group_users_id
            me.grupoSelect.level = item.grupo
            me.grupoSelect.deskripsi = item.descripcion
            me.txt_boton = 'Editar';
            me.titulo = 'Editar menú';
            me.color_boton = 'warning';

        },
        agregar_editar() {
            let me = this;
            // console.log(me.menu, me.grupoSelect.id)
            // return
            me.modalMenu = false;
            if (me.permisoRoot) {
                let datos = new FormData();
                datos.append('idmenu', me.menu.idmenu)
                datos.append('orden', me.menu.orden)
                datos.append('url', me.menu.url)
                datos.append('nombre', me.menu.name)
                datos.append('icono', me.menu.icon)
                datos.append('grupo', me.grupoSelect.id)
                this.$http.post(this.$server_url + 'add_editMenu', datos)
                    .then(res => {
                        // console.log(res.data)
                        me.$vs.notify({
                            text: 'Contenido agregado correctamente',
                            color: 'success',
                            iconPack: 'feather',
                            icon: 'icon-check'
                        })
                        me.getMenu();
                    })
                    .catch(err => {
                        console.log(err)
                    })
            } else {
                me.$vs.notify({
                    text: me.mensaje,
                    time: 10000,
                    color: 'danger',
                    iconPack: 'feather',
                    icon: 'icon-alert-triangle'
                })
                return
            }

        },
        datosPerfiles() {
            let me = this;
            me.listaMenu = [];
            if (me.grupoSelect1.id == 0) {
                me.listaMenu = me.tmpListaMenu;
            } else {
                for (let index = 0; index < me.tmpListaMenu.length; index++) {
                    if (me.tmpListaMenu[index].sys_group_users_id == me.grupoSelect1.id) {
                        me.listaMenu.push(me.tmpListaMenu[index]);
                    }
                }
            }
        },
        confirmaEliminar(item) {
            let me = this;
            me.id_eliminar = item.idmenu;
            if (me.permisoRoot) {
                me.$vs.dialog({
                    type: 'confirm',
                    color: 'danger',
                    title: 'Confirmar',
                    text: 'Seguro desea eliminar este item?',
                    acceptText: 'Aceptar',
                    cancelText: 'Cancelar',
                    accept: me.eliminarMenu
                })
            } else {
                me.$vs.notify({
                    text: me.mensaje,
                    time: 10000,
                    color: 'danger',
                    iconPack: 'feather',
                    icon: 'icon-alert-triangle'
                })
                return
            }
        },
        eliminarMenu() {
            let me = this;
            me.$vs.loading();
            this.$http.get(this.$server_url + 'eliminarMenu/' + me.id_eliminar)
                .then(res => {
                    me.$vs.notify({
                        text: 'Item eliminado correctamente.',
                        color: 'success',
                        iconPack: 'feather',
                        icon: 'icon-check'
                    })
                    me.$vs.loading.close();
                    me.getMenu();
                })
                .catch(er => {
                    me.$vs.loading.close();
                })
        }
    }
}
