import vSelect from 'vue-select'
import moment from 'moment'
import VueRouter from 'vue-router'
import $ from 'jquery'
export default {
    data() {
        return {
            fileName: null,
            formats:['xls', 'xlsx', 'csv', 'txt'],
            cellAutoWidth: true,
            selectedFormat: 'xls',
            activePrompt: false,
            popupGrupos: false,

            evaluaciones: [],
            evaluacion: {
                nombre: '',
                asignatura: '',
                descripcion: '',
                fecha_inicio: '',
                fecha_fin: '',
                estado: '',
                puntos: '',
                duracion: '',
            },
            preguntas: [], //preguntas disponibles para cada evaluacion
            pregunta: {
                descripcion: '',
                respuesta: '',
                opcion1: '',
                opcion2: '',
                opcion3: ''
            },
            preguntasEvaluaciones: [], //preguntas cargadas a las evaluaciones
            preguntaEvaluacion: {
                descripcion: '',
