    methods: {
      filterKey(e) {
        const key = e.key;
        if (key === "," || key === "-" || key === "e" || key === "+")
          return e.preventDefault();
      },
     
      getOrdenParametro() {
        let me = this;
        let url;
        //this.showAgregarNuevo = true;
        if (me.busqueda == "codigo") {
          if (me.busquedaxcodigo.length < 2) {
            me.$vs.notify({
              text: "Minimo 2 caracteres de busqueda",
              color: "warning",
              iconPack: "feather",
              icon: "icon-check",
            });
            return false;
          }
          url =
            this.$server_url +
            "GetOrden_xfiltro?busqueda=" +
            me.busqueda +
            "&razonbusqueda=" +
            me.busquedaxcodigo;
        } else if (me.busqueda == "undefined") {
          if (me.busquedaxcodigo.length < 2) {
            me.$vs.notify({
              text: "Minimo 2 caracteres de busqueda",
              color: "warning",
              iconPack: "feather",
              icon: "icon-check",
            });
            return false;
          }
          url =
            this.$server_url +
            "GetOrden_xfiltro?busqueda=" +
            me.busqueda +
            "&razonbusqueda=" +
            me.busquedaxcodigo;
        } else if (me.busqueda == "temporada") {

          let a = me.region.value+me.anio.value;
          url =
            this.$server_url +
            "GetOrden_xfiltro?busqueda=" +
            me.busqueda +
            "&razonbusqueda=" +
            a;
        } else {
          url = this.$server_url + "GetOrden_xfiltro?busqueda=" + me.busqueda;
        }
        me.$vs.loading();
        this.$http
          .get(url)
          .then(function (res) {
            me.Ordentrabajo = res.data;
  
            if (me.Ordentrabajo.length === 0) {
              me.$vs.notify({
                text: "No hay órden de trabajo para esta búsqueda",
                color: "danger",
                iconPack: "feather",
                icon: "icon-x",
              });
              me.Ordentrabajo = [];
              me.$vs.loading.close();
            } else {
              // me.filtroRegion();
              me.$vs.loading.close();
              if (res.data.message) {
                me.$vs.notify({
                  text: res.data.message,
                  color: "success",
                  iconPack: "feather",
                  icon: "icon-user",
                });
              }
            }
          })
          .catch(function (error) {
            if (error.response && error.response.status === 404) {
              me.$vs.notify({
                text: "No hay órden de trabajo para esta búsqueda",
                color: "danger",
                iconPack: "feather",
                icon: "icon-x",
              });
              me.Ordentrabajo = [];
              me.$vs.loading.close();
            } else {
              console.log(error + " error");
              me.$vs.loading.close();
            }
          });
      },
      mostrarFechayhora: function () {
        const date = new Date();
        const formatear =
          date.getFullYear() +
          "-" +
          ("0" + (date.getMonth() + 1)).slice(-2) +
          "-" +
          ("0" + date.getDate()).slice(-2) +
          " " +
          ("0" + date.getHours()).slice(-2) +
          ":" +
          ("0" + date.getMinutes()).slice(-2) +
          ":" +
          ("0" + date.getSeconds()).slice(-2);
  
        return formatear;
      },
      
      
      remove(index) {
        let me = this;
        let preId = 0;
        preId = this.inputs[index].id;
        if (preId > 0) {
          let formData = new FormData();
          formData.append("id", preId);
  
          this.inputs.splice(index, 1);
        } else {
          this.inputs.splice(index, 1);
        }
      },
      remove1(index, b) {
        let me = this;
        let preId = 0;
        let datos = {
          det_com_codigo: b,
