        me.$vs.loading();
        this.$http
          .post(this.$server_url + "Eliminar_DetalleCompraOrden", datos)
          .then((res) => {
            preId = this.inputs[index].id;
            if (preId > 0) {
              let formData = new FormData();
              formData.append("id", preId);
  
              this.inputs.splice(index, 1);
            } else {
              this.inputs.splice(index, 1);
            }
