import Vue from 'vue'
import axios from 'axios'
import vSelect from 'vue-select'
export default {
    data() {
        return{
            series:[],
            activeConfirm:false,
            id:0,
            popupAddTema:false,
            serie:{
                serie_basica:'',
            },
            estado:'',
            permisoRoot:false,
            usuario:[],
            title:'',
        }
    },
    components: {
        'v-select': vSelect,
    },
    props:{
        id_periodo:{
            type:Number,
            default:0,
        },
    },  
    methods:{
        //para obtener el listado de las series
        getSeries() {
            let me = this;
            this.$http.get(this.$server_url+'getSeriesBasicas/'+me.id_periodo)
                .then(function (res) {
                    me.series = res.data
                })
                .catch(function (error) {
                    console.log(error + ' error');
                })
        },
        //para eliminar la serie
         eliminarSerie() {
              let me = this;      
              me.$vs.loading()
              this.$http.delete(this.$server_url+'series/'+me.id)
              .then(res => {
                 me.getSeries();
                  me.$vs.loading.close()
                  me.$vs.notify({
                  text:'Serie eliminada con exito',
                  color:'success',
                  iconPack: 'feather',
                  icon:'icon-check'})
              })
        },
        //para abrir el popup de confimacion para eliminar
        openConfirm(id,estado){
            this.id = id;
            this.$vs.dialog({
                type:'confirm',
                color: 'danger',
                title: `Confirmacion`,
                text: 'Estas seguro de eliminar esta serie?.',
                accept:this.eliminarSerie
            })
        },
        //para guardar las series
         guardarSerie(){
            let me = this;
            //datos que se obtienen de los inputs
            let formData = new FormData();
            formData.append('id',      me.id)
            let estado = 0
            if(me.serie.serie_basica == 0 )  estado = 1
            if(me.serie.serie_basica == 1 )  estado = 0
            formData.append('estado', estado)
            me.$vs.loading();
            this.$http.post(this.$server_url+'cambiarSerieBasica', formData)
                .then(function (res) {
                    me.$vs.loading.close();
                    me.$vs.notify({
                        text: res.data.message,
                        color: 'primary',
                        iconPack: 'feather',
                        icon: 'icon-alert-triangle'
                    })
                    me.getSeries()
                    limpiar()
                })
                .catch(function (error) {
                    me.$vs.loading.close();
                })
        },
        //para limpiar los campos
        limpiar(){
           let me = this;
           me.id = 0;
           me.serie.serie_basica = "0";
        },
    },
    created(){
        this.usuario = JSON.parse(localStorage.getItem("usuario"))
       
 
    },
    mounted(){
        this.getSeries() 
    },
}
