        agregar_codigo_estudiante() {
            let me = this;
            if (me.ing_codigo == '') {
                me.$vs.notify({
                    text: 'Ingrese el código',
                    color: 'danger',
                    iconPack: 'feather',
                    icon: 'icon-alert-triangle'
                })
                return;
            }
            me.modalCodigo = false;
