.ql-editor,
.ql-blank {
    min-height: 170px !important;
    /* display: inline-block; */
}

.caja-texto {
    border: 1px solid #ccc;
    padding: 8px;
    border-radius: 5px;
}

.flotante {
    position: fixed;
    bottom: 30px;
    right: 80px;
    transition: all 300ms ease 0ms;
    box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.1);
    z-index: 10;
}

.ventana1 {
    box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.1);
    z-index: 12;
    position: fixed;
    top: 30px;
    left: 30px;
    width: 98%;
    height: 98%;
    background-color: red;
}

.vs-popup--close {
    display: none;
}

.borde-unidad {
    border: 1px dashed #ccc;
    /* display: inline-block; */
    padding: 6px;
    cursor: pointer;
}
